mapper.select:
/* USE : ProdPayFgMapper.getProdRankList */
SELECT A.LV1_NM , A.LV2_NM , A.LV3_NM , B.PROD_CD , B.PROD_NM , NVL(C.TOT_SALE_QTY, 0 ) AS TOT_SALE_QTY , NVL(C.TOT_SALE_AMT, 0 ) AS TOT_SALE_AMT , NVL(C.TOT_DC_AMT, 0 ) AS TOT_DC_AMT , NVL(C.REAL_SALE_AMT, 0 ) AS REAL_SALE_AMT , NVL(C.TOT_TIP_AMT, 0 ) AS TOT_TIP_AMT , NVL(C.TOT_ETC_AMT, 0 ) AS TOT_ETC_AMT , NVL(D.PAY_AMT, 0 ) AS PAY_AMT ${sQuery1}
FROM ( SELECT A.P_PROD_CLASS_CD AS LV1_CD, A.PROD_CLASS_CD AS LV2_CD, A.PROD_CLASS_NM AS LV2_NM, B.PROD_CLASS_CD AS LV3_CD, B.PROD_CLASS_NM AS LV3_NM, (SELECT SF_GET_PROD_CLASSES('H', #{hqOfficeCd}, A.P_PROD_CLASS_CD) FROM DUAL) AS LV1_NM, (SELECT SF_GET_PROD_CLASSES('H', #{hqOfficeCd}, A.PROD_CLASS_CD ) FROM DUAL) AS LV2_NM_FULL, (SELECT SF_GET_PROD_CLASSES('H', #{hqOfficeCd}, B.PROD_CLASS_CD ) FROM DUAL) AS LV3_NM_FULL FROM ( SELECT HQ_OFFICE_CD, P_PROD_CLASS_CD, PROD_CLASS_CD, PROD_CLASS_NM FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND P_PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND P_PROD_CLASS_CD = '00000' ) ) A, TB_HQ_PRODUCT_CLASS B WHERE B.P_PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND P_PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND P_PROD_CLASS_CD = '00000' ) ) AND A.HQ_OFFICE_CD = B.HQ_OFFICE_CD AND A.PROD_CLASS_CD = B.P_PROD_CLASS_CD ) A, TB_HQ_PRODUCT B, ( SELECT C.PROD_CD , SUM(C.TOT_SALE_QTY) AS TOT_SALE_QTY , SUM(C.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(C.TOT_DC_AMT) AS TOT_DC_AMT , SUM(C.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(C.TOT_TIP_AMT) AS TOT_TIP_AMT , SUM(C.TOT_ETC_AMT) AS TOT_ETC_AMT FROM TB_SL_DAILY_PROD C WHERE 1 = 1
AND C.STORE_CD IN
#{item}
AND C.SALE_DATE >= #{startDate}
AND C.SALE_DATE <= #{endDate}
GROUP BY C.PROD_CD ) C, ( SELECT D.PROD_CD , NVL(SUM(D.PAY_AMT), 0 ) AS PAY_AMT ${sQuery2} FROM ( SELECT D.PROD_CD , D.PAY_CD , SUM(D.PAY_AMT) AS PAY_AMT ${sQuery3} FROM TB_SL_DAILY_PROD_PAY D WHERE 1 = 1
AND D.STORE_CD IN
#{item}
AND D.SALE_DATE >= #{startDate}
AND D.SALE_DATE <= #{endDate}
GROUP BY D.PROD_CD, D.PAY_CD ) D GROUP BY D.PROD_CD ORDER BY D.PROD_CD ) D WHERE B.HQ_OFFICE_CD = #{hqOfficeCd} AND B.PROD_CLASS_CD = A.LV3_CD AND B.PROD_CD = C.PROD_CD AND C.PROD_CD = D.PROD_CD(+)
AND C.PROD_CD = #{prodCd}
AND C.PROD_NM LIKE '%'||#{prodNm}||'%'
FROM ( SELECT A.P_PROD_CLASS_CD AS LV1_CD, A.PROD_CLASS_CD AS LV2_CD, A.PROD_CLASS_NM AS LV2_NM, B.PROD_CLASS_CD AS LV3_CD, B.PROD_CLASS_NM AS LV3_NM, (SELECT SF_GET_PROD_CLASSES('S', #{storeCd}, A.P_PROD_CLASS_CD) FROM DUAL) AS LV1_NM, (SELECT SF_GET_PROD_CLASSES('S', #{storeCd}, A.PROD_CLASS_CD ) FROM DUAL) AS LV2_NM_FULL, (SELECT SF_GET_PROD_CLASSES('S', #{storeCd}, B.PROD_CLASS_CD ) FROM DUAL) AS LV3_NM_FULL FROM ( SELECT STORE_CD, P_PROD_CLASS_CD, PROD_CLASS_CD, PROD_CLASS_NM FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} AND P_PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} AND P_PROD_CLASS_CD = '00000' ) ) A, TB_MS_PRODUCT_CLASS B WHERE B.P_PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} AND P_PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} AND P_PROD_CLASS_CD = '00000' ) ) AND A.STORE_CD = B.STORE_CD AND A.PROD_CLASS_CD = B.P_PROD_CLASS_CD ) A, TB_MS_PRODUCT B, ( SELECT C.PROD_CD , SUM(C.TOT_SALE_QTY) AS TOT_SALE_QTY , SUM(C.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(C.TOT_DC_AMT) AS TOT_DC_AMT , SUM(C.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(C.TOT_TIP_AMT) AS TOT_TIP_AMT , SUM(C.TOT_ETC_AMT) AS TOT_ETC_AMT FROM TB_SL_DAILY_PROD C WHERE 1 = 1
AND C.STORE_CD IN
#{item}
AND C.SALE_DATE >= #{startDate}
AND C.SALE_DATE <= #{endDate}
GROUP BY C.PROD_CD ) C, ( SELECT D.PROD_CD , NVL(SUM(D.PAY_AMT), 0 ) AS PAY_AMT ${sQuery2} FROM ( SELECT D.PROD_CD , D.PAY_CD , SUM(D.PAY_AMT) AS PAY_AMT ${sQuery3} FROM TB_SL_DAILY_PROD_PAY D WHERE 1 = 1
AND D.STORE_CD IN
#{item}
AND D.SALE_DATE >= #{startDate}
AND D.SALE_DATE <= #{endDate}
GROUP BY D.PROD_CD, D.PAY_CD ) D GROUP BY D.PROD_CD ORDER BY D.PROD_CD ) D WHERE B.STORE_CD = #{storeCd} AND B.PROD_CLASS_CD = A.LV3_CD AND B.PROD_CD = C.PROD_CD AND C.PROD_CD = D.PROD_CD(+)
AND C.PROD_CD = #{prodCd}
AND C.PROD_NM LIKE '%'||#{prodNm}||'%'
ORDER BY C.PROD_CD
