mapper.select:
        /* USE : ProdRankMapper.getProdRankList */

		SELECT M.PROD_CD
     			, TMP.PROD_NM
				${sQuery1}

		    FROM TB_MS_PRODUCT TMP
		    	, (
         		    SELECT TSDPT.STORE_CD
		           			, TSDPT.PROD_CD
		           			, TSDPT.SALE_HOUR
							${sQuery2}
        			FROM SBPORA.TB_SL_DAILY_PROD_TIME TSDPT
		 			WHERE 1 = 1

                    	AND TSDPT.STORE_CD IN

	                        #{item}



					   AND TSDPT.SALE_DATE >= #{startDate}


					   AND TSDPT.SALE_DATE <=  #{endDate}

					GROUP BY TSDPT.STORE_CD, TSDPT.PROD_CD, TSDPT.SALE_HOUR
       				) M
		WHERE TMP.STORE_CD = M.STORE_CD
		AND TMP.PROD_CD = M.PROD_CD
		GROUP BY M.PROD_CD, TMP.PROD_NM
		ORDER BY M.PROD_CD, TMP.PROD_NM
