/* USE : ProdHourMapper.getProdHourList */
SELECT PATH_NM, PROD_CD, PROD_NM ${sQuery2}
FROM ( SELECT FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM, --NM_STR tsdp.PROD_CD, tmp.PROD_NM, SUBSTR(tsdp.SALE_DATE, 5, 2) AS SALE_TIME ${sQuery1} FROM TB_SL_DAILY_PROD tsdp, TB_MS_PRODUCT tmp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tmp.STORE_CD = tsdp.STORE_CD AND tmp.PROD_CD = tsdp.PROD_CD GROUP BY tsdp.PROD_CD, tmp.PROD_NM, tmp.STORE_CD, tmp.PROD_CLASS_CD, tsdp.SALE_DATE ) GROUP BY PROD_CD, PROD_NM, PATH_NM ORDER BY PROD_CD

