AND A.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND A.STORE_CD = B.STORE_CD AND A.PROD_CD = B.PROD_CD GROUP BY A.STORE_CD , B.PROD_CLASS_CD , A.PROD_CD , A.SALE_DATE ) A , ( SELECT B.STORE_CD , B.PROD_CD , B.SALE_DATE , SUM(B.PAY_AMT_02) PAY_AMT_021 FROM ( SELECT A.STORE_CD , A.SALE_DATE , A.POS_NO , A.BILL_NO , A.PROD_CD , SUM(B.PAY_AMT) PAY_AMT_02 FROM TB_SL_SALE_DTL A , TB_SL_SALE_DTL_PAY B WHERE A.HQ_OFFICE_CD = #{hqOfficeCd}
