/* USE : ReportKwuMapper.getPayFgSaleList */ SELECT TO_CHAR(TO_DATE(tssh.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , tssh.TOT_SALE_AMT , tssh.TOT_DC_AMT , tssh.REAL_SALE_AMT , tsshp.PAY${item} AS PAY${item}
, tsshp.PAY02 AS PAY02 -- 현금 , tsshp.PAY021 AS PAY021 -- 현금영수증 FROM ( SELECT tssh.SALE_DATE , SUM(tssh.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(tssh.TOT_DC_AMT) AS TOT_DC_AMT , SUM(tssh.REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT tssh.SALE_DATE , tssh.STORE_CD , SUM(tssh.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(tssh.TOT_DC_AMT) AS TOT_DC_AMT , SUM(tssh.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_SALE_HDR tssh WHERE tssh.HQ_OFFICE_CD = #{hqOfficeCd}
AND tssh.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tssh.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tssh.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tssh.SALE_DATE , tssh.STORE_CD ) tssh GROUP BY tssh.SALE_DATE ) tssh , ( SELECT SALE_DATE , SUM(PAY_AMT_${item}) AS PAY${item} , SUM(NON_CASH_AMT) AS PAY02, SUM(APPR_CASH_AMT) AS PAY021 FROM TB_SL_DAILY_TOTAL tsdt WHERE tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdt.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY SALE_DATE ) tsshp WHERE tsshp.SALE_DATE (+) = tssh.SALE_DATE ORDER BY tssh.SALE_DATE DESC

/* USE : ReportKwuMapper.getPayCardSaleList */ SELECT TO_CHAR(TO_DATE(TSSPC.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , TSSPC.BILL_NO , DECODE(TSSPC.SALE_FG, 1, '승인', -1, '취소') AS APPR_GUBUN , DECODE(TSSPC.APPR_PROC_FG, 1, '포스승인', 2, '단말기승인') AS APPR_PROC_FG , TSSPC.CARD_NO , (TSSPC.SALE_FG * TSSPC.APPR_AMT) AS APPR_AMT , TSSPC.ACQUIRE_NM , TSSPC.ISSUE_NM , TSSPC.APPR_NO , TSSPC.MEMBR_JOIN_NO , DECODE(TSSPC.INST_CNT, 0 , '일시불', '할부') AS INST_CNT_NM , TSSPC.INST_CNT , TO_CHAR(TO_DATE(TSSPC.APPR_DT, 'YYYY-MM-DD HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS') AS APPR_DT FROM TB_SL_SALE_PAY_CARD TSSPC , TB_MS_STORE TMS WHERE TSSPC.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSSPC.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR TSSPC.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND TSSPC.SALE_DATE >= #{startDate}
AND TSSPC.SALE_DATE <= #{endDate}
AND TSSPC.SALE_FG = #{apprFg}
AND TMS.HQ_OFFICE_CD = TSSPC.HQ_OFFICE_CD AND TMS.STORE_CD = TSSPC.STORE_CD ORDER BY TSSPC.STORE_CD , TSSPC.SALE_DATE , TSSPC.POS_NO , TSSPC.BILL_NO

/* USE : ReportKwuMapper.getCashBillInfoList */ SELECT TO_CHAR(TO_DATE(SUBSTR(tsspc.APPR_DT, 0, 8), 'yyyymmdd'), 'yyyy-mm-dd') AS APPR_DATE , DECODE(tsspc.SALE_FG, 1, '승인', -1, '취소') AS APPR_GUBUN , DECODE(tsspc.APPR_PROC_FG, 1, '포스승인', 2, '단말기승인') AS APPR_PROC_NM , tsspc.SALE_AMT AS APPR_AMT , tsspc.CASH_BILL_CARD_NO , tsspc.BILL_NO FROM TB_SL_SALE_PAY_CASH tsspc , TB_CM_NMCODE tcn , TB_MS_STORE tms WHERE tsspc.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspc.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsspc.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsspc.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tcn.NMCODE_GRP_CD (+) = '018' AND tcn.NMCODE_CD (+) = tsspc.APPR_PROC_FG AND tms.HQ_OFFICE_CD = tsspc.HQ_OFFICE_CD AND tms.STORE_CD = tsspc.STORE_CD AND tsspc.APPR_PROC_FG IN ('1','2','4')
ORDER BY tsspc.STORE_CD , tms.STORE_NM , tsspc.APPR_DT
