mapper.select:
/* RtnStatusDayMapper.getRtnStatusDayList */
SELECT M.STORE_CD , M.STORE_NM , M.CNT_Y , M.REAL_SALE_AMT_Y , M.CNT_N , M.REAL_SALE_AMT_N , M.CNT , M.REAL_SALE_AMT
FROM ( SELECT TSSH.STORE_CD , TMS.STORE_NM , SUM(TSSH.SALE_CNT) AS CNT_Y , SUM(TSSH.REAL_SALE_AMT - TSSH.RTN_REAL_SALE_AMT) AS REAL_SALE_AMT_Y , SUM(TSSH.RTN_SALE_CNT) AS CNT_N , SUM(TSSH.RTN_REAL_SALE_AMT) AS REAL_SALE_AMT_N , SUM(TSSH.REAL_SALE_CNT) AS CNT , SUM(TSSH.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL TSSH, TB_MS_STORE TMS WHERE TSSH.HQ_OFFICE_CD = #{hqOfficeCd} AND TSSH.STORE_CD = TMS.STORE_CD
AND TSSH.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR TSSH.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND TSSH.SALE_DATE >= #{startDate}
AND TSSH.SALE_DATE <= #{endDate}
GROUP BY TSSH.STORE_CD, TMS.STORE_NM ) M ORDER BY M.STORE_CD

/* RtnStatusDayMapper.getRtnStatusDayDtlList */
SELECT M.STORE_CD , TO_CHAR(TO_DATE(M.SALE_DATE, 'YYYYMMDD'),'YYYY-MM-DD') SALE_DATE , M.CNT_Y , M.REAL_SALE_AMT_Y , M.CNT_N , M.REAL_SALE_AMT_N , M.CNT , M.REAL_SALE_AMT
FROM ( SELECT TSSH.STORE_CD , TSSH.SALE_DATE , SUM(TSSH.SALE_CNT) AS CNT_Y , SUM(TSSH.REAL_SALE_AMT - TSSH.RTN_REAL_SALE_AMT) AS REAL_SALE_AMT_Y , SUM(TSSH.RTN_SALE_CNT) AS CNT_N , SUM(TSSH.RTN_REAL_SALE_AMT) AS REAL_SALE_AMT_N , SUM(TSSH.REAL_SALE_CNT) AS CNT , SUM(TSSH.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL TSSH WHERE TSSH.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSSH.SALE_DATE >= #{startDate}
AND TSSH.SALE_DATE <= #{endDate}
AND TSSH.STORE_CD = #{storeCd}
GROUP BY TSSH.STORE_CD, TSSH.SALE_DATE ) M ORDER BY M.STORE_CD, M.SALE_DATE DESC

/* RtnStatusDayMapper.getRtnStatusPosDtlList */
SELECT STORE_CD , SALE_DATE , POS_NO, BILL_NO , DECODE(SALE_YN,'Y','매출','반품') AS SALE_YN , TOT_SALE_AMT , TOT_DC_AMT , REAL_SALE_AMT
FROM TB_SL_SALE_HDR TSSH WHERE TSSH.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSSH.SALE_DATE = REPLACE(#{saleDate},'-','')
AND TSSH.STORE_CD = #{storeCd}
AND TSSH.SALE_YN = #{saleYn}
ORDER BY TSSH.POS_NO, TSSH.BILL_NO

/* RtnStatusDayMapper.getRtnStatusProdList */
SELECT PATH_NM, PROD_CD, PROD_NM, BARCD_CD, SUM(CNT) AS CNT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT
FROM ( SELECT FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM, --NM_STR tssh.PROD_CD, tmp.PROD_NM, tmpb.BARCD_CD, tssh.CNT, tssh.REAL_SALE_AMT FROM ( SELECT HQ_OFFICE_CD, STORE_CD, PROD_CD, COUNT(*) AS CNT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_SALE_DTL WHERE 1=1 AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND SALE_DATE BETWEEN #{startDate} AND #{endDate} AND SALE_YN = 'N' GROUP BY HQ_OFFICE_CD, STORE_CD, PROD_CD ) tssh, TB_MS_PRODUCT tmp, TB_MS_PRODUCT_BARCD tmpb WHERE 1=1 AND tmp.STORE_CD = tssh.STORE_CD AND tmp.PROD_CD = tssh.PROD_CD AND tmpb.STORE_CD (+)= tmp.STORE_CD AND tmpb.PROD_CD (+)= tmp.PROD_CD ORDER BY tssh.PROD_CD ) GROUP BY PROD_CD, PROD_NM, BARCD_CD, PATH_NM

/* RtnStatusDayMapper.getRtnStatusProdExcelList */ SELECT PATH_NM, PROD_CD, PROD_NM, BARCD_CD, SUM(CNT) AS CNT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM, --NM_STR tssh.PROD_CD, tmp.PROD_NM, tmpb.BARCD_CD, tssh.CNT, tssh.REAL_SALE_AMT FROM ( SELECT HQ_OFFICE_CD, STORE_CD, PROD_CD, COUNT(*) AS CNT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_SALE_DTL WHERE 1=1 AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND SALE_DATE BETWEEN #{startDate} AND #{endDate} AND SALE_YN = 'N' GROUP BY HQ_OFFICE_CD, STORE_CD, PROD_CD ) tssh, TB_MS_PRODUCT tmp, TB_MS_PRODUCT_BARCD tmpb WHERE 1=1 AND tmp.STORE_CD = tssh.STORE_CD AND tmp.PROD_CD = tssh.PROD_CD AND tmpb.STORE_CD (+)= tmp.STORE_CD AND tmpb.PROD_CD (+)= tmp.PROD_CD ORDER BY tssh.PROD_CD ) GROUP BY PROD_CD, PROD_NM, BARCD_CD, PATH_NM

/* RtnStatusDayMapper.getRtnstatusDayExcelList */
SELECT M.STORE_CD , M.STORE_NM , M.CNT_Y , M.REAL_SALE_AMT_Y , M.CNT_N , M.REAL_SALE_AMT_N , M.CNT , M.REAL_SALE_AMT
FROM ( SELECT TSSH.STORE_CD , TMS.STORE_NM , SUM(TSSH.SALE_CNT) AS CNT_Y , SUM(TSSH.REAL_SALE_AMT - TSSH.RTN_REAL_SALE_AMT) AS REAL_SALE_AMT_Y , SUM(TSSH.RTN_SALE_CNT) AS CNT_N , SUM(TSSH.RTN_REAL_SALE_AMT) AS REAL_SALE_AMT_N , SUM(TSSH.REAL_SALE_CNT) AS CNT , SUM(TSSH.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL TSSH, TB_MS_STORE TMS WHERE TSSH.HQ_OFFICE_CD = #{hqOfficeCd} AND TSSH.STORE_CD = TMS.STORE_CD
AND TSSH.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR TSSH.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND TSSH.SALE_DATE >= #{startDate}
AND TSSH.SALE_DATE <= #{endDate}
GROUP BY TSSH.STORE_CD, TMS.STORE_NM ) M ORDER BY M.STORE_CD

/* RtnStatusDayMapper.getRtnstatusDayDtlExcelList */
SELECT M.STORE_CD , TO_CHAR(TO_DATE(M.SALE_DATE, 'YYYYMMDD'),'YYYY-MM-DD') SALE_DATE , M.CNT_Y , M.REAL_SALE_AMT_Y , M.CNT_N , M.REAL_SALE_AMT_N , M.CNT , M.REAL_SALE_AMT
FROM ( SELECT TSSH.STORE_CD , TSSH.SALE_DATE , SUM(TSSH.SALE_CNT) AS CNT_Y , SUM(TSSH.REAL_SALE_AMT - TSSH.RTN_REAL_SALE_AMT) AS REAL_SALE_AMT_Y , SUM(TSSH.RTN_SALE_CNT) AS CNT_N , SUM(TSSH.RTN_REAL_SALE_AMT) AS REAL_SALE_AMT_N , SUM(TSSH.REAL_SALE_CNT) AS CNT , SUM(TSSH.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL TSSH WHERE TSSH.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSSH.SALE_DATE >= #{startDate}
AND TSSH.SALE_DATE <= #{endDate}
AND TSSH.STORE_CD = #{storeCd}
GROUP BY STORE_CD, SALE_DATE ) M ORDER BY M.STORE_CD, M.SALE_DATE DESC

/* RtnStatusDayMapper.getRtnStatusPosDtlExcelList */
SELECT STORE_CD , SALE_DATE , POS_NO, BILL_NO , DECODE(SALE_YN,'Y','매출','반품') AS SALE_YN , TOT_SALE_AMT , TOT_DC_AMT , REAL_SALE_AMT
FROM TB_SL_SALE_HDR TSSH WHERE TSSH.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSSH.SALE_DATE = REPLACE(#{saleDate},'-','')
AND TSSH.STORE_CD = #{storeCd}
AND TSSH.SALE_YN = #{saleYn}
ORDER BY TSSH.POS_NO, TSSH.BILL_NO

/* USE : TodayDtlMapper.getGuestColList */
SELECT A.NMCODE_CD AS GUEST_CD, NVL(B.NMCODE_NM, '미등록') AS GUEST_NM FROM TB_CM_NMCODE A, TB_HQ_NMCODE B WHERE 1=1 AND A.NMCODE_GRP_CD = '119' AND B.HQ_OFFICE_CD (+)= #{hqOfficeCd} AND B.NMCODE_GRP_CD (+)= A.NMCODE_GRP_CD AND B.NMCODE_CD (+)= A.NMCODE_CD ORDER BY A.NMCODE_CD
SELECT A.NMCODE_CD AS GUEST_CD, NVL(B.NMCODE_NM, '미등록') AS GUEST_NM FROM TB_CM_NMCODE A, TB_MS_STORE_NMCODE B WHERE 1=1 AND A.NMCODE_GRP_CD = '119' AND B.STORE_CD (+)= #{storeCd} AND B.NMCODE_GRP_CD (+)= A.NMCODE_GRP_CD AND B.NMCODE_CD (+)= A.NMCODE_CD ORDER BY A.NMCODE_CD
