mapper.select:
SELECT M.STORE_CD , M.STORE_NM , SUM(DECODE(M.SALE_YN,'Y', M.CNT, 0)) AS CNT_Y , SUM(DECODE(M.SALE_YN,'Y', M.REAL_SALE_AMT, 0)) AS REAL_SALE_AMT_Y , SUM(DECODE(M.SALE_YN,'N', M.CNT, 0)) AS CNT_N , SUM(DECODE(M.SALE_YN,'N', M.REAL_SALE_AMT, 0)) AS REAL_SALE_AMT_N , SUM(M.CNT) AS CNT , SUM(M.REAL_SALE_AMT) AS REAL_SALE_AMT
FROM ( SELECT TSSH.STORE_CD, TMS.STORE_NM, TSSH.SALE_YN, SUM(TSSH.REAL_SALE_AMT) REAL_SALE_AMT, COUNT(*) CNT FROM TB_SL_SALE_HDR TSSH, TB_MS_STORE TMS WHERE TSSH.HQ_OFFICE_CD = #{hqOfficeCd} AND TSSH.STORE_CD = TMS.STORE_CD
AND TSSH.STORE_CD IN
#{item}
AND TSSH.SALE_DATE >= #{startDate}
AND TSSH.SALE_DATE <= #{endDate}
GROUP BY TSSH.STORE_CD, TMS.STORE_NM, TSSH.SALE_YN ) M GROUP BY M.STORE_CD, M.STORE_NM ORDER BY M.STORE_CD

SELECT M.STORE_CD , TO_CHAR(TO_DATE(M.SALE_DATE, 'YYYYMMDD'),'YYYY-MM-DD') SALE_DATE , SUM(DECODE(M.SALE_YN,'Y', M.CNT, 0)) AS CNT_Y , SUM(DECODE(M.SALE_YN,'Y', M.REAL_SALE_AMT, 0)) AS REAL_SALE_AMT_Y , SUM(DECODE(M.SALE_YN,'N', M.CNT, 0)) AS CNT_N , SUM(DECODE(M.SALE_YN,'N', M.REAL_SALE_AMT, 0)) AS REAL_SALE_AMT_N , SUM(M.CNT) AS CNT , SUM(M.REAL_SALE_AMT) AS REAL_SALE_AMT
FROM ( SELECT STORE_CD, SALE_YN, SALE_DATE, SUM(REAL_SALE_AMT) REAL_SALE_AMT, COUNT(*) CNT FROM TB_SL_SALE_HDR TSSH WHERE TSSH.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSSH.SALE_DATE >= #{startDate}
AND TSSH.SALE_DATE <= #{endDate}
AND TSSH.STORE_CD = #{storeCd}
GROUP BY STORE_CD, SALE_YN, SALE_DATE ) M GROUP BY M.STORE_CD, M.SALE_DATE ORDER BY M.STORE_CD, M.SALE_DATE DESC

SELECT STORE_CD , SALE_DATE , POS_NO, BILL_NO , DECODE(SALE_YN,'Y','매출','반품') AS SALE_YN , TOT_SALE_AMT , TOT_DC_AMT , REAL_SALE_AMT
FROM TB_SL_SALE_HDR TSSH WHERE TSSH.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSSH.SALE_DATE = REPLACE(#{saleDate},'-','')
AND TSSH.STORE_CD = #{storeCd}
AND TSSH.SALE_YN = #{saleYn}
ORDER BY TSSH.POS_NO, TSSH.BILL_NO
