mapper.select:
/* SaleMcouponMapper.getSaleMcouponList */
SELECT tms.BRANCH_CD, (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM, tsspm.STORE_CD, tms.STORE_NM, tms.HQ_BRAND_CD AS BRAND_CD, tmsi.MOMS_TEAM, tmsi.MOMS_AC_SHOP,
TO_CHAR(TO_DATE(tsspm.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE,
COUNT(tsspm.SALE_FG) AS SALE_CNT, SUM(tsspm.SALE_AMT) AS SALE_AMT, SUM(CASE WHEN tsspm.SALE_FG = 1 THEN 1 ELSE 0 END) AS APPR_CNT, SUM(CASE WHEN tsspm.SALE_FG = 1 THEN tsspm.SALE_AMT ELSE 0 END) AS APPR_AMT, SUM(CASE WHEN tsspm.SALE_FG = -1 THEN 1 ELSE 0 END) AS CANCEL_CNT, SUM(CASE WHEN tsspm.SALE_FG = -1 THEN tsspm.SALE_AMT ELSE 0 END) AS CANCEL_AMT
FROM TB_SL_SALE_PAY_MCOUPN tsspm, TB_MS_STORE tms, TB_MS_STORE_INFO tmsi WHERE 1=1 AND tsspm.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsspm.STORE_CD IN
#{item}
AND tsspm.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tms.HQ_OFFICE_CD = tsspm.HQ_OFFICE_CD AND tms.STORE_CD = tsspm.STORE_CD AND tmsi.STORE_CD (+)= tsspm.STORE_CD
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY tms.BRANCH_CD, tsspm.STORE_CD, tms.STORE_NM, tms.HQ_BRAND_CD, tmsi.MOMS_TEAM, tmsi.MOMS_AC_SHOP
, tsspm.SALE_DATE ORDER BY tsspm.SALE_DATE
ORDER BY tms.BRANCH_CD, tsspm.STORE_CD

