/* SaleMcouponMapper.getSaleMcouponDtlList */
SELECT tsspm.STORE_CD, tms.STORE_NM, TO_CHAR(TO_DATE(tsspm.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE, COUNT(tsspm.SALE_FG) AS SALE_CNT, SUM(tsspm.SALE_AMT) AS SALE_AMT FROM TB_SL_SALE_PAY_MCOUPN tsspm, TB_MS_STORE tms WHERE 1=1 AND tsspm.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspm.STORE_CD = #{storeCd} AND tsspm.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tms.STORE_CD = tsspm.STORE_CD GROUP BY tsspm.STORE_CD, tms.STORE_NM, tsspm.SALE_DATE ORDER BY tsspm.STORE_CD, tsspm.SALE_DATE
SELECT TO_CHAR(TO_DATE(tsspm.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE, tsspm.POS_NO, tsspm.BILL_NO, tsspm.LINE_NO, tsspm.LINE_SEQ_NO, tsspm.SALE_YN, tsspm.SALE_AMT FROM TB_SL_SALE_PAY_MCOUPN tsspm WHERE 1=1 AND tsspm.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspm.STORE_CD = #{storeCd} AND tsspm.SALE_DATE = #{saleDate} ORDER BY tsspm.SALE_DATE, tsspm.POS_NO, tsspm.BILL_NO, tsspm.LINE_NO, tsspm.LINE_SEQ_NO
