mapper.select:
/* ServiceTimeAvgMapper.getServiceTimeAvgList */ SELECT (CASE WHEN SALE_TIME IN ('07') THEN '00시~07시' ELSE (SALE_TIME || '시') END) AS SALE_TIME,
SUM(STORE_${item}_TC) AS STORE_${item}_TC ,
TO_CHAR(TO_DATE(SUM(STORE_${item}_SERVICE_TIME), 'SSSSS'), 'HH24:MI:SS') AS STORE_${item}_SERVICE_TIME
FROM ( SELECT SALE_TIME,
SUM(STORE_${item}_TC) AS STORE_${item}_TC ,
SUM(STORE_${item}_SERVICE_TIME) AS STORE_${item}_SERVICE_TIME
-- SUM(STORE_E01085_TC) AS STORE_E01085_TC, -- SUM(STORE_E01085_SERVICE_TIME) AS STORE_E01085_SERVICE_TIME, FROM ( SELECT tpotdt.STORE_CD, (CASE WHEN tpotdt.SALE_HH IN ('00', '01', '02', '03', '04', '05', '06', '07') THEN '07' ELSE tpotdt.SALE_HH END) AS SALE_TIME, SUM(tpotdt.ORDER_CNT) AS TC, DECODE(SUM(tpotdt.ORDER_CNT), 0, 0, ROUND(SUM(tpotdt.TOT_SVC_TIME)/SUM(tpotdt.ORDER_CNT))) AS SERVICE_TIME FROM TB_PS_ORDER_TRACKING_DAILY_TIME tpotdt, TB_MS_STORE tms, TB_MS_STORE_INFO tmsi WHERE 1=1 AND tpotdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tpotdt.STORE_CD IN (${storeCdQuery})
AND tpotdt.SALE_DATE = #{startDate}
AND tpotdt.SERVICE_TYPE = #{serviceType}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tms.HQ_OFFICE_CD = tpotdt.HQ_OFFICE_CD AND tms.STORE_CD = tpotdt.STORE_CD AND tmsi.STORE_CD (+)= tms.STORE_CD GROUP BY tpotdt.STORE_CD, tpotdt.SALE_HH ) PIVOT ( SUM(TC) AS TC, SUM(SERVICE_TIME) AS SERVICE_TIME FOR STORE_CD IN (${pivotStoreCol}) -- IN ('E01085' AS STORE_E01085, -- 'E01095' AS STORE_E01095) ) GROUP BY SALE_TIME UNION ALL
SELECT (CASE WHEN (level+6) < 10 THEN '0'||TO_CHAR(level+6) ELSE TO_CHAR(level+6) END) AS SALE_TIME,
0 AS STORE_${item}_TC, 0 AS STORE_${item}_SERVICE_TIME
FROM dual CONNECT BY LEVEL <= 17
) GROUP BY SALE_TIME ORDER BY SALE_TIME
