mapper.select:
/* USE : SideMapper.sideProdClass */
SELECT PATH, PATH_NM, PROD_CD, PROD_NM, SIDE_PROD_CD, SIDE_PROD_NM, SEL_TYPE_FG, BARCD_CD, SUM(TOT_SALE_QTY) AS TOT_SALE_QTY, SUM(TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(TOT_DC_AMT) AS TOT_DC_AMT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT
FROM ( SELECT FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'CD_STR') AS PATH, --CD_STR FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM, --NM_STR tsdp.PROD_CD, CASE tsdp.SEL_TYPE_FG WHEN 'S' THEN tsdp.SIDE_PROD_CD END AS SIDE_PROD_CD, CASE tsdp.SEL_TYPE_FG WHEN 'P' THEN '사이드모상품' WHEN 'S' THEN '사이드상품' WHEN 'N' THEN '일반판매상품' END AS SEL_TYPE_FG, tmp.PROD_NM, CASE tsdp.SEL_TYPE_FG WHEN 'S' THEN 'ㅤ- ▶ ' || tmps.PROD_NM ELSE tmps.PROD_NM END AS SIDE_PROD_NM, tmpb.BARCD_CD, SUM(tsdp.TOT_SALE_QTY) AS TOT_SALE_QTY, SUM(tsdp.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdp.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdp.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_PROD_SDSEL tsdp, TB_MS_PRODUCT tmp, TB_MS_PRODUCT tmps, TB_MS_PRODUCT_BARCD tmpb WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND tmp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND tmp.STORE_CD = tsdp.STORE_CD AND tmp.PROD_CD = tsdp.PROD_CD AND tmps.STORE_CD = tsdp.STORE_CD AND tmps.PROD_CD = tsdp.SIDE_PROD_CD AND tmpb.STORE_CD (+)= tmps.STORE_CD AND tmpb.PROD_CD (+)= tmps.PROD_CD GROUP BY tsdp.PROD_CD, tmp.PROD_NM, tmpb.BARCD_CD, tmp.STORE_CD, tmp.PROD_CLASS_CD, tsdp.SIDE_PROD_CD, tmps.PROD_NM, tsdp.SEL_TYPE_FG ) WHERE 1=1
AND PATH LIKE '%'|| #{prodClassCd} ||'%'
GROUP BY PROD_CD, PROD_NM, BARCD_CD, PATH, PATH_NM, SIDE_PROD_CD, SIDE_PROD_NM, SEL_TYPE_FG ORDER BY PROD_CD, SEL_TYPE_FG

