mapper.select:
/* StoreProdSaleReportMapper.getStoreProdSaleReportList */ SELECT SUBSTR(thsr.FROM_SALE_DATE, 0, 6) AS SALE_MONTH, '전체매장통합파일' AS PROC_GUBUN, thsr.PROC_DT, (SELECT USER_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE USER_ID = thsr.REG_ID) AS USER_NM, thsr.PROC_FG, thsr.PROC_MSG, '다운로드' AS DOWNLOAD, thsr.FROM_SALE_DATE, thsr.TO_SALE_DATE, thsr.FILE_NAME FROM TB_HQ_SALE_REPORT thsr WHERE 1=1 AND thsr.HQ_OFFICE_CD = #{hqOfficeCd}
AND thsr.FROM_SALE_DATE >= #{startMonth}||'01' AND thsr.TO_SALE_DATE <= #{endMonth}||'31'
AND thsr.REPORT_TYPE = '01' ORDER BY thsr.FROM_SALE_DATE DESC

mapper.delete:
/* StoreProdSaleReportMapper.getStoreProdSaleReportDel */
DELETE TB_HQ_SALE_REPORT
WHERE 1=1
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND FROM_SALE_DATE = #{fromSaleDate}
AND TO_SALE_DATE = #{toSaleDate}
AND REPORT_TYPE = '01'

mapper.select:
/* StoreProdSaleReportMapper.getStoreProdSaleReportChk */
SELECT
COUNT(thsr.REPORT_TYPE) AS CNT
FROM TB_HQ_SALE_REPORT thsr
WHERE 1=1
AND thsr.HQ_OFFICE_CD = #{hqOfficeCd}
AND thsr.FROM_SALE_DATE = #{dataCreateMonth}||'01'
AND thsr.TO_SALE_DATE = #{dataCreateMonth}||#{dataCreateMonthLastDate}
AND thsr.REPORT_TYPE = '01'

mapper.insert:
/* StoreProdSaleReportMapper.getBranchAreaSaveUpdate */
UPDATE TB_HQ_NMCODE
SET
NMCODE_NM = #{branchNm},
MOD_DT = #{modDt},
MOD_ID = #{modId}
WHERE 1=1
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND NMCODE_GRP_CD = 'N001'
AND PARENT_CD = 'N001'
AND NMCODE_CD = #{branchCd}

mapper.delete:
/* StoreProdSaleReportMapper.getBranchAreaSaveDelete */
DELETE TB_HQ_NMCODE
WHERE 1=1
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND NMCODE_GRP_CD = 'N001'
AND PARENT_CD = 'N001'
AND NMCODE_CD = #{branchCd}

/* StoreProdSaleReportMapper.getBranchAreaDetailSaveDeleteAll */
DELETE TB_HQ_NMCODE
WHERE 1=1
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND NMCODE_GRP_CD = 'N001'
AND PARENT_CD = #{branchCd}

mapper.select:
/* StoreProdSaleReportMapper.getBranchAreaDetailList */
SELECT
thn.NMCODE_CD AS AREA_CD,
thn.NMCODE_NM AS AREA_NM
FROM TB_HQ_NMCODE thn
WHERE 1=1
AND thn.HQ_OFFICE_CD = #{hqOfficeCd}
AND thn.NMCODE_GRP_CD = 'N001'
AND thn.PARENT_CD = #{branchCd}

/* StoreProdSaleReportMapper.getBranchAreaAreaCd */
SELECT
LPAD(NVL(MAX(thn.NMCODE_CD), 0) + 1, 4, '0')
FROM TB_HQ_NMCODE thn
WHERE 1=1
AND thn.HQ_OFFICE_CD = #{hqOfficeCd}
AND thn.NMCODE_GRP_CD = 'N001'
AND thn.PARENT_CD = #{branchCd}

mapper.insert:
/* StoreProdSaleReportMapper.getBranchAreaDetailSaveUpdate */
UPDATE TB_HQ_NMCODE
SET
NMCODE_NM = #{areaNm},
MOD_DT = #{modDt},
MOD_ID = #{modId}
WHERE 1=1
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND NMCODE_GRP_CD = 'N001'
AND PARENT_CD = #{branchCd}
AND NMCODE_CD = #{areaCd}

mapper.delete:
/* StoreProdSaleReportMapper.getBranchAreaDetailSaveDelete */
DELETE TB_HQ_NMCODE
WHERE 1=1
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND NMCODE_GRP_CD = 'N001'
AND PARENT_CD = #{branchCd}
AND NMCODE_CD = #{areaCd}

mapper.select:
/* StoreProdSaleReportMapper.getAreaStoreMappingStoreList */ SELECT tms.STORE_CD, tms.STORE_NM, tms.BIZ_NO, tms.SYS_STAT_FG FROM TB_MS_STORE tms WHERE 1=1 AND tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tms.STORE_CD NOT IN ( SELECT thas.STORE_CD FROM TB_HQ_AREA_STORE thas WHERE 1=1 AND thas.HQ_OFFICE_CD = #{hqOfficeCd} )
AND tms.STORE_CD LIKE '%'|| #{storeCd} ||'%'
AND tms.STORE_NM LIKE '%'|| #{storeNm} ||'%'
AND tms.BIZ_NO LIKE '%'|| #{bizNo} ||'%'
AND tms.SYS_STAT_FG = #{sysStatFg}
ORDER BY tms.STORE_CD

mapper.delete:
/* StoreProdSaleReportMapper.getAreaStoreMappingDetailSaveDelete */
DELETE TB_HQ_AREA_STORE
WHERE 1=1
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD = #{storeCd}
AND BRANCH_CD = #{branchCd}
AND AREA_CD = #{areaCd}
