FROM TB_SL_DAILY_TABLE TSDT WHERE 1 = 1
AND STORE_CD IN
#{item}
AND TBL_CD in (${sQuery2})
AND TSDT.SALE_DATE >= #{startDate} AND TSDT.SALE_DATE <= #{endDate}
GROUP BY SALE_DATE ORDER BY SALE_DATE DESC

SELECT tmp.TBL_CD AS NMCODE_CD
