mapper.select:

SELECT TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'DY') SALE_DAY
		, SUM(REAL_SALE_AMT) TOT_REAL_SALE_AMT
		, SUM(REAL_SALE_CNT) TOT_REAL_SALE_CNT
		, SUM(GUEST_CNT_1) TOT_GUEST_CNT
		${sQuery1}

FROM TB_SL_DAILY_TABLE TSDT
WHERE 1 = 1


	AND STORE_CD||'||'||TBL_CD IN

		#{item}



  	AND TSDT.SALE_DATE >=  #{startDate}


  	AND TSDT.SALE_DATE <=  #{endDate}


GROUP BY TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'DY')
ORDER BY TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'DY') DESC
