mapper.select:
/* USE : WeightMapper.getWeightList */ SELECT A.STORE_CD, D.STORE_NM, TO_CHAR(TO_DATE(B.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE, B.POS_NO, B.BILL_NO, A.PROD_CD, A.PROD_NM, C.BARCD_CD, B.SALE_UPRC, B.SALE_SCALE_WT,
CASE WHEN B.SALE_SCALE_WT >= 1000 THEN B.SALE_SCALE_WT * 0.001 || 'Kg' ELSE B.SALE_SCALE_WT || 'g' END AS SALE_SCALE_WT_KG,
B.SALE_QTY, CASE B.SALE_YN WHEN 'Y' THEN (B.SALE_SCALE_WT * B.SALE_QTY) WHEN 'N' THEN -1 * (B.SALE_SCALE_WT * B.SALE_QTY) END AS TOT_SALE_SCALE_WT, B.SALE_AMT, B.DC_AMT, B.REAL_SALE_AMT FROM TB_MS_PRODUCT A, TB_SL_SALE_DTL B, TB_MS_PRODUCT_BARCD C, TB_MS_STORE D WHERE 1=1 AND D.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.STORE_CD = #{storeCd}
AND A.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR A.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND A.PROD_TYPE_FG = '3' AND B.STORE_CD = A.STORE_CD AND B.PROD_CD = A.PROD_CD AND B.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND C.STORE_CD (+) = A.STORE_CD AND C.PROD_CD (+) = A.PROD_CD AND D.STORE_CD = A.STORE_CD
AND A.PROD_CD = #{prodCd}
AND A.PROD_NM = #{prodNm}
ORDER BY A.STORE_CD, B.SALE_DATE, B.POS_NO, B.BILL_NO

/* USE : WeightMapper.getWeightDayList */ SELECT A.STORE_CD, D.STORE_NM, TO_CHAR(TO_DATE(B.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE, A.PROD_CD, A.PROD_NM, C.BARCD_CD, B.SALE_UPRC, B.SALE_SCALE_WT,
CASE WHEN B.SALE_SCALE_WT >= 1000 THEN B.SALE_SCALE_WT * 0.001 || 'Kg' ELSE B.SALE_SCALE_WT || 'g' END AS SALE_SCALE_WT_KG,
SUM(B.SALE_QTY) AS SALE_QTY, CASE B.SALE_YN WHEN 'Y' THEN SUM(SALE_SCALE_WT * B.SALE_QTY) WHEN 'N' THEN -1 * SUM(SALE_SCALE_WT * B.SALE_QTY) END AS TOT_SALE_SCALE_WT, SUM(B.SALE_AMT) AS SALE_AMT, SUM(B.DC_AMT) AS DC_AMT, SUM(B.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_MS_PRODUCT A, TB_SL_SALE_DTL B, TB_MS_PRODUCT_BARCD C, TB_MS_STORE D WHERE 1=1 AND D.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.STORE_CD = #{storeCd}
AND A.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR A.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND A.PROD_TYPE_FG = '3' AND B.STORE_CD = A.STORE_CD AND B.PROD_CD = A.PROD_CD AND B.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND C.STORE_CD (+) = A.STORE_CD AND C.PROD_CD (+) = A.PROD_CD AND D.STORE_CD = A.STORE_CD
AND A.PROD_CD = #{prodCd}
AND A.PROD_NM = #{prodNm}
GROUP BY A.STORE_CD, D.STORE_NM, B.SALE_DATE, A.PROD_CD, A.PROD_NM, C.BARCD_CD, B.SALE_UPRC, B.SALE_YN, B.SALE_SCALE_WT ORDER BY A.STORE_CD, B.SALE_DATE, A.PROD_CD
