mapper.select:
/* USE : OrderAcceptHistoryMapper.getSearchOrderAcceptHistory */
SELECT	TO_CHAR(TO_DATE(A.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE
,       TO_CHAR(TO_DATE(A.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL
,		B.BRANCH_CD
,       (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS WHERE HQ_OFFICE_CD = 'H0393' AND BRANCH_CD = B.BRANCH_CD) AS BRANCH_NM
,		A.HQ_OFFICE_CD
,		A.STORE_CD
,		B.STORE_NM
,       (SELECT HQ_BRAND_NM FROM TB_HQ_BRAND WHERE HQ_OFFICE_CD = B.HQ_OFFICE_CD AND HQ_BRAND_CD = B.HQ_BRAND_CD) AS BRAND_NM
,		C.MOMS_TEAM
,		C.MOMS_AC_SHOP
,       TO_CHAR(TO_DATE(A.SYS_DATE, 'YYYYMMDD'), 'YYYY-MM-DD') AS SYS_DATE
,       A.DLVR_IN_FG
,       TO_CHAR(TO_DATE(A.CHANGE_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS CHANGE_DT
,		A.BEFORE_STATUS
,		A.ACCEPT_STATUS
,		A.EMP_NO
,       (SELECT EMP_NM FROM TB_MS_EMPLOYEE WHERE EMP_NO = A.EMP_NO AND STORE_CD = A.STORE_CD) AS USER_NM
FROM	TB_PS_ORDER_ACCEPT_HISTORY	A
,		TB_MS_STORE B
,       TB_MS_STORE_INFO C
WHERE 	B.HQ_OFFICE_CD 	=	A.HQ_OFFICE_CD
AND 	B.STORE_CD 		=	A.STORE_CD
AND    	C.STORE_CD 	(+) =	A.STORE_CD
AND     A.HQ_OFFICE_CD  =   #{hqOfficeCd}
AND     A.SALE_DATE     BETWEEN #{startDate} AND #{endDate}

    AND A.STORE_CD IN (${storeCdQuery})


    AND B.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때



        -- 매장브랜드 전체일때
        AND B.HQ_BRAND_CD IN

            #{item}




    AND C.MOMS_TEAM = #{momsTeam}


    AND C.MOMS_AC_SHOP = #{momsAcShop}


    AND C.MOMS_AREA_FG = #{momsAreaFg}


    AND C.MOMS_COMMERCIAL = #{momsCommercial}


    AND C.MOMS_SHOP_TYPE = #{momsShopType}


    AND C.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}


    AND B.BRANCH_CD = #{branchCd}


    AND C.MOMS_STORE_FG_01 = #{momsStoreFg01}


    AND C.MOMS_STORE_FG_02 = #{momsStoreFg02}


    AND C.MOMS_STORE_FG_03 = #{momsStoreFg03}


    AND C.MOMS_STORE_FG_04 = #{momsStoreFg04}


    AND C.MOMS_STORE_FG_05 = #{momsStoreFg05}

ORDER
BY      B.STORE_CD
,       B.HQ_BRAND_CD
,       C.MOMS_TEAM
,       C.MOMS_AC_SHOP
