mapper.select:
/* StoreAvgMapper.getStoreAvgList */
SELECT (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsdt.STORE_CD , tms.STORE_NM , COUNT(DISTINCT tsdt.SALE_DATE) AS DATE_CNT , ROUND(AVG(NVL(tsdt.BILL_CNT, 0))) AS BILL_CNT , ROUND(AVG(NVL(tsdt.REAL_SALE_AMT, 0)) / AVG(NVL(tsdt.BILL_CNT, 0))) AS BILL_UPRC -- 총 판매량 , ROUND(AVG(NVL(tsdt.TOT_SALE_AMT, 0))) AS TOT_SALE_AMT , ROUND(AVG(NVL(tsdt.REAL_SALE_AMT, 0))) AS REAL_SALE_AMT , ROUND(AVG(NVL(tsdt.TOT_DC_AMT, 0))) AS TOT_DC_AMT , ROUND(AVG(NVL(tsdt.NET_SALE_AMT, 0))) AS NET_SALE_AMT
FROM TB_SL_DAILY_TOTAL tsdt , TB_MS_STORE tms WHERE tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdt.STORE_CD IN
#{item}
GROUP BY tsdt.STORE_CD, tms.STORE_NM, tms.HQ_OFFICE_CD, tms.BRANCH_CD ORDER BY tsdt.STORE_CD

/* StoreAvgMapper.getStoreAvgExcelList */ SELECT (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsdt.STORE_CD , tms.STORE_NM , COUNT(DISTINCT tsdt.SALE_DATE) AS DATE_CNT , ROUND(AVG(NVL(tsdt.BILL_CNT, 0))) AS BILL_CNT , ROUND(AVG(NVL(tsdt.REAL_SALE_AMT, 0)) / AVG(NVL(tsdt.BILL_CNT, 0))) AS BILL_UPRC -- 총 판매량 , ROUND(AVG(NVL(tsdt.TOT_SALE_AMT, 0))) AS TOT_SALE_AMT , ROUND(AVG(NVL(tsdt.REAL_SALE_AMT, 0))) AS REAL_SALE_AMT , ROUND(AVG(NVL(tsdt.TOT_DC_AMT, 0))) AS TOT_DC_AMT , ROUND(AVG(NVL(tsdt.NET_SALE_AMT, 0))) AS NET_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt , TB_MS_STORE tms WHERE tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdt.STORE_CD IN
#{item}
GROUP BY tsdt.STORE_CD, tms.STORE_NM, tms.HQ_OFFICE_CD, tms.BRANCH_CD ORDER BY tsdt.STORE_CD
