mapper.select:
/* USE : StoreDayChannelMapper.getDayList */ SELECT TO_CHAR(TO_DATE(tsshp.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , tsshp.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tsshp.BRANCH_CD) AS BRANCH_NM , tsshp.STORE_CNT
, tsshp.STORE_CD , (SELECT STORE_NM FROM TB_MS_STORE tms WHERE tms.STORE_CD = tsshp.STORE_CD) AS STORE_NM
, tsshp.YOIL , tsshp.TOT_SALE_AMT , tsshp.TOT_DC_AMT , tsshp.REAL_SALE_AMT , tsshp.GA_AMT , tsshp.VAT_AMT , tsshp.TOT_TIP_AMT , tsshp.TOT_ETC_AMT , tsshp.CUP_AMT , tsshp.BILL_CNT , tsshp.BILL_UPRC , tsshp.STIN_BILL_CNT , tsshp.STIN_BILL_UPRC , tsshp.STIN_REAL_SALE_AMT , DECODE(tsshp.REAL_SALE_AMT, 0, 0, (tsshp.STIN_REAL_SALE_AMT / tsshp.REAL_SALE_AMT)*100) AS STIN_RATE , tsshp.DLVR_BILL_CNT , tsshp.DLVR_BILL_UPRC , tsshp.DLVR_REAL_SALE_AMT , DECODE(tsshp.REAL_SALE_AMT, 0, 0, (tsshp.DLVR_REAL_SALE_AMT / tsshp.REAL_SALE_AMT)*100) AS DLVR_RATE , tsshp.PACK_BILL_CNT , tsshp.PACK_BILL_UPRC , tsshp.PACK_REAL_SALE_AMT , DECODE(tsshp.REAL_SALE_AMT, 0, 0, (tsshp.PACK_REAL_SALE_AMT / tsshp.REAL_SALE_AMT)*100) AS PACK_RATE , NVL(tsshp.PAY${item}, 0) + NVL(tsshp.PAY02, 0) + NVL(tsshp.PAY021, 0) AS TOT_PAY_AMT , tsshp.PAY${item} AS PAY${item}
, tsshp.PAY02 AS PAY02 -- 현금 , tsshp.PAY021 AS PAY021 -- 현금영수증 , tsdto.SALE_QTY${item} AS SALE_QTY${item} , tsdto.REAL_SALE_AMT${item} AS REAL_SALE_AMT${item}
FROM ( SELECT tsdt.SALE_DATE , TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , tms.BRANCH_CD , COUNT(DISTINCT tsdt.STORE_CD) AS STORE_CNT
, tsdt.STORE_CD
, SUM(PAY_AMT_${item}) AS PAY${item}
, SUM(NON_CASH_AMT) AS PAY02 , SUM(APPR_CASH_AMT) AS PAY021 , SUM(TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(TOT_DC_AMT) AS TOT_DC_AMT , SUM(REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(REAL_SALE_AMT) - SUM(VAT_AMT) AS GA_AMT , SUM(VAT_AMT) AS VAT_AMT , SUM(TOT_TIP_AMT) AS TOT_TIP_AMT , SUM(TOT_ETC_AMT) AS TOT_ETC_AMT , SUM(CUP_AMT) AS CUP_AMT , SUM(REAL_SALE_CNT) AS BILL_CNT , DECODE(SUM(REAL_SALE_CNT), 0, 0, ROUND(SUM(REAL_SALE_AMT) / SUM(REAL_SALE_CNT))) AS BILL_UPRC , SUM(CASE WHEN DLVR_ORDER_FG = '1' THEN REAL_SALE_CNT ELSE 0 END) AS STIN_BILL_CNT , SUM(CASE WHEN DLVR_ORDER_FG = '1' THEN DECODE(REAL_SALE_CNT , 0, 0, ROUND(REAL_SALE_AMT / REAL_SALE_CNT)) ELSE 0 END) AS STIN_BILL_UPRC , SUM(CASE WHEN DLVR_ORDER_FG = '1' THEN REAL_SALE_AMT ELSE 0 END) AS STIN_REAL_SALE_AMT , SUM(CASE WHEN DLVR_ORDER_FG = '2' THEN REAL_SALE_CNT ELSE 0 END) AS DLVR_BILL_CNT , SUM(CASE WHEN DLVR_ORDER_FG = '2' THEN DECODE(REAL_SALE_CNT , 0, 0, ROUND(REAL_SALE_AMT / REAL_SALE_CNT)) ELSE 0 END) AS DLVR_BILL_UPRC , SUM(CASE WHEN DLVR_ORDER_FG = '2' THEN REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT , SUM(CASE WHEN DLVR_ORDER_FG = '3' THEN REAL_SALE_CNT ELSE 0 END) AS PACK_BILL_CNT , SUM(CASE WHEN DLVR_ORDER_FG = '3' THEN DECODE(REAL_SALE_CNT , 0, 0, ROUND(REAL_SALE_AMT / REAL_SALE_CNT)) ELSE 0 END) AS PACK_BILL_UPRC , SUM(CASE WHEN DLVR_ORDER_FG = '3' THEN REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL_DLVR tsdt , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN
#{item}
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.STORE_CD = tmsi.STORE_CD(+)
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY SALE_DATE, BRANCH_CD
, tsdt.STORE_CD
) tsshp , ( SELECT tms.BRANCH_CD , tsdto.SALE_DATE
, tsdto.STORE_CD
, SUM(CASE WHEN DLVR_IN_FG = ${item} THEN SALE_QTY END) AS SALE_QTY${item} , SUM(CASE WHEN DLVR_IN_FG = ${item} THEN REAL_SALE_AMT END) AS REAL_SALE_AMT${item}
FROM TB_SL_DAILY_TOTAL_ORDAPP tsdto , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tsdto.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdto.STORE_CD IN
#{item}
AND tsdto.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdto.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdto.STORE_CD = tms.STORE_CD AND tsdto.STORE_CD = tmsi.STORE_CD(+)
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY tms.BRANCH_CD, tsdto.SALE_DATE
, tsdto.STORE_CD
) tsdto WHERE tsdto.SALE_DATE (+) = tsshp.SALE_DATE -- AND tsdto.BRANCH_CD (+) = tsshp.BRANCH_CD
AND tsdto.STORE_CD (+) = tsshp.STORE_CD
ORDER BY tsshp.SALE_DATE, tsshp.BRANCH_CD
, tsshp.STORE_CD
