mapper.select:
/* StoreDayTimeMapper.getStoreDayTimeList */
SELECT COUNT(DISTINCT tssh.STORE_CD) AS STORE_CNT , tms.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM
, tssh.STORE_CD , tms.STORE_NM , tms.HQ_BRAND_CD AS BRAND , tmsi.MOMS_TEAM , tmsi.MOMS_AC_SHOP
, TO_CHAR(TO_DATE(SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL
, SUM(SALE_QTY) AS SALE_QTY , SUM(TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(TOT_DC_AMT) AS TOT_DC_AMT , SUM(REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(SALE_CNT) AS SALE_CNT , SUM(RTN_SALE_CNT) AS RTN_SALE_CNT , SUM(REAL_SALE_CNT) AS REAL_SALE_CNT , DECODE(SUM(REAL_SALE_CNT), 0, 0, ROUND(SUM(REAL_SALE_AMT) / SUM(REAL_SALE_CNT))) AS BILL_UPRC , SUM(CASE WHEN SALE_HOUR = ${item} THEN SALE_QTY END) AS SALE_QTY${item} , SUM(CASE WHEN SALE_HOUR = ${item} THEN REAL_SALE_AMT END) AS REAL_SALE_AMT${item}
FROM TB_SL_DAILY_TIME tssh , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tssh.HQ_OFFICE_CD = #{hqOfficeCd} AND tssh.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tssh.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tssh.STORE_CD = tms.STORE_CD AND tssh.STORE_CD = tmsi.STORE_CD(+)
AND tssh.STORE_CD IN
#{item}
AND SALE_HOUR BETWEEN #{startTime} AND #{endTime}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
HAVING SUM(TOT_SALE_AMT) IS NOT NULL GROUP BY tms.BRANCH_CD
, tssh.STORE_CD, tms.STORE_NM , tms.HQ_BRAND_CD , tmsi.MOMS_TEAM , tmsi.MOMS_AC_SHOP
, SALE_DATE
ORDER BY BRANCH_CD DESC
, tssh.STORE_CD , tms.HQ_BRAND_CD , tmsi.MOMS_TEAM , tmsi.MOMS_AC_SHOP
, SALE_DATE
SELECT STORE_CNT , tssh.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tssh.BRANCH_CD) AS BRANCH_NM
, tssh.STORE_CD , (SELECT STORE_NM FROM TB_MS_STORE tms WHERE tms.STORE_CD = tssh.STORE_CD) AS STORE_NM , tssh.HQ_BRAND_CD , tssh.MOMS_TEAM , tssh.MOMS_AC_SHOP
, TO_CHAR(TO_DATE(tssh.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , TO_CHAR(TO_DATE(tssh.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL
, SUM(SALE_QTY) AS SALE_QTY , SUM(TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(TOT_DC_AMT) AS TOT_DC_AMT , SUM(REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(SALE_CNT) AS SALE_CNT , SUM(RTN_SALE_CNT) AS RTN_SALE_CNT , SUM(REAL_SALE_CNT) AS REAL_SALE_CNT , DECODE(SUM(REAL_SALE_CNT), 0, 0, ROUND(SUM(REAL_SALE_AMT) / SUM(REAL_SALE_CNT))) AS BILL_UPRC , NVL(SUM(SALE_QTY${item}), 0) AS SALE_QTY${item} , NVL(SUM(REAL_SALE_AMT${item}), 0) AS REAL_SALE_AMT${item}
FROM ( SELECT BRANCH_CD , COUNT(DISTINCT STORE_CD) AS STORE_CNT
, STORE_CD , HQ_BRAND_CD , MOMS_TEAM , MOMS_AC_SHOP
, SALE_DATE
, SUM(SALE_QTY) AS SALE_QTY , SUM(TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(TOT_DC_AMT) AS TOT_DC_AMT , SUM(REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(SALE_CNT) AS SALE_CNT , SUM(RTN_SALE_CNT) AS RTN_SALE_CNT , SUM(REAL_SALE_CNT) AS REAL_SALE_CNT , SUM(CASE WHEN TIME_SLOT = ${item} THEN SALE_QTY END)AS SALE_QTY${item} , SUM(CASE WHEN TIME_SLOT = ${item} THEN REAL_SALE_AMT END) AS REAL_SALE_AMT${item}
FROM ( SELECT tms.BRANCH_CD , tsdt.STORE_CD
, SALE_DATE
, SUM(SALE_QTY) AS SALE_QTY , SUM(TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(TOT_DC_AMT) AS TOT_DC_AMT , SUM(REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(SALE_CNT) AS SALE_CNT , SUM(RTN_SALE_CNT) AS RTN_SALE_CNT , SUM(REAL_SALE_CNT) AS REAL_SALE_CNT , thn.TIME_SLOT FROM TB_SL_DAILY_TIME tsdt , ( SELECT MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) || LPAD(MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) + 1, 2, '0') AS TIME_SLOT, MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) AS START_TIME, MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) AS END_TIME FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD ='001' GROUP BY NMCODE_NM ) thn , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdt.SALE_HOUR BETWEEN thn.START_TIME AND thn.END_TIME AND tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.STORE_CD = tmsi.STORE_CD(+)
AND tsdt.STORE_CD IN
#{item}
AND TIME_SLOT = replace(#{timeSlot},'~','')
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY tms.BRANCH_CD, tsdt.STORE_CD, TIME_SLOT
, SALE_DATE
) GROUP BY BRANCH_CD
, STORE_CD , HQ_BRAND_CD , MOMS_TEAM , MOMS_AC_SHOP
, SALE_DATE
) tssh HAVING SUM(TOT_SALE_AMT) IS NOT NULL GROUP BY BRANCH_CD, STORE_CNT
, STORE_CD , HQ_BRAND_CD , MOMS_TEAM , MOMS_AC_SHOP
, SALE_DATE
ORDER BY BRANCH_CD
, STORE_CD , HQ_BRAND_CD , MOMS_TEAM , MOMS_AC_SHOP
, SALE_DATE
