mapper.select:
/* USE : StoreLangUseMapper.getStoreLangUseList */ SELECT STORE_CD , STORE_NM , BRANCH_NM , BRANCH_CD , START_DATE , END_DATE , TOT_CNT , KOREAN_CNT , ENGLISH_CNT , ENGLISH_PER , CHINESE_CNT , CHINESE_PER , JAPANESE_CNT , JAPANESE_PER , 100 - ENGLISH_PER - CHINESE_PER - JAPANESE_PER AS KOREAN_PER FROM (
SELECT '-' AS STORE_CD , '전체' AS STORE_NM
, tms.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM
, '-' AS BRANCH_CD , '-' AS BRANCH_NM
, #{startDate} AS START_DATE , #{endDate} AS END_DATE , NVL(SUM(A.TOT_CNT),0) AS TOT_CNT , SUM(A.KOREAN) AS KOREAN_CNT , SUM(A.ENGLISH) AS ENGLISH_CNT , TRUNC(DECODE(SUM(A.TOT_CNT), 0, 0, SUM(A.ENGLISH)/SUM(A.TOT_CNT))* 100, 2) AS ENGLISH_PER , SUM(A.CHINESE) AS CHINESE_CNT , TRUNC(DECODE(SUM(A.TOT_CNT), 0, 0, SUM(A.CHINESE)/SUM(A.TOT_CNT))* 100, 2) AS CHINESE_PER , SUM(A.JAPANESE) AS JAPANESE_CNT , TRUNC(DECODE(SUM(A.TOT_CNT), 0, 0, SUM(A.JAPANESE)/SUM(A.TOT_CNT))* 100, 2) AS JAPANESE_PER
SELECT A.STORE_CD , tms.STORE_NM , tms.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , #{startDate} AS START_DATE , #{endDate} AS END_DATE , A.TOT_CNT AS TOT_CNT , A.KOREAN AS KOREAN_CNT , A.ENGLISH AS ENGLISH_CNT , TRUNC(DECODE(A.TOT_CNT, 0, 0, A.ENGLISH/A.TOT_CNT)* 100, 2) AS ENGLISH_PER , A.CHINESE AS CHINESE_CNT , TRUNC(DECODE(A.TOT_CNT, 0, 0, A.CHINESE/A.TOT_CNT)* 100, 2) AS CHINESE_PER , A.JAPANESE AS JAPANESE_CNT , TRUNC(DECODE(A.TOT_CNT, 0, 0, A.JAPANESE/A.TOT_CNT)* 100, 2) AS JAPANESE_PER
FROM ( SELECT A.STORE_CD , COUNT(A.STORE_CD) AS TOT_CNT , SUM(CASE WHEN NVL(A.MULTI_LANG_FG, '1') = '1' THEN 1 ELSE 0 END) AS KOREAN , SUM(CASE WHEN NVL(A.MULTI_LANG_FG, '1') = '2' THEN 1 ELSE 0 END) AS ENGLISH , SUM(CASE WHEN NVL(A.MULTI_LANG_FG, '1') = '3' THEN 1 ELSE 0 END) AS CHINESE , SUM(CASE WHEN NVL(A.MULTI_LANG_FG, '1') = '4' THEN 1 ELSE 0 END) AS JAPANESE FROM TB_SL_SALE_HDR A , ( SELECT TMPE.STORE_CD , TMPE.POS_NO FROM TB_MS_POS_ENVST tmpe , TB_CM_ENVST_DTL tced WHERE TMPE.ENVST_CD = TCED.ENVST_CD AND TMPE.ENVST_VAL = TCED.ENVST_VAL_CD AND TCED.ENVST_CD = '4019' AND TCED.ENVST_VAL_CD = '6' ) B WHERE A.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND A.STORE_CD = B.STORE_CD AND A.POS_NO = B.POS_NO AND A.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.STORE_CD IN (${storeCdQuery})
GROUP BY A.STORE_CD ) A , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE A.STORE_CD = tms.STORE_CD AND A.STORE_CD = tmsi.STORE_CD(+)
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tmsi.MOMS_STORE_FG_02 = #{momsStoreFg02}
AND tmsi.MOMS_STORE_FG_03 = #{momsStoreFg03}
AND tmsi.MOMS_STORE_FG_04 = #{momsStoreFg04}
AND tmsi.MOMS_STORE_FG_05 = #{momsStoreFg05}
GROUP BY tms.BRANCH_CD
ORDER BY tms.BRANCH_CD NULLS FIRST
, tms.STORE_CD , tms.HQ_BRAND_CD , tmsi.MOMS_TEAM , tmsi.MOMS_AC_SHOP
)
