/* StoreMonthPosMapper.getMonthList */
SELECT TO_CHAR(TO_DATE(tsdt.SALE_YM, 'yyyymm'), 'yyyy-mm') YEAR_MONTH, tsdt.SALE_CNT, tsdt.STORE_CNT, tsdt.BRANCH_CD, (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tsdt.BRANCH_CD) AS BRANCH_NM,
tsdt.STORE_CD, (SELECT STORE_NM FROM TB_MS_STORE tms WHERE tms.STORE_CD = tsdt.STORE_CD) AS STORE_NM, tsdt.HQ_BRAND_CD AS BRAND, tsdt.MOMS_TEAM, tsdt.MOMS_AC_SHOP,
tsdt.TOT_SALE_AMT, tsdt.TOT_DC_AMT, tsdt.REAL_SALE_AMT, tsdt.BILL_CNT, tsdt.BILL_UPRC, tsdt.GA_AMT, tsdt.VAT_AMT, tsdt.TOT_TIP_AMT, tsdt.TOT_ETC_AMT, tsdt.CUP_AMT,
tsdt.PAY_AMT_${item} AS PAY${item} ,
tsdt.MPAY${item} AS MPAY${item} , tsdt.PAY_AMT_02 AS PAY02, tsdt.PAY_AMT_021 AS PAY021, tsdt.TOT_PAY_AMT, tsdt.STIN_BILL_CNT, DECODE(tsdt.STIN_BILL_CNT , 0, 0, ROUND(tsdt.STIN_REAL_SALE_AMT / tsdt.STIN_BILL_CNT)) AS STIN_BILL_UPRC, tsdt.STIN_REAL_SALE_AMT, DECODE(tsdt.REAL_SALE_AMT, 0, 0, (tsdt.STIN_REAL_SALE_AMT / tsdt.REAL_SALE_AMT)*100) AS STIN_RATE, tsdt.DLVR_BILL_CNT, DECODE(tsdt.DLVR_BILL_CNT , 0, 0, ROUND(tsdt.DLVR_REAL_SALE_AMT / tsdt.DLVR_BILL_CNT)) AS DLVR_BILL_UPRC, tsdt.DLVR_REAL_SALE_AMT, DECODE(tsdt.REAL_SALE_AMT, 0, 0, (tsdt.DLVR_REAL_SALE_AMT / tsdt.REAL_SALE_AMT)*100) AS DLVR_RATE, tsdt.PACK_BILL_CNT, DECODE(tsdt.PACK_BILL_CNT , 0, 0, ROUND(tsdt.PACK_REAL_SALE_AMT / tsdt.PACK_BILL_CNT)) AS PACK_BILL_UPRC, tsdt.PACK_REAL_SALE_AMT, DECODE(tsdt.REAL_SALE_AMT, 0, 0, (tsdt.PACK_REAL_SALE_AMT / tsdt.REAL_SALE_AMT)*100) AS PACK_RATE , tsdto.SALE_QTY${item} AS SALE_QTY${item} , tsdto.REAL_SALE_AMT${item} AS REAL_SALE_AMT${item}
, GEN_REAL_SALE_CNT_POS , GEN_REAL_SALE_AMT_POS , DLVR_REAL_SALE_CNT_POS , DLVR_REAL_SALE_AMT_POS , PACK_REAL_SALE_CNT_POS , PACK_REAL_SALE_AMT_POS , GEN_REAL_SALE_CNT_CHANNEL , GEN_REAL_SALE_AMT_CHANNEL , DLVR_REAL_SALE_CNT_CHANNEL , DLVR_REAL_SALE_AMT_CHANNEL , PACK_REAL_SALE_CNT_CHANNEL , PACK_REAL_SALE_AMT_CHANNEL , GEN_REAL_SALE_CNT_KIOSK , GEN_REAL_SALE_AMT_KIOSK , DLVR_REAL_SALE_CNT_KIOSK , DLVR_REAL_SALE_AMT_KIOSK , PACK_REAL_SALE_CNT_KIOSK , PACK_REAL_SALE_AMT_KIOSK
FROM ( SELECT SUBSTR(tsdt.SALE_DATE,0,6) AS SALE_YM, COUNT(DISTINCT tsdt.SALE_DATE) AS SALE_CNT, COUNT(DISTINCT tsdt.STORE_CD) AS STORE_CNT, NVL(tms.BRANCH_CD, ' ') AS BRANCH_CD,
tsdt.STORE_CD, tms.HQ_BRAND_CD, tmsi.MOMS_TEAM, tmsi.MOMS_AC_SHOP,
SUM(tsdt.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdt.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdt.REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(tsdt.REAL_SALE_CNT) AS BILL_CNT, DECODE(SUM(REAL_SALE_CNT), 0, 0, ROUND(SUM(tsdt.REAL_SALE_AMT) / SUM(tsdt.BILL_CNT))) AS BILL_UPRC, SUM(tsdt.REAL_SALE_AMT) - SUM(tsdt.VAT_AMT) AS GA_AMT, SUM(tsdt.VAT_AMT) AS VAT_AMT, SUM(tsdt.TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(tsdt.TOT_ETC_AMT) AS TOT_ETC_AMT, SUM(tsdt.CUP_AMT) AS CUP_AMT,
SUM(NVL(tsdt.PAY_AMT_${item}, 0)) + SUM(NVL(tsdt.NON_CASH_AMT, 0)) + SUM(NVL(tsdt.APPR_CASH_AMT, 0)) AS TOT_PAY_AMT,
SUM(NVL(tsdt.PAY_AMT_${item}, 0)) AS PAY_AMT_${item} ,
SUM(MPAY_AMT_${item}) AS MPAY${item} , SUM(NVL(tsdt.NON_CASH_AMT, 0)) AS PAY_AMT_02, SUM(NVL(tsdt.APPR_CASH_AMT, 0)) AS PAY_AMT_021, SUM(CASE WHEN DLVR_ORDER_FG = '1' THEN REAL_SALE_CNT ELSE 0 END) AS STIN_BILL_CNT , SUM(CASE WHEN DLVR_ORDER_FG = '1' THEN REAL_SALE_AMT ELSE 0 END) AS STIN_REAL_SALE_AMT , SUM(CASE WHEN DLVR_ORDER_FG = '2' THEN REAL_SALE_CNT ELSE 0 END) AS DLVR_BILL_CNT , SUM(CASE WHEN DLVR_ORDER_FG = '2' THEN REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT , SUM(CASE WHEN DLVR_ORDER_FG = '3' THEN REAL_SALE_CNT ELSE 0 END) AS PACK_BILL_CNT , SUM(CASE WHEN DLVR_ORDER_FG = '3' THEN REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL_DLVR tsdt , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{startMonth}||'01' AND #{endMonth}||'31'
AND tsdt.STORE_CD IN
#{item}
AND tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.STORE_CD = tmsi.STORE_CD(+)
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY SUBSTR(tsdt.SALE_DATE,0,6), tms.BRANCH_CD
, tsdt.STORE_CD , tms.HQ_BRAND_CD , tmsi.MOMS_TEAM , tmsi.MOMS_AC_SHOP
) tsdt , ( SELECT tsdto.SALE_YM , NVL(tms.BRANCH_CD, ' ') AS BRANCH_CD
, tsdto.STORE_CD
, SUM(CASE WHEN DLVR_IN_FG = ${item} THEN SALE_QTY END) AS SALE_QTY${item} , SUM(CASE WHEN DLVR_IN_FG = ${item} THEN REAL_SALE_AMT END) AS REAL_SALE_AMT${item}
FROM TB_SL_MONTHLY_TOTAL_ORDAPP tsdto , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tsdto.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdto.STORE_CD IN
#{item}
AND tsdto.SALE_YM BETWEEN #{startMonth} AND #{endMonth} AND tsdto.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdto.STORE_CD = tms.STORE_CD AND tsdto.STORE_CD = tmsi.STORE_CD(+)
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY tms.BRANCH_CD, tsdto.SALE_YM
, tsdto.STORE_CD
) tsdto , ( SELECT tsmpd.SALE_YM , NVL(tms.BRANCH_CD, ' ') AS BRANCH_CD
, tsmpd.STORE_CD
, SUM(CASE WHEN DLVR_ORDER_FG = '1' AND tmpe.ENVST_VAL != '6' AND DLVR_IN_FG IN ('0', '1') THEN REAL_SALE_CNT ELSE 0 END) AS GEN_REAL_SALE_CNT_POS , SUM(CASE WHEN DLVR_ORDER_FG = '1' AND tmpe.ENVST_VAL != '6' AND DLVR_IN_FG IN ('0', '1') THEN REAL_SALE_AMT ELSE 0 END) AS GEN_REAL_SALE_AMT_POS , SUM(CASE WHEN DLVR_ORDER_FG = '2' AND tmpe.ENVST_VAL != '6' AND DLVR_IN_FG IN ('0', '1') THEN REAL_SALE_CNT ELSE 0 END) AS DLVR_REAL_SALE_CNT_POS , SUM(CASE WHEN DLVR_ORDER_FG = '2' AND tmpe.ENVST_VAL != '6' AND DLVR_IN_FG IN ('0', '1') THEN REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT_POS , SUM(CASE WHEN DLVR_ORDER_FG = '3' AND tmpe.ENVST_VAL != '6' AND DLVR_IN_FG IN ('0', '1') THEN REAL_SALE_CNT ELSE 0 END) AS PACK_REAL_SALE_CNT_POS , SUM(CASE WHEN DLVR_ORDER_FG = '3' AND tmpe.ENVST_VAL != '6' AND DLVR_IN_FG IN ('0', '1') THEN REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT_POS , SUM(CASE WHEN DLVR_ORDER_FG = '1' AND tmpe.ENVST_VAL != '6' AND DLVR_IN_FG NOT IN ('0', '1') THEN REAL_SALE_CNT ELSE 0 END) AS GEN_REAL_SALE_CNT_CHANNEL , SUM(CASE WHEN DLVR_ORDER_FG = '1' AND tmpe.ENVST_VAL != '6' AND DLVR_IN_FG NOT IN ('0', '1') THEN REAL_SALE_AMT ELSE 0 END) AS GEN_REAL_SALE_AMT_CHANNEL , SUM(CASE WHEN DLVR_ORDER_FG = '2' AND tmpe.ENVST_VAL != '6' AND DLVR_IN_FG NOT IN ('0', '1') THEN REAL_SALE_CNT ELSE 0 END) AS DLVR_REAL_SALE_CNT_CHANNEL , SUM(CASE WHEN DLVR_ORDER_FG = '2' AND tmpe.ENVST_VAL != '6' AND DLVR_IN_FG NOT IN ('0', '1') THEN REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT_CHANNEL , SUM(CASE WHEN DLVR_ORDER_FG = '3' AND tmpe.ENVST_VAL != '6' AND DLVR_IN_FG NOT IN ('0', '1') THEN REAL_SALE_CNT ELSE 0 END) AS PACK_REAL_SALE_CNT_CHANNEL , SUM(CASE WHEN DLVR_ORDER_FG = '3' AND tmpe.ENVST_VAL != '6' AND DLVR_IN_FG NOT IN ('0', '1') THEN REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT_CHANNEL , SUM(CASE WHEN DLVR_ORDER_FG = '1' AND tmpe.ENVST_VAL = '6' THEN REAL_SALE_CNT ELSE 0 END) AS GEN_REAL_SALE_CNT_KIOSK , SUM(CASE WHEN DLVR_ORDER_FG = '1' AND tmpe.ENVST_VAL = '6' THEN REAL_SALE_AMT ELSE 0 END) AS GEN_REAL_SALE_AMT_KIOSK , SUM(CASE WHEN DLVR_ORDER_FG = '2' AND tmpe.ENVST_VAL = '6' THEN REAL_SALE_CNT ELSE 0 END) AS DLVR_REAL_SALE_CNT_KIOSK , SUM(CASE WHEN DLVR_ORDER_FG = '2' AND tmpe.ENVST_VAL = '6' THEN REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT_KIOSK , SUM(CASE WHEN DLVR_ORDER_FG = '3' AND tmpe.ENVST_VAL = '6' THEN REAL_SALE_CNT ELSE 0 END) AS PACK_REAL_SALE_CNT_KIOSK , SUM(CASE WHEN DLVR_ORDER_FG = '3' AND tmpe.ENVST_VAL = '6' THEN REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT_KIOSK FROM TB_SL_MONTHLY_POS_DLVR tsmpd , TB_MS_POS_ENVST tmpe , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tsmpd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmpd.STORE_CD = tmpe.STORE_CD AND tsmpd.POS_NO = tmpe.POS_NO AND tmpe.ENVST_CD = '4019'
AND tsmpd.STORE_CD IN
#{item}
AND tsmpd.SALE_YM BETWEEN #{startMonth} AND #{endMonth} AND tsmpd.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsmpd.STORE_CD = tms.STORE_CD AND tsmpd.STORE_CD = tmsi.STORE_CD(+)
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY tms.BRANCH_CD, tsmpd.SALE_YM
, tsmpd.STORE_CD
) tsmpd WHERE tsdt.SALE_YM = tsdto.SALE_YM (+) AND tsdt.BRANCH_CD = tsdto.BRANCH_CD (+) AND tsdt.SALE_YM = tsmpd.SALE_YM (+) AND tsdt.BRANCH_CD = tsmpd.BRANCH_CD (+)
AND tsdt.STORE_CD = tsdto.STORE_CD (+) AND tsdt.STORE_CD = tsmpd.STORE_CD (+)
ORDER BY tsdt.SALE_YM, tsdt.BRANCH_CD
, tsdt.STORE_CD , tsdt.HQ_BRAND_CD , tsdt.MOMS_TEAM , tsdt.MOMS_AC_SHOP

