/* USE : StoreOpenCloseMapper.getStoreOpenCloseDayList */ SELECT A.TIME , A.MIN , A.MAX , #{saleDate} AS SALE_DATE , ( SELECT COUNT(DISTINCT STORE_CD) FROM TB_PS_EXACTCALC tpe WHERE tpe.HQ_OFFICE_CD = #{hqOfficeCd} AND tpe.SALE_DATE = #{saleDate} AND TPE.REG_SEQ = '00' AND EXTRACT(HOUR FROM TO_TIMESTAMP(tpe.OPEN_DT, 'YYYYMMDDHH24MISS')) BETWEEN A.MIN AND A.MAX) AS OPEN_CNT , ( SELECT COUNT(DISTINCT STORE_CD) FROM TB_PS_EXACTCALC tpe WHERE tpe.HQ_OFFICE_CD = #{hqOfficeCd} AND tpe.SALE_DATE = #{saleDate} AND TPE.REG_SEQ = '00' AND EXTRACT(HOUR FROM TO_TIMESTAMP(tpe.CLOSE_DT, 'YYYYMMDDHH24MISS')) BETWEEN A.MIN AND A.MAX) AS CLOSE_CNT FROM (
SELECT thn.NMCODE_NM || '(' || MIN(thn.NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY thn.NMCODE_ITEM_1) || '~' ||LPAD(MAX(thn.NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY thn.NMCODE_ITEM_1) + 1, 2, '0') || ')' AS TIME , MIN(thn.NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY thn.NMCODE_ITEM_1) AS MIN , LPAD(MAX(thn.NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY thn.NMCODE_ITEM_1), 2, '0') AS MAX FROM TB_HQ_NMCODE thn WHERE thn.HQ_OFFICE_CD = #{hqOfficeCd} AND thn.NMCODE_GRP_CD = '001' GROUP BY NMCODE_NM
SELECT LPAD(LEVEL-1, 2, '0') AS TIME , LPAD(LEVEL-1, 2, '0') AS MIN , LPAD(LEVEL-1, 2, '0') AS MAX FROM DUAL CONNECT BY LEVEL <= 24
) A ORDER BY A.MIN

/* USE : StoreOpenCloseMapper.getStoreOpenCloseDayDtlList */ SELECT TPE.HQ_OFFICE_CD , TPE.STORE_CD , (SELECT STORE_NM FROM TB_MS_STORE WHERE STORE_CD = TPE.STORE_CD) AS STORE_NM , TO_CHAR(TO_DATE(TPE.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , TO_CHAR(TO_DATE(TPE.OPEN_DT, 'YYYYMMDDHH24MISS'), 'HH24:MI:SS') AS OPEN_TIME , TO_CHAR(TO_DATE(TPE.CLOSE_DT, 'YYYYMMDDHH24MISS'), 'HH24:MI:SS') AS CLOSE_TIME , TPE.RUN_TIME , TPE.POS_NO , TPE.REG_SEQ , TPE.CLOSE_FG_SEQ , TPE.CLOSE_FG_NM , TO_CHAR(TO_DATE(TPE.REG_DT,'YYYYMMDDHH24MISS'),'YYYY.MM.DD HH24:MI:SS') AS REG_DT ---------------------------- , NVL((SELECT SUM(NVL(TPES.TOT_SALE_AMT,0)) FROM TB_PS_EXACTCALC_SALE TPES WHERE TPE.HQ_OFFICE_CD = TPES.HQ_OFFICE_CD AND TPE.HQ_BRAND_CD = TPES.HQ_BRAND_CD AND TPE.STORE_CD = TPES.STORE_CD AND TPE.SALE_DATE = TPES.SALE_DATE AND TPE.POS_NO = TPES.POS_NO AND TPE.REG_SEQ = TPES.REG_SEQ ),0) AS TOT_SALE_AMT , NVL((SELECT SUM(NVL(TPES.TOT_DC_AMT,0)) FROM TB_PS_EXACTCALC_SALE TPES WHERE TPE.HQ_OFFICE_CD = TPES.HQ_OFFICE_CD AND TPE.HQ_BRAND_CD = TPES.HQ_BRAND_CD AND TPE.STORE_CD = TPES.STORE_CD AND TPE.SALE_DATE = TPES.SALE_DATE AND TPE.POS_NO = TPES.POS_NO AND TPE.REG_SEQ = TPES.REG_SEQ ),0) AS TOT_DC_AMT , NVL((SELECT SUM(NVL(TPES.REAL_SALE_AMT,0)) FROM TB_PS_EXACTCALC_SALE TPES WHERE TPE.HQ_OFFICE_CD = TPES.HQ_OFFICE_CD AND TPE.HQ_BRAND_CD = TPES.HQ_BRAND_CD AND TPE.STORE_CD = TPES.STORE_CD AND TPE.SALE_DATE = TPES.SALE_DATE AND TPE.POS_NO = TPES.POS_NO AND TPE.REG_SEQ = TPES.REG_SEQ ),0) AS REAL_SALE_AMT ------------------------------ , NVL((SELECT SUM(TPEF.FUND_AMT) FROM TB_PS_EXACTCALC_FUND TPEF WHERE TPE.HQ_OFFICE_CD = TPEF.HQ_OFFICE_CD AND TPE.HQ_BRAND_CD = TPEF.HQ_BRAND_CD AND TPE.STORE_CD = TPEF.STORE_CD AND TPE.SALE_DATE = TPEF.SALE_DATE AND TPE.POS_NO = TPEF.POS_NO AND TPE.REG_SEQ = TPEF.REG_SEQ ),0) AS FUND_AMT ------------------------------ -- 기존 현금 금액 - 현금영수 금액 , NVL((SELECT SUM(TPEC.CASH_EXACT_AMT) FROM TB_PS_EXACTCALC_CASH TPEC --현금 WHERE TPE.HQ_OFFICE_CD = TPEC.HQ_OFFICE_CD AND TPE.HQ_BRAND_CD = TPEC.HQ_BRAND_CD AND TPE.STORE_CD = TPEC.STORE_CD AND TPE.SALE_DATE = TPEC.SALE_DATE AND TPE.POS_NO = TPEC.POS_NO AND TPE.REG_SEQ = TPEC.REG_SEQ AND TPEC.CASH_EXACT_CD = '02' ) - (SELECT SUM(CASE WHEN TSSPC.APPR_PROC_FG IN ('1','2','4') THEN TSSPC.SALE_AMT ELSE 0 END) FROM TB_SL_SALE_PAY_CASH TSSPC WHERE TPE.HQ_OFFICE_CD = TSSPC.HQ_OFFICE_CD AND TPE.HQ_BRAND_CD = TSSPC.HQ_BRAND_CD AND TPE.STORE_CD = TSSPC.STORE_CD AND TPE.SALE_DATE = TSSPC.SALE_DATE -- AND TPE.POS_NO = TSSPC.POS_NO ), 0) AS CASH_EXACT_AMT ------------------------------ -- 기존 현금 금액이 없으면 현금영수 금액도 0원으로 출력 , CASE WHEN NVL((SELECT SUM(TPEC.CASH_EXACT_AMT) FROM TB_PS_EXACTCALC_CASH TPEC --현금 WHERE TPE.HQ_OFFICE_CD = TPEC.HQ_OFFICE_CD AND TPE.HQ_BRAND_CD = TPEC.HQ_BRAND_CD AND TPE.STORE_CD = TPEC.STORE_CD AND TPE.SALE_DATE = TPEC.SALE_DATE AND TPE.POS_NO = TPEC.POS_NO AND TPE.REG_SEQ = TPEC.REG_SEQ AND TPEC.CASH_EXACT_CD = '02' ), 0) = 0 THEN 0 ELSE (NVL((SELECT SUM(CASE WHEN TSSPC.APPR_PROC_FG IN ('1','2','4') THEN TSSPC.SALE_AMT ELSE 0 END) FROM TB_SL_SALE_PAY_CASH TSSPC WHERE TPE.HQ_OFFICE_CD = TSSPC.HQ_OFFICE_CD AND TPE.HQ_BRAND_CD = TSSPC.HQ_BRAND_CD AND TPE.STORE_CD = TSSPC.STORE_CD AND TPE.SALE_DATE = TSSPC.SALE_DATE -- AND TPE.POS_NO = TSSPC.POS_NO ),0)) END AS CASH_BILL_SALE_AMT ------------------------------ , NVL((SELECT SUM(DECODE(TPEI.ACCNT_FG,'1',TPEI.ACCNT_AMT, 0)) FROM TB_PS_EXACTCALC_IOMONEY TPEI --시재 WHERE TPE.HQ_OFFICE_CD = TPEI.HQ_OFFICE_CD AND TPE.HQ_BRAND_CD = TPEI.HQ_BRAND_CD AND TPE.STORE_CD = TPEI.STORE_CD AND TPE.SALE_DATE = TPEI.SALE_DATE AND TPE.POS_NO = TPEI.POS_NO ),0) AS ACCNT_IN_AMT , NVL((SELECT SUM(DECODE(TPEI.ACCNT_FG,'2',TPEI.ACCNT_AMT, 0)) FROM TB_PS_EXACTCALC_IOMONEY TPEI --시재 WHERE TPE.HQ_OFFICE_CD = TPEI.HQ_OFFICE_CD AND TPE.HQ_BRAND_CD = TPEI.HQ_BRAND_CD AND TPE.STORE_CD = TPEI.STORE_CD AND TPE.SALE_DATE = TPEI.SALE_DATE AND TPE.POS_NO = TPEI.POS_NO AND TPE.REG_SEQ = TPEI.REG_SEQ ),0) AS ACCNT_OUT_AMT ------------------------------ , NVL((SELECT SUM(TPECT.CASH_TICKET_AMT) FROM TB_PS_EXACTCALC_CASH_TICKET TPECT WHERE TPE.HQ_OFFICE_CD = TPECT.HQ_OFFICE_CD AND TPE.HQ_BRAND_CD = TPECT.HQ_BRAND_CD AND TPE.STORE_CD = TPECT.STORE_CD AND TPE.SALE_DATE = TPECT.SALE_DATE AND TPE.POS_NO = TPECT.POS_NO AND TPE.REG_SEQ = TPECT.REG_SEQ ),0) AS CASH_TICKET_AMT ------------------------------ , NVL((SELECT SUM(TPEL.LOST_AMT) FROM TB_PS_EXACTCALC_LOST TPEL --과부족 WHERE TPE.HQ_OFFICE_CD = TPEL.HQ_OFFICE_CD AND TPE.HQ_BRAND_CD = TPEL.HQ_BRAND_CD AND TPE.STORE_CD = TPEL.STORE_CD AND TPE.SALE_DATE = TPEL.SALE_DATE AND TPE.POS_NO = TPEL.POS_NO AND TPE.REG_SEQ = TPEL.REG_SEQ ),0) AS LOST_AMT ---------------------------- FROM ( SELECT TPE.CLOSE_FG_SEQ , DECODE(TPE.CLOSE_FG_SEQ, '1', '개점', '2', '중간마감', '일마감') AS CLOSE_FG_NM , TPE.HQ_OFFICE_CD , TPE.HQ_BRAND_CD , TPE.STORE_CD , TPE.SALE_DATE , TPE.OPEN_DT , TPE.CLOSE_DT , TRUNC((TRUNC((TO_DATE(TPE.CLOSE_DT, 'YYYYMMDD HH24MISS') - TO_DATE(TPE.OPEN_DT, 'YYYYMMDD HH24MISS')) * 24 * 60, 2))/ 60) || '시간' || TRUNC(MOD(TRUNC((TO_DATE(TPE.CLOSE_DT, 'YYYYMMDD HH24MISS') - TO_DATE(TPE.OPEN_DT, 'YYYYMMDD HH24MISS')) * 24 * 60, 2), 60)) || '분' AS RUN_TIME , TPE.POS_NO , TPE.REG_SEQ , TPE.REG_DT FROM ( SELECT '1' AS CLOSE_FG_SEQ , '1' AS CLOSE_POS_SEQ , TPE.HQ_OFFICE_CD , TPE.HQ_BRAND_CD , TPE.STORE_CD , TPE.SALE_DATE , TPE.OPEN_DT , TPE.CLOSE_DT , TPE.POS_NO , TPE.REG_SEQ , TPE.OPEN_DT AS REG_DT FROM TB_PS_EXACTCALC TPE WHERE TPE.HQ_OFFICE_CD = #{hqOfficeCd} AND TPE.SALE_DATE = #{saleDate}
AND EXTRACT(HOUR FROM TO_TIMESTAMP(tpe.OPEN_DT, 'YYYYMMDDHH24MISS')) BETWEEN #{min} AND #{max}
AND 1 = 2
AND TPE.REG_SEQ = '00' UNION ALL SELECT '3' AS CLOSE_FG_SEQ , '3' AS CLOSE_POS_SEQ , TPE.HQ_OFFICE_CD , TPE.HQ_BRAND_CD , TPE.STORE_CD , TPE.SALE_DATE , TPE.OPEN_DT , TPE.CLOSE_DT , TPE.POS_NO , TPE.REG_SEQ , TPE.CLOSE_DT AS REG_DT FROM TB_PS_EXACTCALC TPE WHERE TPE.HQ_OFFICE_CD = #{hqOfficeCd} AND TPE.SALE_DATE = #{saleDate}
AND EXTRACT(HOUR FROM TO_TIMESTAMP(tpe.CLOSE_DT, 'YYYYMMDDHH24MISS')) BETWEEN #{min} AND #{max}
AND 1 = 2
AND TPE.REG_SEQ = '00' ) TPE ) TPE GROUP BY TPE.HQ_OFFICE_CD , TPE.HQ_BRAND_CD , TPE.STORE_CD , TPE.SALE_DATE , TPE.OPEN_DT , TPE.CLOSE_DT , TPE.RUN_TIME , TPE.POS_NO , TPE.REG_SEQ , TPE.CLOSE_FG_SEQ , TPE.CLOSE_FG_NM , TPE.REG_DT ORDER BY TPE.STORE_CD, TPE.SALE_DATE, TPE.CLOSE_FG_SEQ
