mapper.select:
/* TimeProdMapper.getTimeProdList */
SELECT STORE_CD , STORE_NM , PROD_CD , PROD_NM , TOT_SALE_CNT , TOT_SALE_AMT ${sQuery1}
FROM ( SELECT tsdpt.STORE_CD , tms.STORE_NM , tsdpt.PROD_CD , thp.PROD_NM , SUM(tsdpt.SALE_CNT) AS TOT_SALE_CNT , SUM(tsdpt.TOT_SALE_AMT) AS TOT_SALE_AMT ${sQuery2} FROM TB_SL_DAILY_PROD_TIME tsdpt , TB_MS_STORE tms , TB_HQ_PRODUCT thp WHERE tsdpt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdpt.STORE_CD = tms.STORE_CD AND thp.HQ_OFFICE_CD = tsdpt.HQ_OFFICE_CD AND thp.PROD_CD = tsdpt.PROD_CD AND tsdpt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdpt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdpt.STORE_CD IN
#{item}
AND thp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
GROUP BY tsdpt.STORE_CD, tms.STORE_NM, tsdpt.PROD_CD,thp.PROD_NM ) ORDER BY STORE_CD, PROD_CD

/* TimeProdMapper.getTimeProdExcelList */ SELECT STORE_CD , STORE_NM , PROD_CD , PROD_NM , TOT_SALE_CNT , TOT_SALE_AMT ${sQuery1} FROM ( SELECT tsdpt.STORE_CD , tms.STORE_NM , tsdpt.PROD_CD , thp.PROD_NM , SUM(tsdpt.SALE_CNT) AS TOT_SALE_CNT , SUM(tsdpt.TOT_SALE_AMT) AS TOT_SALE_AMT ${sQuery2} FROM TB_SL_DAILY_PROD_TIME tsdpt , TB_MS_STORE tms , TB_HQ_PRODUCT thp WHERE tsdpt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdpt.STORE_CD = tms.STORE_CD AND thp.HQ_OFFICE_CD = tsdpt.HQ_OFFICE_CD AND thp.PROD_CD = tsdpt.PROD_CD AND tsdpt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdpt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdpt.STORE_CD IN
#{item}
AND thp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
GROUP BY tsdpt.STORE_CD, tms.STORE_NM, tsdpt.PROD_CD, thp.PROD_NM ) ORDER BY STORE_CD, PROD_CD
