, BRANCH_NM , STORE_CD , STORE_NM , L_CLASS_CD , L_CLASS_NM , M_CLASS_CD , M_CLASS_NM , S_CLASS_CD , S_CLASS_NM , PROD_CD , PROD_NM , SEL_TYPE_FG , SIDE_PROD_CD , SIDE_PROD_NM , SALE_QTY1 , SALE_QTY2 , SALE_QTY3 , TOT_SALE_AMT , DC_AMT , REAL_SALE_AMT1 , REAL_SALE_AMT2 , REAL_SALE_AMT3 , TIME_SLOT AS SALE_HOUR ${sQuery1} FROM ( SELECT
TO_CHAR(TO_DATE(tsdpsdt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , TO_CHAR(TO_DATE(tsdpsdt.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL
TO_CHAR(TO_DATE(#{startDate}, 'yyyymmdd'), 'yyyy-mm-dd') AS DAY_FROM , TO_CHAR(TO_DATE(#{endDate}, 'yyyymmdd'), 'yyyy-mm-dd') AS DAY_TO
, (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsdpsdt.STORE_CD , tms.STORE_NM , L_CLASS_CD , L_CLASS_NM , M_CLASS_CD , M_CLASS_NM , S_CLASS_CD , S_CLASS_NM , tsdpsdt.PROD_CD , (CASE tsdpsdt.SEL_TYPE_FG WHEN 'S' THEN 'ㅤ- ▶ ' || tmps2.PROD_NM ELSE tmps2.PROD_NM END) AS PROD_NM , (CASE tsdpsdt.SEL_TYPE_FG WHEN 'P' THEN '사이드모상품' WHEN 'S' THEN '사이드상품' WHEN 'N' THEN '일반판매상품' END) AS SEL_TYPE_FG , (CASE tsdpsdt.SEL_TYPE_FG WHEN 'S' THEN tsdpsdt.SIDE_PROD_CD END) AS SIDE_PROD_CD , (CASE tsdpsdt.SEL_TYPE_FG WHEN 'S' THEN 'ㅤ- ▶ ' || tmps2.PROD_NM ELSE tmps2.PROD_NM END) AS SIDE_PROD_NM , SUM(CASE WHEN tsdpsdt.SEL_TYPE_FG = 'N' OR tsdpsdt.SEL_TYPE_FG = 'P' THEN tsdpsdt.TOT_SALE_QTY END) AS SALE_QTY1 , SUM(CASE WHEN tsdpsdt.SEL_TYPE_FG = 'N' OR tsdpsdt.SEL_TYPE_FG = 'S' THEN tsdpsdt.TOT_SALE_QTY END) AS SALE_QTY2 , SUM(tsdpsdt.TOT_SALE_QTY) AS SALE_QTY3 , SUM(tsdpsdt.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(tsdpsdt.TOT_DC_AMT) AS DC_AMT , SUM(CASE WHEN tsdpsdt.SEL_TYPE_FG = 'N' OR tsdpsdt.SEL_TYPE_FG = 'P' THEN tsdpsdt.REAL_SALE_AMT END) AS REAL_SALE_AMT1 , SUM(CASE WHEN tsdpsdt.SEL_TYPE_FG = 'N' OR tsdpsdt.SEL_TYPE_FG = 'S' THEN tsdpsdt.REAL_SALE_AMT END) AS REAL_SALE_AMT2 , SUM(tsdpsdt.REAL_SALE_AMT) AS REAL_SALE_AMT3 , thn.TIME_SLOT ${sQuery2} FROM TB_SL_DAILY_PROD_SDSEL_DLVR_TIME tsdpsdt , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi , TB_MS_PRODUCT tmp , TB_MS_PRODUCT tmps2 -- 사이드 , TB_MS_PRODUCT_CLASS_V02 tmpcv , (SELECT MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) || LPAD(MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) + 1, 2, '0') AS TIME_SLOT, MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) AS START_TIME, MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) AS END_TIME FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD ='001' GROUP BY NMCODE_NM ) thn WHERE 1=1 AND tsdpsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdpsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdpsdt.SALE_HOUR BETWEEN thn.START_TIME AND thn.END_TIME
AND thn.TIME_SLOT = replace(#{timeSlot},'~','')
AND tmps2.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = tmps2.STORE_CD START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = tmps2.STORE_CD CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = tmps2.STORE_CD )
AND tmps2.PROD_CD LIKE '%'||#{prodCd}||'%'
AND tmps2.PROD_NM LIKE '%'||#{prodNm}||'%'
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tsdpsdt.STORE_CD IN
#{item}
AND tmps2.HQ_BRAND_CD = #{prodHqBrandCd} -- 상품브랜드 선택일때
-- 상품브랜드 전체일때 AND tmps2.HQ_BRAND_CD IN
#{item}
AND tmps2.PROD_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tsdpsdt.SEL_TYPE_FG IN ('N', 'P')
AND tsdpsdt.SEL_TYPE_FG IN ('N', 'S')
AND tsdpsdt.STORE_CD = tms.STORE_CD AND tmsi.STORE_CD (+)= tms.STORE_CD AND tmp.STORE_CD = tsdpsdt.STORE_CD AND tmp.PROD_CD = tsdpsdt.PROD_CD AND tmps2.STORE_CD = tsdpsdt.STORE_CD AND tmps2.PROD_CD = tsdpsdt.PROD_CD AND tmpcv.STORE_CD = tmps2.STORE_CD AND tmpcv.PROD_CD = tmps2.PROD_CD
GROUP BY tsdpsdt.SALE_DATE, tms.HQ_OFFICE_CD, tms.BRANCH_CD, tsdpsdt.STORE_CD, tms.STORE_NM, L_CLASS_CD, L_CLASS_NM, M_CLASS_CD, M_CLASS_NM, S_CLASS_CD, S_CLASS_NM, tsdpsdt.PROD_CD, tmp.PROD_NM, tsdpsdt.SEL_TYPE_FG, tsdpsdt.SIDE_PROD_CD, tmps2.PROD_NM, thn.TIME_SLOT
GROUP BY tms.HQ_OFFICE_CD, tms.BRANCH_CD, tsdpsdt.STORE_CD, tms.STORE_NM, L_CLASS_CD, L_CLASS_NM, M_CLASS_CD, M_CLASS_NM, S_CLASS_CD, S_CLASS_NM, tsdpsdt.PROD_CD, tmp.PROD_NM, tsdpsdt.SEL_TYPE_FG, tsdpsdt.SIDE_PROD_CD, tmps2.PROD_NM, thn.TIME_SLOT
) A
ORDER BY A.SALE_DATE, A.STORE_CD, A.TIME_SLOT ASC
ORDER BY A.STORE_CD, A.TIME_SLOT ASC
, A.SALE_QTY1 DESC, A.PROD_CD ASC
, A.SALE_QTY2 DESC, A.PROD_CD ASC
, A.SALE_QTY3 DESC, A.PROD_CD ASC
, A.SALE_QTY3 DESC, A.PROD_CD ASC
