mapper.select:
/* TodayMomsMapper.getTodayMomsList */
SELECT (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tssh.STORE_CD , tms.STORE_NM , tssh.POS_NO , tssh.BILL_NO , TO_CHAR(TO_DATE(tssh.BILL_DT,'yyyy/mm/dd hh24:mi:ss'),'hh24:mi:ss') AS BILL_DT , tssh.SALE_YN , tssh.TOT_SALE_AMT , tssh.TOT_DC_AMT , tssh.REAL_SALE_AMT , (tssh.REAL_SALE_AMT - tssh.VAT_AMT) AS GA_AMT , tssh.VAT_AMT , tssh.TOT_TIP_AMT , tssh.TOT_ETC_AMT , tssh.CUP_AMT , tssh.TOT_GUEST_CNT , DECODE(NVL(tssh.TOT_GUEST_CNT,0), 0, 0, DECODE(tssh.SALE_YN, 'Y', ROUND(tssh.REAL_SALE_AMT / tssh.TOT_GUEST_CNT), -(ROUND(tssh.REAL_SALE_AMT / tssh.TOT_GUEST_CNT)))) AS GUEST_UPRC , tssh.REG_DT AS FIRST_ORDER_DT , tssh.TOT_OFFADD_AMT , NVL(tsshp.PAY${item}, 0) + NVL(tsspc.PAY02, 0) + NVL(tsspc.PAY021, 0) AS TOT_PAY_AMT , tsshp.PAY${item} AS PAY${item}
, tsshd.DC${item} AS DC${item}
, tsspc.PAY02 AS PAY02 -- 현금 , tsspc.PAY021 AS PAY021 -- 현금영수증
FROM TB_SL_SALE_HDR tssh , ( SELECT STORE_CD, POS_NO, BILL_NO , PAY${item} AS PAY${item}
FROM ( SELECT tsshp.STORE_CD, tsshp.POS_NO, tsshp.BILL_NO, tsshp.PAY_CD, tsshp.PAY_AMT FROM TB_SL_SALE_HDR_PAY tsshp WHERE tsshp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsshp.STORE_CD IN
#{item}
AND tsshp.SALE_DATE = #{startDate} AND tsshp.PAY_CD != '02' -- 현금,현금영수증 분리 ) PIVOT ( SUM(PAY_AMT) FOR PAY_CD IN (${pivotPayCol}) ) ) tsshp , ( SELECT STORE_CD, POS_NO, BILL_NO , DC${item} AS DC${item}
FROM ( SELECT tsshd.STORE_CD, tsshd.POS_NO, tsshd.BILL_NO, tsshd.DC_CD, tsshd.DC_AMT FROM TB_SL_SALE_HDR_DC tsshd WHERE tsshd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsshd.STORE_CD IN
#{item}
AND tsshd.SALE_DATE = #{startDate} ) PIVOT ( SUM(DC_AMT) FOR DC_CD IN (${pivotDcCol}) ) ) tsshd , ( -- 현금,현금영수증 분리 SELECT tsspc.STORE_CD, tsspc.POS_NO, tsspc.BILL_NO, SUM(tsspc.SALE_AMT) - SUM(CASE WHEN tsspc.APPR_PROC_FG IN ('1','2','4') THEN tsspc.SALE_AMT ELSE 0 END) AS PAY02, -- 현금 SUM(CASE WHEN tsspc.APPR_PROC_FG IN ('1','2','4') THEN tsspc.SALE_AMT ELSE 0 END) AS PAY021 -- 현금영수증 FROM TB_SL_SALE_PAY_CASH tsspc WHERE tsspc.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsspc.STORE_CD IN
#{item}
AND tsspc.SALE_DATE = #{startDate} GROUP BY tsspc.STORE_CD, tsspc.POS_NO, tsspc.BILL_NO ) tsspc , TB_MS_TABLE_V01 tmt , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tssh.HQ_OFFICE_CD = #{hqOfficeCd}
AND tssh.STORE_CD IN
#{item}
AND tssh.SALE_DATE = #{startDate} AND tms.HQ_OFFICE_CD = tssh.HQ_OFFICE_CD AND tms.STORE_CD = tssh.STORE_CD AND tmsi.STORE_CD (+) = tssh.STORE_CD
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tsshp.POS_NO (+) = tssh.POS_NO AND tsshp.BILL_NO (+) = tssh.BILL_NO AND tsshd.POS_NO (+) = tssh.POS_NO AND tsshd.BILL_NO (+) = tssh.BILL_NO AND tmt.STORE_CD (+) = tssh.STORE_CD AND tmt.TBL_CD (+) = tssh.TBL_CD AND tsspc.POS_NO (+) = tssh.POS_NO AND tsspc.BILL_NO (+) = tssh.BILL_NO ORDER BY tssh.STORE_CD, tssh.POS_NO, tssh.BILL_NO

/* TodayMomsMapper.getTodayMomsExcelList */ SELECT (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tssh.STORE_CD , tms.STORE_NM , tssh.POS_NO , tssh.BILL_NO , TO_CHAR(TO_DATE(tssh.BILL_DT,'yyyy/mm/dd hh24:mi:ss'),'hh24:mi:ss') AS BILL_DT , tssh.SALE_YN , tssh.TOT_SALE_AMT , tssh.TOT_DC_AMT , tssh.REAL_SALE_AMT , (tssh.REAL_SALE_AMT - tssh.VAT_AMT) AS GA_AMT , tssh.VAT_AMT , tssh.TOT_TIP_AMT , tssh.TOT_ETC_AMT , tssh.CUP_AMT , tssh.TOT_GUEST_CNT , DECODE(NVL(tssh.TOT_GUEST_CNT,0), 0, 0, DECODE(tssh.SALE_YN, 'Y', ROUND(tssh.REAL_SALE_AMT / tssh.TOT_GUEST_CNT), -(ROUND(tssh.REAL_SALE_AMT / tssh.TOT_GUEST_CNT)))) AS GUEST_UPRC , tssh.REG_DT AS FIRST_ORDER_DT , tssh.TOT_OFFADD_AMT , NVL(tsshp.PAY${item}, 0) + NVL(tsspc.PAY02, 0) + NVL(tsspc.PAY021, 0) AS TOT_PAY_AMT , tsshp.PAY${item} AS PAY${item}
, tsshd.DC${item} AS DC${item}
, tsspc.PAY02 AS PAY02 -- 현금 , tsspc.PAY021 AS PAY021 -- 현금영수증 FROM TB_SL_SALE_HDR tssh , ( SELECT STORE_CD, POS_NO, BILL_NO , PAY${item} AS PAY${item}
FROM ( SELECT tsshp.STORE_CD, tsshp.POS_NO, tsshp.BILL_NO, tsshp.PAY_CD, tsshp.PAY_AMT FROM TB_SL_SALE_HDR_PAY tsshp WHERE tsshp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsshp.STORE_CD IN
#{item}
AND tsshp.SALE_DATE = #{startDate} AND tsshp.PAY_CD != '02' -- 현금,현금영수증 분리 ) PIVOT ( SUM(PAY_AMT) FOR PAY_CD IN (${pivotPayCol}) ) ) tsshp , ( SELECT STORE_CD, POS_NO, BILL_NO , DC${item} AS DC${item}
FROM ( SELECT tsshd.STORE_CD, tsshd.POS_NO, tsshd.BILL_NO, tsshd.DC_CD, tsshd.DC_AMT FROM TB_SL_SALE_HDR_DC tsshd WHERE tsshd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsshd.STORE_CD IN
#{item}
AND tsshd.SALE_DATE = #{startDate} ) PIVOT ( SUM(DC_AMT) FOR DC_CD IN (${pivotDcCol}) ) ) tsshd , ( -- 현금,현금영수증 분리 SELECT tsspc.STORE_CD, tsspc.POS_NO, tsspc.BILL_NO, SUM(tsspc.SALE_AMT) - SUM(CASE WHEN tsspc.APPR_PROC_FG IN ('1','2','4') THEN tsspc.SALE_AMT ELSE 0 END) AS PAY02, -- 현금 SUM(CASE WHEN tsspc.APPR_PROC_FG IN ('1','2','4') THEN tsspc.SALE_AMT ELSE 0 END) AS PAY021 -- 현금영수증 FROM TB_SL_SALE_PAY_CASH tsspc WHERE tsspc.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsspc.STORE_CD IN
#{item}
AND tsspc.SALE_DATE = #{startDate} GROUP BY tsspc.STORE_CD, tsspc.POS_NO, tsspc.BILL_NO ) tsspc , TB_MS_TABLE_V01 tmt , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tssh.HQ_OFFICE_CD = #{hqOfficeCd}
AND tssh.STORE_CD IN
#{item}
AND tssh.SALE_DATE = #{startDate} AND tms.HQ_OFFICE_CD = tssh.HQ_OFFICE_CD AND tms.STORE_CD = tssh.STORE_CD AND tmsi.STORE_CD (+) = tssh.STORE_CD
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tsshp.POS_NO (+) = tssh.POS_NO AND tsshp.BILL_NO (+) = tssh.BILL_NO AND tsshd.POS_NO (+) = tssh.POS_NO AND tsshd.BILL_NO (+) = tssh.BILL_NO AND tmt.STORE_CD (+) = tssh.STORE_CD AND tmt.TBL_CD (+) = tssh.TBL_CD AND tsspc.POS_NO (+) = tssh.POS_NO AND tsspc.BILL_NO (+) = tssh.BILL_NO ORDER BY tssh.STORE_CD, tssh.POS_NO, tssh.BILL_NO
