mapper.insert:
/* USE : AcinsMapper.insertAcinsToExcelUploadData */
INSERT INTO TB_ST_HQ_ACTUAL_INSPECTION_DTL ( HQ_OFFICE_CD , ACINS_DATE , SEQ_NO , HQ_BRAND_CD , PROD_CD , COST_UPRC , ADJ_AMT , CMPT_CURR_QTY , ACINS_QTY , ADJ_QTY , ACINS_AMT , PO_UNIT_QTY , REMARK , REG_DT , REG_ID , MOD_DT , MOD_ID ) SELECT #{hqOfficeCd} , #{date} , #{seqNo} , '00' , tpte.PROD_CD , NVL(tshsc.AVG_COST_UPRC, tpte.COST_UPRC) AS COST_UPRC , NVL(tshsc.AVG_COST_UPRC, tpte.COST_UPRC) * (tpte.QTY - NVL(tshsc.CURR_QTY,0)) AS ADJ_AMT , NVL(tshsc.CURR_QTY,0) AS CMPT_CURR_QTY , tpte.QTY AS ACINS_QTY , tpte.QTY - NVL(tshsc.CURR_QTY,0) AS ADJ_QTY , NVL(tshsc.AVG_COST_UPRC, tpte.COST_UPRC) * tpte.QTY AS ACINS_AMT , tpte.PO_UNIT_QTY , tpte.REMARK , #{regDt} , #{regId} , #{modDt} , #{modId} FROM ( SELECT tpte.HQ_OFFICE_CD , tpte.PROD_CD , thp.PO_UNIT_QTY , NVL(MAX(thp.COST_UPRC), 0) AS COST_UPRC , SUM(tpte.QTY) AS QTY , MAX(tpte.REMARK) AS REMARK FROM TB_PO_TEMP_EXCEL tpte , TB_HQ_PRODUCT thp WHERE tpte.SESSION_ID = #{sessionId} AND tpte.PROD_CD IS NOT NULL AND thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.PROD_CD = tpte.PROD_CD AND thp.STOCK_PROD_YN = 'Y' AND thp.USE_YN = 'Y' GROUP BY tpte.HQ_OFFICE_CD , tpte.PROD_CD , thp.PO_UNIT_QTY ) tpte , ( SELECT tshsc.HQ_OFFICE_CD, tshsc.PROD_CD, tshsc.CURR_QTY, tshsm.AVG_COST_UPRC FROM TB_ST_HQ_STOCK_CUR tshsc , TB_ST_HQ_STOCK_MONTHLY tshsm WHERE tshsc.HQ_OFFICE_CD = #{hqOfficeCd} AND tshsm.IOSTOCK_YM (+) = SUBSTR(#{date}, 0, 6) AND tshsm.HQ_OFFICE_CD(+) = tshsc.HQ_OFFICE_CD AND tshsm.PROD_CD (+) = tshsc.PROD_CD ) tshsc WHERE tshsc.HQ_OFFICE_CD (+) = tpte.HQ_OFFICE_CD AND tshsc.PROD_CD (+) = tpte.PROD_CD
INSERT INTO TB_ST_STORE_ACTUAL_INSPECTION_DTL ( STORE_CD , ACINS_DATE , SEQ_NO , HQ_BRAND_CD , PROD_CD , COST_UPRC , ADJ_AMT , CMPT_CURR_QTY , ACINS_QTY , ADJ_QTY , ACINS_AMT , PO_UNIT_QTY , REMARK , REG_DT , REG_ID , MOD_DT , MOD_ID ) SELECT #{storeCd} , #{date} , #{seqNo} , '00' , tpte.PROD_CD , NVL(tsssc.AVG_COST_UPRC, tpte.COST_UPRC) AS COST_UPRC , NVL(tsssc.AVG_COST_UPRC, tpte.COST_UPRC) * (tpte.QTY - NVL(tsssc.CURR_QTY,0)) AS ADJ_AMT , NVL(tsssc.CURR_QTY,0) AS CMPT_CURR_QTY , tpte.QTY AS ACINS_QTY , tpte.QTY - NVL(tsssc.CURR_QTY,0) AS ADJ_QTY , NVL(tsssc.AVG_COST_UPRC, tpte.COST_UPRC) * tpte.QTY AS ACINS_AMT , tpte.PO_UNIT_QTY , tpte.REMARK , #{regDt} , #{regId} , #{modDt} , #{modId} FROM ( SELECT tpte.STORE_CD , tpte.PROD_CD , tmp.PO_UNIT_QTY , NVL(MAX(tmp.COST_UPRC), 0) AS COST_UPRC , SUM(tpte.QTY) AS QTY , MAX(tpte.REMARK) AS REMARK FROM TB_PO_TEMP_EXCEL tpte , TB_MS_PRODUCT tmp WHERE tpte.SESSION_ID = #{sessionId} AND tpte.PROD_CD IS NOT NULL AND tmp.STORE_CD = #{storeCd} AND tmp.PROD_CD = tpte.PROD_CD AND tmp.STOCK_PROD_YN = 'Y' AND tmp.USE_YN = 'Y' GROUP BY tpte.STORE_CD , tpte.PROD_CD , tmp.PO_UNIT_QTY ) tpte , ( SELECT tsssc.STORE_CD, tsssc.PROD_CD, tsssc.CURR_QTY, tsssm.AVG_COST_UPRC FROM TB_ST_STORE_STOCK_CUR tsssc , TB_ST_STORE_STOCK_MONTHLY tsssm WHERE tsssc.STORE_CD = #{storeCd} AND tsssm.IOSTOCK_YM(+) = SUBSTR(#{date}, 0, 6) AND tsssm.STORE_CD (+) = tsssc.STORE_CD AND tsssm.PROD_CD (+) = tsssc.PROD_CD ) tsssc WHERE tsssc.STORE_CD (+) = tpte.STORE_CD AND tsssc.PROD_CD (+) = tpte.PROD_CD

mapper.delete:
/* USE : AcinsMapper.deleteExcelUploadCompleteData */



        DELETE  TB_PO_TEMP_EXCEL
        WHERE   SESSION_ID  = #{sessionId}
        AND     PROD_CD     IS NOT NULL
        AND     PROD_CD     IN  ( SELECT  PROD_CD
                                  FROM    TB_HQ_PRODUCT
                                  WHERE   HQ_OFFICE_CD  =   #{hqOfficeCd}
                                  AND     STOCK_PROD_YN =   'Y'
                                  AND     USE_YN        =   'Y'
                                )



        DELETE  TB_PO_TEMP_EXCEL
        WHERE   SESSION_ID  = #{sessionId}
        AND     PROD_CD     IS NOT NULL
        AND     PROD_CD     IN  ( SELECT  PROD_CD
                                  FROM    TB_MS_PRODUCT
                                  WHERE   STORE_CD      =   #{storeCd}
                                  AND     STOCK_PROD_YN =   'Y'
                                  AND     USE_YN        =   'Y'
                                )
