mapper.select:
/* USE : AcinsMapper.getHqAcinsList */

SELECT  tshai.ACINS_DATE, tshai.HQ_OFFICE_CD, tshai.SEQ_NO
,       tshai.ACINS_TITLE, tshai.PROC_FG, tshai.DTL_CNT
,       tshai.ADJ_CNT, tshai.CONFM_DATE
,       SUBSTR(tshai.REG_DT,0,8) AS REG_DATE
FROM    TB_ST_HQ_ACTUAL_INSPECTION tshai
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     ACINS_DATE  BETWEEN #{startDate} AND #{endDate}


    AND tshai.PROC_FG = #{procFg}


ORDER BY tshai.ACINS_DATE DESC, tshai.SEQ_NO DESC

/* USE : AcinsMapper.getStAcinsList */

SELECT  tssai.ACINS_DATE, tssai.STORE_CD, tssai.SEQ_NO
,       tssai.ACINS_TITLE, tssai.PROC_FG, tssai.DTL_CNT
,       tssai.ADJ_CNT, tssai.CONFM_DATE
,       SUBSTR(tssai.REG_DT,0,8) AS REG_DATE
FROM    TB_ST_STORE_ACTUAL_INSPECTION tssai
WHERE   tssai.STORE_CD    = #{storeCd}
AND     tssai.ACINS_DATE  BETWEEN #{startDate} AND #{endDate}


    AND tssai.PROC_FG = #{procFg}


ORDER BY tssai.ACINS_DATE DESC, tssai.SEQ_NO DESC

/* USE : AcinsMapper.getHqProcFgCheck */

SELECT  tshai.PROC_FG, tshai.ACINS_TITLE
FROM    TB_ST_HQ_ACTUAL_INSPECTION tshai
WHERE   tshai.HQ_OFFICE_CD  = #{hqOfficeCd}
AND     tshai.ACINS_DATE    = #{acinsDate}
AND     tshai.SEQ_NO        = #{seqNo}

/* USE : AcinsMapper.getStProcFgCheck */

SELECT  tssai.PROC_FG, tssai.ACINS_TITLE
FROM    TB_ST_STORE_ACTUAL_INSPECTION tssai
WHERE   tssai.STORE_CD      = #{storeCd}
AND     tssai.ACINS_DATE    = #{acinsDate}
AND     tssai.SEQ_NO        = #{seqNo}

/* USE : AcinsMapper.getHqAcinsRegistList */
SELECT thpb.BARCD_CD, thp.PROD_CD, thp.PROD_NM, thp.PO_UNIT_QTY , NVL((SELECT MAX(SALE_UPRC) KEEP(DENSE_RANK FIRST ORDER BY REG_DT DESC) FROM TB_HQ_PRODUCT_SALE_PRICE WHERE HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND PROD_CD = thp.PROD_CD AND #{acinsDate} BETWEEN START_DATE AND END_DATE ), 0) AS SALE_UPRC , NVL(tshsc.AVG_COST_UPRC, thp.COST_UPRC) AS COST_UPRC , NVL(NVL(tshaid.CMPT_CURR_QTY, tshsc.CURR_QTY), 0) AS CMPT_CURR_QTY , tshaid.ACINS_QTY , tshaid.ACINS_AMT , tshaid.ADJ_QTY , tshaid.ADJ_AMT , tshaid.REMARK , DECODE(tshaid.PROD_CD, NULL, 'I', 'U') AS ACINS_PROD_STATUS
FROM TB_HQ_PRODUCT thp , TB_ST_HQ_ACTUAL_INSPECTION_DTL tshaid , ( SELECT tshsc.PROD_CD, tshsc.CURR_QTY, tshsm.AVG_COST_UPRC FROM TB_ST_HQ_STOCK_CUR tshsc , TB_ST_HQ_STOCK_MONTHLY tshsm WHERE tshsc.HQ_OFFICE_CD = #{hqOfficeCd} AND tshsm.IOSTOCK_YM (+) = SUBSTR(#{acinsDate}, 0, 6) AND tshsm.HQ_OFFICE_CD(+) = tshsc.HQ_OFFICE_CD AND tshsm.PROD_CD (+) = tshsc.PROD_CD ) tshsc , TB_HQ_PRODUCT_BARCD thpb WHERE thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.STOCK_PROD_YN = 'Y' AND thp.USE_YN = 'Y' AND tshaid.PROD_CD (+) = thp.PROD_CD AND tshaid.HQ_OFFICE_CD (+) = #{hqOfficeCd} AND tshaid.ACINS_DATE (+) = #{acinsDate} AND tshaid.SEQ_NO (+) = #{seqNo} AND tshsc.PROD_CD (+) = thp.PROD_CD AND thpb.HQ_OFFICE_CD (+) = thp.HQ_OFFICE_CD AND thpb.PROD_CD (+) = thp.PROD_CD
AND thp.PROD_CD = #{prodCd}
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thpb.BARCD_CD = #{barcdCd}
AND (thp.PROD_CD = #{prodBarcdCd} OR thpb.BARCD_CD = #{prodBarcdCd})
AND tshaid.ACINS_QTY IS NOT NULL
AND tshaid.ACINS_QTY IS NULL
ORDER BY thp.PROD_CD

/* USE : AcinsMapper.getStAcinsRegistList */
SELECT tmpb.BARCD_CD, tmp.PROD_CD, tmp.PROD_NM, tmp.PO_UNIT_QTY , NVL((SELECT MAX(SALE_UPRC) KEEP(DENSE_RANK FIRST ORDER BY REG_DT DESC) FROM TB_MS_PRODUCT_SALE_PRICE WHERE STORE_CD = tmp.STORE_CD AND PROD_CD = tmp.PROD_CD AND #{acinsDate} BETWEEN START_DATE AND END_DATE ), 0) AS SALE_UPRC , NVL(tsssc.AVG_COST_UPRC, tmp.COST_UPRC) AS COST_UPRC , NVL(NVL(tssaid.CMPT_CURR_QTY, tsssc.CURR_QTY), 0) AS CMPT_CURR_QTY , tssaid.ACINS_QTY , tssaid.ACINS_AMT , tssaid.ADJ_QTY , tssaid.ADJ_AMT , tssaid.REMARK , DECODE(tssaid.PROD_CD, NULL, 'I', 'U') AS ACINS_PROD_STATUS
FROM TB_MS_PRODUCT tmp , TB_ST_STORE_ACTUAL_INSPECTION_DTL tssaid , ( SELECT tsssc.PROD_CD, tsssc.CURR_QTY, tsssm.AVG_COST_UPRC FROM TB_ST_STORE_STOCK_CUR tsssc , TB_ST_STORE_STOCK_MONTHLY tsssm WHERE tsssc.STORE_CD = #{storeCd} AND tsssm.IOSTOCK_YM (+) = SUBSTR(#{acinsDate}, 0, 6) AND tsssm.STORE_CD (+) = tsssc.STORE_CD AND tsssm.PROD_CD (+) = tsssc.PROD_CD ) tsssc , TB_MS_PRODUCT_BARCD tmpb WHERE tmp.STORE_CD = #{storeCd} AND tmp.STOCK_PROD_YN = 'Y' AND tmp.USE_YN = 'Y' AND tssaid.PROD_CD (+) = tmp.PROD_CD AND tssaid.STORE_CD (+) = #{storeCd} AND tssaid.ACINS_DATE (+) = #{acinsDate} AND tssaid.SEQ_NO (+) = #{seqNo} AND tsssc.PROD_CD (+) = tmp.PROD_CD AND tmpb.STORE_CD (+) = tmp.STORE_CD AND tmpb.PROD_CD (+) = tmp.PROD_CD
AND tmp.PROD_CD = #{prodCd}
AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND tmpb.BARCD_CD = #{barcdCd}
AND (tmp.PROD_CD = #{prodBarcdCd} OR tmpb.BARCD_CD = #{prodBarcdCd})
AND tssaid.ACINS_QTY IS NOT NULL
AND tssaid.ACINS_QTY IS NULL
ORDER BY tmp.PROD_CD

/* USE : AcinsMapper.getHqNewSeqNo */

SELECT  NVL(MAX(tshai.SEQ_NO), 0)+1 AS SEQ_NO
FROM    TB_ST_HQ_ACTUAL_INSPECTION tshai
WHERE   tshai.HQ_OFFICE_CD  =  #{hqOfficeCd}
AND     tshai.ACINS_DATE    =  #{acinsDate}

/* USE : AcinsMapper.getStNewSeqNo */

SELECT  NVL(MAX(tssai.SEQ_NO), 0)+1 AS SEQ_NO
FROM    TB_ST_STORE_ACTUAL_INSPECTION tssai
WHERE   tssai.STORE_CD      =  #{storeCd}
AND     tssai.ACINS_DATE    =  #{acinsDate}

/* USE : AcinsMapper.getHqAcinsDtlList */
SELECT thpb.BARCD_CD, thp.PROD_CD, thp.PROD_NM, thp.PO_UNIT_QTY , NVL((SELECT MAX(SALE_UPRC) KEEP(DENSE_RANK FIRST ORDER BY REG_DT DESC) FROM TB_HQ_PRODUCT_SALE_PRICE WHERE HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND PROD_CD = thp.PROD_CD AND #{acinsDate} BETWEEN START_DATE AND END_DATE ), 0) AS SALE_UPRC , NVL(tshsm.AVG_COST_UPRC, thp.COST_UPRC) AS COST_UPRC , tshaid.CMPT_CURR_QTY , tshaid.ACINS_QTY , tshaid.ACINS_AMT , tshaid.ADJ_QTY , tshaid.ADJ_AMT , tshaid.REMARK , 'U' AS ACINS_PROD_STATUS
FROM TB_ST_HQ_ACTUAL_INSPECTION_DTL tshaid , TB_HQ_PRODUCT thp , ( SELECT tshsm.PROD_CD , tshsm.AVG_COST_UPRC , (tshsm.BASE_QTY + NVL(IOSTOCK_QTY, 0)) AS CMPT_DATE_QTY FROM ( SELECT tshsm.IOSTOCK_YM, tshsm.HQ_OFFICE_CD , tshsm.PROD_CD, tshsm.BASE_QTY, tshsm.AVG_COST_UPRC FROM TB_ST_HQ_STOCK_MONTHLY tshsm WHERE tshsm.HQ_OFFICE_CD = #{hqOfficeCd} AND tshsm.IOSTOCK_YM = SUBSTR(#{acinsDate}, 0, 6) ) tshsm , ( SELECT tshsd.IOSTOCK_DATE, tshsd.HQ_OFFICE_CD , tshsd.PROD_CD , SUM(tshsd.IOSTOCK_QTY) AS IOSTOCK_QTY FROM TB_ST_HQ_STOCK_DAILY tshsd WHERE tshsd.HQ_OFFICE_CD = #{hqOfficeCd} AND tshsd.IOSTOCK_DATE = #{acinsDate} GROUP BY tshsd.IOSTOCK_DATE, tshsd.HQ_OFFICE_CD , tshsd.PROD_CD ) tshsd WHERE tshsd.PROD_CD(+)= tshsm.PROD_CD ) tshsm , TB_HQ_PRODUCT_BARCD thpb WHERE tshaid.HQ_OFFICE_CD = #{hqOfficeCd} AND tshaid.ACINS_DATE = #{acinsDate} AND tshaid.SEQ_NO = #{seqNo} AND tshaid.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND tshaid.PROD_CD = thp.PROD_CD AND tshsm.PROD_CD (+) = tshaid.PROD_CD AND thpb.HQ_OFFICE_CD (+) = tshaid.HQ_OFFICE_CD AND thpb.PROD_CD (+) = tshaid.PROD_CD ORDER BY tshaid.PROD_CD

/* USE : AcinsMapper.getStAcinsDtlList */
SELECT tmpb.BARCD_CD, tmp.PROD_CD, tmp.PROD_NM, tmp.PO_UNIT_QTY , NVL((SELECT MAX(SALE_UPRC) KEEP(DENSE_RANK FIRST ORDER BY REG_DT DESC) FROM TB_MS_PRODUCT_SALE_PRICE WHERE STORE_CD = tmp.STORE_CD AND PROD_CD = tmp.PROD_CD AND #{acinsDate} BETWEEN START_DATE AND END_DATE ), 0) AS SALE_UPRC , NVL(tsssm.AVG_COST_UPRC, tmp.COST_UPRC) AS COST_UPRC , tssaid.CMPT_CURR_QTY , tssaid.ACINS_QTY , tssaid.ACINS_AMT , tssaid.ADJ_QTY , tssaid.ADJ_AMT , tssaid.REMARK , 'U' AS ACINS_PROD_STATUS
FROM TB_ST_STORE_ACTUAL_INSPECTION_DTL tssaid , TB_MS_PRODUCT tmp , ( SELECT tsssm.PROD_CD , tsssm.AVG_COST_UPRC , (tsssm.BASE_QTY + NVL(IOSTOCK_QTY, 0)) AS CMPT_DATE_QTY FROM ( SELECT tsssm.IOSTOCK_YM, tsssm.STORE_CD , tsssm.PROD_CD, tsssm.BASE_QTY, tsssm.AVG_COST_UPRC FROM TB_ST_STORE_STOCK_MONTHLY tsssm WHERE tsssm.STORE_CD = #{storeCd} AND tsssm.IOSTOCK_YM = SUBSTR(#{acinsDate}, 0, 6) ) tsssm , ( SELECT tsssd.IOSTOCK_DATE, tsssd.STORE_CD , tsssd.PROD_CD , SUM(tsssd.IOSTOCK_QTY) AS IOSTOCK_QTY FROM TB_ST_STORE_STOCK_DAILY tsssd WHERE tsssd.STORE_CD = #{storeCd} AND tsssd.IOSTOCK_DATE = #{acinsDate} GROUP BY tsssd.IOSTOCK_DATE, tsssd.STORE_CD , tsssd.PROD_CD ) tsssd WHERE tsssd.PROD_CD(+)= tsssm.PROD_CD ) tsssm , TB_MS_PRODUCT_BARCD tmpb WHERE tssaid.STORE_CD = #{storeCd} AND tssaid.ACINS_DATE = #{acinsDate} AND tssaid.SEQ_NO = #{seqNo} AND tssaid.STORE_CD = tmp.STORE_CD AND tssaid.PROD_CD = tmp.PROD_CD AND tsssm.PROD_CD (+) = tssaid.PROD_CD AND tmpb.STORE_CD (+) = tssaid.STORE_CD AND tmpb.PROD_CD (+) = tssaid.PROD_CD ORDER BY tssaid.PROD_CD

