mapper.insert:
/* USE : AcinsMapper.insertHqAcinsHd */
INSERT INTO TB_ST_HQ_ACTUAL_INSPECTION
(
    HQ_OFFICE_CD,
    STORAGE_CD,
    ACINS_DATE,
    SEQ_NO,
    ACINS_TITLE,
    PROC_FG,
    DTL_CNT,
    ADJ_CNT,
    TOT_ACINS_AMT,
    REG_DT,
    REG_ID,
    MOD_DT,
    MOD_ID
)
SELECT	tshaid.HQ_OFFICE_CD
,		#{storageCd}
,		tshaid.ACINS_DATE
,		tshaid.SEQ_NO
,		#{acinsTitle}
,		#{procFg}
,		COUNT(tshaid.PROD_CD)
,		SUM(DECODE(tshaid.ADJ_QTY, 0, 0, 1))
,		SUM(tshaid.ACINS_AMT)
,       #{regDt}
,       #{regId}
,       #{modDt}
,       #{modId}
FROM	TB_ST_HQ_ACTUAL_INSPECTION_DTL tshaid
WHERE	tshaid.HQ_OFFICE_CD	=	#{hqOfficeCd}
AND     tshaid.ACINS_DATE	=	#{acinsDate}
AND     tshaid.SEQ_NO	    =	#{seqNo}
GROUP
BY		tshaid.HQ_OFFICE_CD
,		#{storageCd}
,		tshaid.ACINS_DATE
,		tshaid.SEQ_NO
,		#{acinsTitle}
,		#{procFg}
,       #{regDt}
,       #{regId}
,       #{modDt}
,       #{modId}

/* USE : AcinsMapper.insertStAcinsHd */
INSERT INTO TB_ST_STORE_ACTUAL_INSPECTION
(
    STORE_CD,
    STORAGE_CD,
    ACINS_DATE,
    SEQ_NO,
    ACINS_TITLE,
    PROC_FG,
    DTL_CNT,
    ADJ_CNT,
    TOT_ACINS_AMT,
    REG_DT,
    REG_ID,
    MOD_DT,
    MOD_ID
)
SELECT  tshaid.STORE_CD
,       #{storageCd}
,       tshaid.ACINS_DATE
,       tshaid.SEQ_NO
,       #{acinsTitle}
,       #{procFg}
,       COUNT(tshaid.PROD_CD)
,       SUM(DECODE(tshaid.ADJ_QTY, 0, 0, 1))
,       SUM(tshaid.ACINS_AMT)
,       #{regDt}
,       #{regId}
,       #{modDt}
,       #{modId}
FROM    TB_ST_STORE_ACTUAL_INSPECTION_DTL tshaid
WHERE   tshaid.STORE_CD     =   #{storeCd}
AND     tshaid.ACINS_DATE   =   #{acinsDate}
AND     tshaid.SEQ_NO       =   #{seqNo}
GROUP
BY      tshaid.STORE_CD
,       #{storageCd}
,       tshaid.ACINS_DATE
,       tshaid.SEQ_NO
,       #{acinsTitle}
,       #{procFg}
,       #{regDt}
,       #{regId}
,       #{modDt}
,       #{modId}

mapper.update:
/* USE : AcinsMapper.updateHqAcinsHd */
UPDATE  TB_ST_HQ_ACTUAL_INSPECTION
SET     (DTL_CNT, ADJ_CNT, TOT_ACINS_AMT)
      = (   SELECT  COUNT(tshaid.PROD_CD)
            ,       SUM(DECODE(tshaid.ADJ_QTY, 0, 0, 1))
            ,       SUM(tshaid.ACINS_AMT)
            FROM    TB_ST_HQ_ACTUAL_INSPECTION_DTL tshaid
            WHERE   tshaid.HQ_OFFICE_CD =   #{hqOfficeCd}
            AND     tshaid.ACINS_DATE   =   #{acinsDate}
            AND     tshaid.SEQ_NO       =   #{seqNo}
        )
,       ACINS_TITLE = #{acinsTitle}
,       PROC_FG     = #{procFg}
,       CONFM_DATE  = DECODE(#{procFg}, '0', NULL, SUBSTR(#{modDt}, 0, 8))
,       CONFM_ID    = DECODE(#{procFg}, '0', NULL, #{modId})
,       MOD_DT      = #{modDt}
,       MOD_ID      = #{modId}
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     ACINS_DATE    = #{acinsDate}
AND     SEQ_NO        = #{seqNo}

/* USE : AcinsMapper.updateStAcinsHd */
UPDATE  TB_ST_STORE_ACTUAL_INSPECTION
SET     (DTL_CNT, ADJ_CNT, TOT_ACINS_AMT)
      = (   SELECT  COUNT(tshaid.PROD_CD)
            ,       SUM(DECODE(tshaid.ADJ_QTY, 0, 0, 1))
            ,       SUM(tshaid.ACINS_AMT)
            FROM    TB_ST_STORE_ACTUAL_INSPECTION_DTL tshaid
            WHERE   tshaid.STORE_CD =   #{storeCd}
            AND     tshaid.ACINS_DATE   =   #{acinsDate}
            AND     tshaid.SEQ_NO       =   #{seqNo}
        )
,       ACINS_TITLE = #{acinsTitle}
,       PROC_FG     = #{procFg}
,       CONFM_DATE  = DECODE(#{procFg}, '0', NULL, SUBSTR(#{modDt}, 0, 8))
,       CONFM_ID    = DECODE(#{procFg}, '0', NULL, #{modId})
,       MOD_DT      = #{modDt}
,       MOD_ID      = #{modId}
WHERE   STORE_CD      = #{storeCd}
AND     ACINS_DATE    = #{acinsDate}
AND     SEQ_NO        = #{seqNo}
