mapper.select:
/* USE : HqAcinsMapper.getHqAcinsList */

SELECT  tshai.ACINS_DATE, tshai.HQ_OFFICE_CD, tshai.SEQ_NO
,       tshai.ACINS_TITLE, tshai.PROC_FG, tshai.DTL_CNT
,       tshai.ADJ_CNT, tshai.CONFM_DATE
,       SUBSTR(tshai.REG_DT,0,8) AS REG_DATE
FROM    TB_ST_HQ_ACTUAL_INSPECTION tshai
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     ACINS_DATE  BETWEEN #{startDate} AND #{endDate}


    AND tshai.PROC_FG = #{procFg}


ORDER BY tshai.ACINS_DATE DESC, tshai.SEQ_NO DESC

/* USE : HqAcinsMapper.getProcFgCheck */

SELECT  tshai.PROC_FG, tshai.ACINS_TITLE
FROM    TB_ST_HQ_ACTUAL_INSPECTION tshai
WHERE   tshai.HQ_OFFICE_CD  = #{hqOfficeCd}
AND     tshai.ACINS_DATE    = #{acinsDate}
AND     tshai.SEQ_NO        = #{seqNo}

/* USE : HqAcinsMapper.getHqAcinsRegistList */
SELECT thpb.BARCD_CD, thp.PROD_CD, thp.PROD_NM, thp.PO_UNIT_QTY , NVL((SELECT MAX(SALE_UPRC) KEEP(DENSE_RANK FIRST ORDER BY REG_DT DESC) FROM TB_HQ_PRODUCT_SALE_PRICE WHERE HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND PROD_CD = thp.PROD_CD AND #{acinsDate} BETWEEN START_DATE AND END_DATE ), 0) AS SALE_UPRC , NVL(tshsc.AVG_COST_UPRC, thp.COST_UPRC) AS COST_UPRC , NVL(NVL(tshaid.CMPT_CURR_QTY, tshsc.CURR_QTY), 0) AS CMPT_CURR_QTY , tshaid.ACINS_QTY , tshaid.ACINS_AMT , tshaid.ADJ_QTY , tshaid.ADJ_AMT , tshaid.REMARK , DECODE(tshaid.PROD_CD, NULL, 'I', 'U') AS ACINS_PROD_STATUS FROM TB_HQ_PRODUCT thp , TB_ST_HQ_ACTUAL_INSPECTION_DTL tshaid , ( SELECT tshsc.PROD_CD, tshsc.CURR_QTY, tshsm.AVG_COST_UPRC FROM TB_ST_HQ_STOCK_CUR tshsc , TB_ST_HQ_STOCK_MONTHLY tshsm WHERE tshsc.HQ_OFFICE_CD = #{hqOfficeCd} AND tshsm.IOSTOCK_YM (+) = SUBSTR(#{acinsDate}, 0, 6) AND tshsm.HQ_OFFICE_CD(+) = tshsc.HQ_OFFICE_CD AND tshsm.PROD_CD (+) = tshsc.PROD_CD ) tshsc , TB_HQ_PRODUCT_BARCD thpb WHERE thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.STOCK_PROD_YN = 'Y' AND thp.USE_YN = 'Y' AND tshaid.PROD_CD (+) = thp.PROD_CD AND tshaid.HQ_OFFICE_CD (+) = #{hqOfficeCd} AND tshaid.ACINS_DATE (+) = #{acinsDate} AND tshaid.SEQ_NO (+) = #{seqNo} AND tshsc.PROD_CD (+) = thp.PROD_CD AND thpb.HQ_OFFICE_CD (+) = thp.HQ_OFFICE_CD AND thpb.PROD_CD (+) = thp.PROD_CD
AND thp.PROD_CD = #{prodCd}
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thpb.BARCD_CD = #{barcdCd}
AND (thp.PROD_CD = #{prodBarcdCd} OR thpb.BARCD_CD = #{prodBarcdCd})
ORDER BY thp.PROD_CD

/* USE : HqAcinsMapper.getNewSeqNo */

SELECT  NVL(MAX(tshai.SEQ_NO), 0)+1 AS SEQ_NO
FROM    TB_ST_HQ_ACTUAL_INSPECTION tshai
WHERE   tshai.HQ_OFFICE_CD  =  #{hqOfficeCd}
AND     tshai.ACINS_DATE    =  #{acinsDate}

mapper.delete:
/* USE : HqAcinsMapper.deleteAllHqAcinsDtl */
DELETE  TB_ST_HQ_ACTUAL_INSPECTION_DTL
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     ACINS_DATE    = #{acinsDate}
AND     SEQ_NO        = #{seqNo}

/* USE : HqAcinsMapper.deleteHqAcinsHd */
DELETE  TB_ST_HQ_ACTUAL_INSPECTION
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     ACINS_DATE    = #{acinsDate}
AND     SEQ_NO        = #{seqNo}

mapper.update:
/* USE : HqAcinsMapper.updateHqAcinsDtl */
UPDATE  TB_ST_HQ_ACTUAL_INSPECTION_DTL
SET     ACINS_QTY = #{acinsQty}
,       ACINS_AMT = #{acinsAmt}
,       ADJ_QTY   = #{adjQty}
,       ADJ_AMT   = #{adjAmt}
,       REMARK    = #{remark}
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     ACINS_DATE    = #{acinsDate}
AND     SEQ_NO        = #{seqNo}
AND     PROD_CD       = #{prodCd}

mapper.delete:
/* USE : HqAcinsMapper.deleteHqAcinsDtl */
DELETE  TB_ST_HQ_ACTUAL_INSPECTION_DTL
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     ACINS_DATE    = #{acinsDate}
AND     SEQ_NO        = #{seqNo}
AND     PROD_CD       = #{prodCd}

mapper.insert:
/* USE : HqAcinsMapper.insertHqAcinsHd */
INSERT INTO TB_ST_HQ_ACTUAL_INSPECTION
(
    HQ_OFFICE_CD,
    STORAGE_CD,
    ACINS_DATE,
    SEQ_NO,
    ACINS_TITLE,
    PROC_FG,
    DTL_CNT,
    ADJ_CNT,
    TOT_ACINS_AMT,
    REG_DT,
    REG_ID,
    MOD_DT,
    MOD_ID
)
SELECT	tshaid.HQ_OFFICE_CD
,		#{storageCd}
,		tshaid.ACINS_DATE
,		tshaid.SEQ_NO
,		#{acinsTitle}
,		#{procFg}
,		COUNT(tshaid.PROD_CD)
,		SUM(DECODE(tshaid.ADJ_QTY, 0, 0, 1))
,		SUM(tshaid.ACINS_AMT)
,       #{regDt}
,       #{regId}
,       #{modDt}
,       #{modId}
FROM	TB_ST_HQ_ACTUAL_INSPECTION_DTL tshaid
WHERE	tshaid.HQ_OFFICE_CD	=	#{hqOfficeCd}
AND     tshaid.ACINS_DATE	=	#{acinsDate}
AND     tshaid.SEQ_NO	    =	#{seqNo}
GROUP
BY		tshaid.HQ_OFFICE_CD
,		#{storageCd}
,		tshaid.ACINS_DATE
,		tshaid.SEQ_NO
,		#{acinsTitle}
,		#{procFg}
,       #{regDt}
,       #{regId}
,       #{modDt}
,       #{modId}

mapper.update:
/* USE : HqAcinsMapper.updateHqAcinsHd */
UPDATE  TB_ST_HQ_ACTUAL_INSPECTION
SET     (DTL_CNT, ADJ_CNT, TOT_ACINS_AMT)
      = (   SELECT  COUNT(tshaid.PROD_CD)
            ,		SUM(DECODE(tshaid.ADJ_QTY, 0, 0, 1))
            ,		SUM(tshaid.ACINS_AMT)
            FROM    TB_ST_HQ_ACTUAL_INSPECTION_DTL tshaid
            WHERE	tshaid.HQ_OFFICE_CD	=	#{hqOfficeCd}
            AND     tshaid.ACINS_DATE	=	#{acinsDate}
            AND     tshaid.SEQ_NO	    =	#{seqNo}
        )
,       ACINS_TITLE = #{acinsTitle}
,       PROC_FG     = #{procFg}
,       CONFM_DATE  = DECODE(#{procFg}, '0', NULL, SUBSTR(#{modDt}, 0, 8))
,       CONFM_ID    = DECODE(#{procFg}, '0', NULL, #{modId})
,       MOD_DT      = #{modDt}
,       MOD_ID      = #{modId}
WHERE	HQ_OFFICE_CD  =	#{hqOfficeCd}
AND     ACINS_DATE	  =	#{acinsDate}
AND     SEQ_NO	      =	#{seqNo}
