mapper.select:
/* USE : StockComPopupMapper.getDailyIoStockStoreInfoListStoreInQty, 매장입고 */ SELECT B.SLIP_NO , B.IN_DATE AS INSTOCK_DATE , B.HQ_OFFICE_CD , B.HQ_OFFICE_NM , FN_GET_PROD_CLASS_CD_NM_STR(#{storeCd}, B.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM , B.PROD_CD , B.PROD_NM , B.BARCD_CD , B.IN_TOT_QTY , B.IN_TOT FROM ( SELECT TPHSO.SLIP_NO /* 전표번호 */ , TPHSO.IN_DATE /* 입고일 */ , TPHSO.HQ_OFFICE_CD , (SELECT HQ_OFFICE_NM FROM TB_HQ_OFFICE Z WHERE Z.HQ_OFFICE_CD = TPHSO.HQ_OFFICE_CD) AS HQ_OFFICE_NM , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD , SUM(TPHSOD.IN_TOT_QTY) AS IN_TOT_QTY , SUM(TPHSOD.IN_TOT) AS IN_TOT FROM TB_PO_HQ_STORE_OUTSTOCK TPHSO , TB_PO_HQ_STORE_OUTSTOCK_DTL TPHSOD , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TPHSO.STORE_CD = #{storeCd} AND TMP.STOCK_PROD_YN = 'Y' AND TPHSO.SLIP_FG = '1' AND TPHSO.SLIP_KIND = '0' AND TPHSO.PROC_FG = '30'
AND TPHSO.IN_DATE = #{saleDate}
AND TPHSO.HQ_OFFICE_CD = TPHSOD.HQ_OFFICE_CD AND TPHSO.SLIP_NO = TPHSOD.SLIP_NO AND TPHSOD.STORE_CD = TMP.STORE_CD AND TPHSOD.PROD_CD = TMP.PROD_CD AND TPHSOD.STORE_CD = TMPB.STORE_CD(+) AND TPHSOD.PROD_CD = TMPB.PROD_CD(+) GROUP BY TPHSO.SLIP_NO , TPHSO.IN_DATE , TPHSO.HQ_OFFICE_CD , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD ) B ORDER BY B.SLIP_NO, B.PROD_CD

/* USE : StockComPopupMapper.getDailyIoStockStoreInfoListStoreOutQty, 매장반품 */ SELECT B.SLIP_NO , B.OUT_DATE AS CARRY_OUT_DATE , B.HQ_OFFICE_CD , B.HQ_OFFICE_NM , FN_GET_PROD_CLASS_CD_NM_STR(#{storeCd}, B.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM , B.PROD_CD , B.PROD_NM , B.BARCD_CD , B.IN_TOT_QTY , B.IN_TOT FROM ( SELECT TPHSO.SLIP_NO /* 전표번호 */ , TPHSO.OUT_DATE /* 입고일 */ , TPHSO.HQ_OFFICE_CD , (SELECT HQ_OFFICE_NM FROM TB_HQ_OFFICE Z WHERE Z.HQ_OFFICE_CD = TPHSO.HQ_OFFICE_CD) AS HQ_OFFICE_NM , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD , SUM(TPHSOD.OUT_TOT_QTY * TPHSO.SLIP_FG) AS IN_TOT_QTY , SUM(TPHSOD.OUT_TOT * TPHSO.SLIP_FG) AS IN_TOT FROM TB_PO_HQ_STORE_OUTSTOCK TPHSO , TB_PO_HQ_STORE_OUTSTOCK_DTL TPHSOD , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TPHSO.STORE_CD = #{storeCd} AND TMP.STOCK_PROD_YN = 'Y' AND TPHSO.SLIP_FG = '-1' /* 입고:1, 출고:-1 */ AND TPHSO.SLIP_KIND = '0' AND TPHSO.PROC_FG IN ('20', '30')
AND TPHSO.OUT_DATE = #{saleDate}
AND TPHSO.HQ_OFFICE_CD = TPHSOD.HQ_OFFICE_CD AND TPHSO.SLIP_NO = TPHSOD.SLIP_NO AND TPHSOD.STORE_CD = TMP.STORE_CD AND TPHSOD.PROD_CD = TMP.PROD_CD AND TPHSOD.STORE_CD = TMPB.STORE_CD(+) AND TPHSOD.PROD_CD = TMPB.PROD_CD(+) GROUP BY TPHSO.SLIP_NO , TPHSO.OUT_DATE , TPHSO.HQ_OFFICE_CD , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD ) B ORDER BY B.SLIP_NO, B.PROD_CD

/* USE : StockComPopupMapper.getDailyIoStockStoreInfoListPurchsInQty, 사입입고 */ SELECT B.SLIP_NO , B.INSTOCK_DATE , B.VENDR_CD , B.VENDR_NM , FN_GET_PROD_CLASS_CD_NM_STR(#{storeCd}, B.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM , B.PROD_CD , B.PROD_NM , B.BARCD_CD , B.IN_TOT_QTY , B.IN_TOT FROM ( SELECT TPSVI.SLIP_NO /* 전표번호 */ , TPSVI.INSTOCK_DATE , TPSVI.VENDR_CD , (SELECT VENDR_NM FROM TB_MS_VENDOR Z WHERE Z.STORE_CD = TPSVI.STORE_CD AND Z.VENDR_CD = TPSVI.VENDR_CD) AS VENDR_NM /* 거래처명 */ , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD , SUM(TPSVID.IN_TOT_QTY) AS IN_TOT_QTY , SUM(TPSVID.IN_TOT) AS IN_TOT FROM TB_PO_STORE_VENDR_INSTOCK TPSVI , TB_PO_STORE_VENDR_INSTOCK_DTL TPSVID , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TPSVI.STORE_CD = #{storeCd} AND TMP.STOCK_PROD_YN = 'Y' AND TPSVI.SLIP_FG = '1' /* 입고:1, 출고:-1 */
AND TPSVI.INSTOCK_DATE = #{saleDate}
AND TPSVI.STORE_CD = TPSVID.STORE_CD AND TPSVI.SLIP_NO = TPSVID.SLIP_NO AND TPSVID.STORE_CD = TMP.STORE_CD AND TPSVID.PROD_CD = TMP.PROD_CD AND TPSVID.STORE_CD = TMPB.STORE_CD(+) AND TPSVID.PROD_CD = TMPB.PROD_CD(+) GROUP BY TPSVI.STORE_CD , TPSVI.SLIP_NO , TPSVI.INSTOCK_DATE , TPSVI.VENDR_CD , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD ) B ORDER BY B.SLIP_NO, B.PROD_CD

/* USE : StockComPopupMapper.getDailyIoStockStoreInfoListPurchsOutQty, 사입반품 */ SELECT B.SLIP_NO , B.INSTOCK_DATE AS RTN_DATE , B.VENDR_CD , B.VENDR_NM , FN_GET_PROD_CLASS_CD_NM_STR(#{storeCd}, B.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM , B.PROD_CD , B.PROD_NM , B.BARCD_CD , B.IN_TOT_QTY , B.IN_TOT FROM ( SELECT TPSVI.SLIP_NO /* 전표번호 */ , TPSVI.INSTOCK_DATE , TPSVI.VENDR_CD , (SELECT VENDR_NM FROM TB_MS_VENDOR Z WHERE Z.STORE_CD = TPSVI.STORE_CD AND Z.VENDR_CD = TPSVI.VENDR_CD) AS VENDR_NM /* 거래처명 */ , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD , SUM(TPSVID.IN_TOT_QTY * TPSVI.SLIP_FG) AS IN_TOT_QTY , SUM(TPSVID.IN_TOT * TPSVI.SLIP_FG) AS IN_TOT FROM TB_PO_STORE_VENDR_INSTOCK TPSVI , TB_PO_STORE_VENDR_INSTOCK_DTL TPSVID , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TPSVI.STORE_CD = #{storeCd} AND TMP.STOCK_PROD_YN = 'Y' AND TPSVI.SLIP_FG = '-1' /* 입고:1, 출고:-1 */
AND TPSVI.INSTOCK_DATE = #{saleDate}
AND TPSVI.STORE_CD = TPSVID.STORE_CD AND TPSVI.SLIP_NO = TPSVID.SLIP_NO AND TPSVID.STORE_CD = TMP.STORE_CD AND TPSVID.PROD_CD = TMP.PROD_CD AND TPSVID.STORE_CD = TMPB.STORE_CD(+) AND TPSVID.PROD_CD = TMPB.PROD_CD(+) GROUP BY TPSVI.STORE_CD , TPSVI.SLIP_NO , TPSVI.INSTOCK_DATE , TPSVI.VENDR_CD , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD ) B ORDER BY B.SLIP_NO, B.PROD_CD

/* USE : StockComPopupMapper.getDailyIoStockStoreInfoListMoveInQty, 매장이입 */ SELECT B.SLIP_NO , B.IN_CONFM_DT , B.HQ_OFFICE_CD , B.HQ_OFFICE_NM , FN_GET_PROD_CLASS_CD_NM_STR(#{storeCd}, B.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM , B.PROD_CD , B.PROD_NM , B.BARCD_CD , B.IN_TOT_QTY , B.IN_TOT FROM ( SELECT TPSM.SLIP_NO /* 전표번호 */ , TO_CHAR(TO_DATE(TPSM.IN_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') AS IN_CONFM_DT , TPSM.HQ_OFFICE_CD , (SELECT HQ_OFFICE_NM FROM TB_HQ_OFFICE Z WHERE Z.HQ_OFFICE_CD = TPSM.HQ_OFFICE_CD) AS HQ_OFFICE_NM , THP.PROD_CLASS_CD , THP.PROD_CD , THP.PROD_NM , THPB.BARCD_CD , SUM(TPSMD.IN_TOT_QTY) AS IN_TOT_QTY , SUM(TPSMD.IN_TOT) AS IN_TOT FROM TB_PO_STORE_MOVE TPSM , TB_PO_STORE_MOVE_DTL TPSMD , TB_HQ_PRODUCT THP , TB_HQ_PRODUCT_BARCD THPB WHERE TPSM.HQ_OFFICE_CD = #{hqOfficeCd} AND TPSM.IN_STORE_CD = #{storeCd} AND THP.STOCK_PROD_YN = 'Y'
AND TPSM.MOVE_DATE = #{saleDate}
AND TPSM.HQ_OFFICE_CD = TPSMD.HQ_OFFICE_CD AND TPSM.SLIP_NO = TPSMD.SLIP_NO AND TPSMD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TPSMD.PROD_CD = THP.PROD_CD AND TPSMD.HQ_OFFICE_CD = THPB.HQ_OFFICE_CD(+) AND TPSMD.PROD_CD = THPB.PROD_CD(+) GROUP BY TPSM.HQ_OFFICE_CD , TPSM.SLIP_NO , TO_CHAR(TO_DATE(TPSM.IN_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') , TPSM.IN_STORE_CD , THP.PROD_CLASS_CD , THP.PROD_CD , THP.PROD_NM , THPB.BARCD_CD ) B ORDER BY B.SLIP_NO, B.PROD_CD

/* USE : StockComPopupMapper.getDailyIoStockStoreInfoListMoveOutQty, 매장이출 */ SELECT B.SLIP_NO , B.OUT_CONFM_DT , B.HQ_OFFICE_CD , B.HQ_OFFICE_NM , FN_GET_PROD_CLASS_CD_NM_STR(#{storeCd}, B.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM , B.PROD_CD , B.PROD_NM , B.BARCD_CD , B.OUT_TOT_QTY , B.OUT_TOT FROM ( SELECT TPSM.SLIP_NO /* 전표번호 */ , TO_CHAR(TO_DATE(TPSM.OUT_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') AS OUT_CONFM_DT , TPSM.HQ_OFFICE_CD , (SELECT HQ_OFFICE_NM FROM TB_HQ_OFFICE Z WHERE Z.HQ_OFFICE_CD = TPSM.HQ_OFFICE_CD) AS HQ_OFFICE_NM , THP.PROD_CLASS_CD , THP.PROD_CD , THP.PROD_NM , THPB.BARCD_CD , SUM(TPSMD.OUT_TOT_QTY) AS OUT_TOT_QTY , SUM(TPSMD.OUT_TOT) AS OUT_TOT FROM TB_PO_STORE_MOVE TPSM , TB_PO_STORE_MOVE_DTL TPSMD , TB_HQ_PRODUCT THP , TB_HQ_PRODUCT_BARCD THPB WHERE TPSM.HQ_OFFICE_CD = #{hqOfficeCd} AND TPSM.OUT_STORE_CD = #{storeCd} AND THP.STOCK_PROD_YN = 'Y'
AND TPSM.MOVE_DATE = #{saleDate}
AND TPSM.HQ_OFFICE_CD = TPSMD.HQ_OFFICE_CD AND TPSM.SLIP_NO = TPSMD.SLIP_NO AND TPSMD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TPSMD.PROD_CD = THP.PROD_CD AND TPSMD.HQ_OFFICE_CD = THPB.HQ_OFFICE_CD(+) AND TPSMD.PROD_CD = THPB.PROD_CD(+) GROUP BY TPSM.HQ_OFFICE_CD , TPSM.SLIP_NO , TO_CHAR(TO_DATE(TPSM.OUT_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') , TPSM.OUT_STORE_CD , THP.PROD_CLASS_CD , THP.PROD_CD , THP.PROD_NM , THPB.BARCD_CD ) B ORDER BY B.SLIP_NO, B.PROD_CD

/* USE : StockComPopupMapper.getDailyIoStockStoreInfoListDisuseQty, 재고폐기 */ SELECT B.DISUSE_DATE , B.SEQ_NO , B.DISUSE_TITLE AS TITLE , B.PROD_FG_NM , FN_GET_PROD_CLASS_CD_NM_STR(#{storeCd}, B.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM , B.PROD_CD , B.PROD_NM , B.BARCD_CD , B.DISUSE_QTY , B.DISUSE_AMT FROM ( SELECT TSSD.DISUSE_DATE , TSSD.SEQ_NO , TSSD.DISUSE_TITLE , DECODE(TSSD.PROC_FG,'0','입력','확정') AS PROD_FG_NM , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD , SUM(TSSDD.DISUSE_QTY) AS DISUSE_QTY , SUM(TSSDD.DISUSE_AMT) AS DISUSE_AMT FROM TB_ST_STORE_DISUSE TSSD , TB_ST_STORE_DISUSE_DTL TSSDD , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TSSD.STORE_CD = #{storeCd} AND TMP.STOCK_PROD_YN = 'Y'
AND TSSD.DISUSE_DATE = #{saleDate}
AND TSSD.STORE_CD = TSSDD.STORE_CD AND TSSD.DISUSE_DATE = TSSDD.DISUSE_DATE AND TSSD.SEQ_NO = TSSDD.SEQ_NO AND TSSDD.STORE_CD = TMP.STORE_CD AND TSSDD.PROD_CD = TMP.PROD_CD AND TSSDD.STORE_CD = TMPB.STORE_CD(+) AND TSSDD.PROD_CD = TMPB.PROD_CD(+) GROUP BY TSSD.DISUSE_DATE , TSSD.SEQ_NO , TSSD.DISUSE_TITLE , TSSD.PROC_FG , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD ) B ORDER BY B.PROD_CD

/* USE : StockComPopupMapper.getDailyIoStockStoreInfoListAdjQty, 재고조정 */ SELECT B.ADJ_DATE , B.SEQ_NO , B.ADJ_TITLE AS TITLE , B.PROD_FG_NM , FN_GET_PROD_CLASS_CD_NM_STR(#{storeCd}, B.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM , B.PROD_CD , B.PROD_NM , B.BARCD_CD , B.ADJ_QTY , B.ADJ_AMT FROM ( SELECT TSSA.ADJ_DATE , TSSA.SEQ_NO , TSSA.ADJ_TITLE , DECODE(TSSA.PROC_FG,'0','입력','확정') AS PROD_FG_NM , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD , SUM(TSSAD.ADJ_QTY) AS ADJ_QTY , SUM(TSSAD.ADJ_AMT) AS ADJ_AMT FROM TB_ST_STORE_ADJUST TSSA , TB_ST_STORE_ADJUST_DTL TSSAD , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TSSA.STORE_CD = #{storeCd} AND TMP.STOCK_PROD_YN = 'Y'
AND TSSA.ADJ_DATE = #{saleDate}
AND TSSA.STORE_CD = TSSAD.STORE_CD AND TSSA.ADJ_DATE = TSSAD.ADJ_DATE AND TSSA.SEQ_NO = TSSAD.SEQ_NO AND TSSAD.STORE_CD = TMP.STORE_CD AND TSSAD.PROD_CD = TMP.PROD_CD AND TSSAD.STORE_CD = TMPB.STORE_CD(+) AND TSSAD.PROD_CD = TMPB.PROD_CD(+) GROUP BY TSSA.ADJ_DATE , TSSA.SEQ_NO , TSSA.ADJ_TITLE , TSSA.PROC_FG , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD ) B ORDER BY B.PROD_CD

mapper.sql:
    /* USE : StockComPopupMapper.hqProdClassList  본사 상품분류 */
        FROM    (

							SELECT  A.HQ_OFFICE_CD
							,       A.PROD_CLASS_CD
							,		NULL	LV1_CD
							,		NULL	LV2_CD
							,		NULL	LV3_CD
							,       DECODE	(A.LV ,1  ,SUBSTR(A.AC,INSTR(A.AC,'^',1,1)+1,LENGTH(A.AC))
							                    ,    SUBSTR(A.AC,INSTR(A.AC,'^',1,1)+1,INSTR(A.AC,'^',2,1)-2)
							             	)  AS  LV1_NM
							,       DECODE	(A.LV ,1  ,NULL
							                    ,2  ,SUBSTR(A.AC,INSTR(A.AC,'^',2,1)+1,LENGTH(A.AC))
							                    ,3  ,SUBSTR(A.AC,INSTR(A.AC,'^',1,2)+1,(INSTR(A.AC,'^',1,3)-1)-INSTR(A.AC,'^',1,2))
							              	)  AS  LV2_NM
							,       DECODE	(A.LV ,1  ,NULL
							                    ,2  ,NULL
							                    ,3  ,SUBSTR(A.AC,INSTR(A.AC,'^',1,3)+1)
							             	)  AS  LV3_NM
							FROM    (
							        SELECT  LEVEL LV,  HQ_OFFICE_CD,    PROD_CLASS_CD,   SYS_CONNECT_BY_PATH(PROD_CLASS_NM, '^')  AS AC
							        FROM    (
							                    SELECT  HQ_OFFICE_CD,   PROD_CLASS_CD, PROD_CLASS_NM, P_PROD_CLASS_CD
							                    FROM    TB_HQ_PRODUCT_CLASS
							                    WHERE   HQ_OFFICE_CD    =   #{hqOfficeCd}
							                )
							        START WITH  P_PROD_CLASS_CD ='00000'
                                    AND         HQ_OFFICE_CD    =   #{hqOfficeCd}
							        CONNECT BY
							        PRIOR       PROD_CLASS_CD   =   P_PROD_CLASS_CD
                                    AND         HQ_OFFICE_CD    =   #{hqOfficeCd}
							        )   A

                )   A

/* USE : StockComPopupMapper.storeProdClassList 매장 상품분류 */ FROM (
SELECT A.STORE_CD , A.PROD_CLASS_CD , NULL LV1_CD , NULL LV2_CD , NULL LV3_CD , DECODE(A.LV ,1 ,SUBSTR(A.AC,INSTR(A.AC,'^',1,1)+1,LENGTH(A.AC)) , SUBSTR(A.AC,INSTR(A.AC,'^',1,1)+1,INSTR(A.AC,'^',2,1)-2)) AS LV1_NM , DECODE(A.LV ,1 ,NULL ,2 ,SUBSTR(A.AC,INSTR(A.AC,'^',2,1)+1,LENGTH(A.AC)) ,3 ,SUBSTR(A.AC,INSTR(A.AC,'^',1,2)+1,(INSTR(A.AC,'^',1,3)-1)-INSTR(A.AC,'^',1,2)) ) AS LV2_NM , DECODE(A.LV ,1 ,NULL ,2 ,NULL ,3 ,SUBSTR(A.AC,INSTR(A.AC,'^',1,3)+1)) AS LV3_NM FROM ( SELECT LEVEL LV, STORE_CD, PROD_CLASS_CD, SYS_CONNECT_BY_PATH(PROD_CLASS_NM, '^') AS AC FROM ( SELECT STORE_CD, PROD_CLASS_CD, PROD_CLASS_NM, P_PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} ) START WITH P_PROD_CLASS_CD ='00000' AND STORE_CD = #{storeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = #{storeCd} ) A
) A

/* USE : StockComPopupMapper.getDailyIoStockStoreFrom3,12 매장입고,매장반품 */ , ( SELECT TPHSO.SLIP_NO /* 전표번호 */ , TPHSO.IN_DATE /* 입고일 */ , TPHSO.HQ_OFFICE_CD , (SELECT HQ_OFFICE_NM FROM TB_HQ_OFFICE Z WHERE Z.HQ_OFFICE_CD = TPHSO.HQ_OFFICE_CD) AS HQ_OFFICE_NM , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD , SUM(TPHSOD.IN_TOT_QTY) AS IN_TOT_QTY , SUM(TPHSOD.IN_TOT) AS IN_TOT FROM TB_PO_HQ_STORE_OUTSTOCK TPHSO , TB_PO_HQ_STORE_OUTSTOCK_DTL TPHSOD , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TPHSO.STORE_CD = #{storeCd} AND TMP.STOCK_PROD_YN = 'Y'
AND TPHSO.SLIP_FG = '1' /* 입고:1, 출고:-1 */
AND TPHSO.SLIP_FG = '-1' /* 입고:1, 출고:-1 */
AND TPHSO.IN_DATE = #{saleDate}
AND TPHSO.HQ_OFFICE_CD = TPHSOD.HQ_OFFICE_CD AND TPHSO.SLIP_NO = TPHSOD.SLIP_NO AND TPHSOD.STORE_CD = TMP.STORE_CD AND TPHSOD.PROD_CD = TMP.PROD_CD AND TPHSOD.STORE_CD = TMPB.STORE_CD(+) AND TPHSOD.PROD_CD = TMPB.PROD_CD(+) GROUP BY TPHSO.SLIP_NO , TPHSO.IN_DATE , TPHSO.HQ_OFFICE_CD , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD ) B WHERE A.PROD_CLASS_CD(+) = B.PROD_CLASS_CD ORDER BY B.SLIP_NO, B.PROD_CD

/* USE : StockComPopupMapper.getDailyIoStockStoreFrom6,18 사입입고,사입반품 */ , ( SELECT TPSVI.SLIP_NO /* 전표번호 */ , TPSVI.INSTOCK_DATE , TPSVI.VENDR_CD , (SELECT VENDR_NM FROM TB_MS_VENDOR Z WHERE Z.STORE_CD = TPSVI.STORE_CD AND Z.VENDR_CD = TPSVI.VENDR_CD) AS VENDR_NM /* 거래처명 */ , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD , SUM(TPSVID.IN_TOT_QTY) AS IN_TOT_QTY , SUM(TPSVID.IN_TOT) AS IN_TOT FROM TB_PO_STORE_VENDR_INSTOCK TPSVI , TB_PO_STORE_VENDR_INSTOCK_DTL TPSVID , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TPSVI.STORE_CD = #{storeCd} AND TMP.STOCK_PROD_YN = 'Y'
AND TPSVI.SLIP_FG = '1' /* 입고:1, 출고:-1 */
AND TPSVI.SLIP_FG = '-1' /* 입고:1, 출고:-1 */
AND TPSVI.INSTOCK_DATE = #{saleDate}
AND TPSVI.STORE_CD = TPSVID.STORE_CD AND TPSVI.SLIP_NO = TPSVID.SLIP_NO AND TPSVID.STORE_CD = TMP.STORE_CD AND TPSVID.PROD_CD = TMP.PROD_CD AND TPSVID.STORE_CD = TMPB.STORE_CD(+) AND TPSVID.PROD_CD = TMPB.PROD_CD(+) GROUP BY TPSVI.STORE_CD , TPSVI.SLIP_NO , TPSVI.INSTOCK_DATE , TPSVI.VENDR_CD , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD ) B WHERE A.PROD_CLASS_CD(+) = B.PROD_CLASS_CD ORDER BY B.SLIP_NO, B.PROD_CD

/* USE : StockComPopupMapper.getDailyIoStockStoreFrom14 매장이입 */ , ( SELECT TPSM.SLIP_NO /* 전표번호 */ , TO_CHAR(TO_DATE(TPSM.IN_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') AS IN_CONFM_DT , TPSM.HQ_OFFICE_CD , (SELECT HQ_OFFICE_NM FROM TB_HQ_OFFICE Z WHERE Z.HQ_OFFICE_CD = TPSM.HQ_OFFICE_CD) AS HQ_OFFICE_NM , THP.PROD_CLASS_CD , THP.PROD_CD , THP.PROD_NM , THPB.BARCD_CD , SUM(TPSMD.IN_TOT_QTY) AS IN_TOT_QTY , SUM(TPSMD.IN_TOT) AS IN_TOT FROM TB_PO_STORE_MOVE TPSM , TB_PO_STORE_MOVE_DTL TPSMD , TB_HQ_PRODUCT THP , TB_HQ_PRODUCT_BARCD THPB WHERE TPSM.HQ_OFFICE_CD = #{hqOfficeCd} AND TPSM.IN_STORE_CD = #{storeCd} AND THP.STOCK_PROD_YN = 'Y'
AND TPSM.MOVE_DATE = #{saleDate}
AND TPSM.HQ_OFFICE_CD = TPSMD.HQ_OFFICE_CD AND TPSM.SLIP_NO = TPSMD.SLIP_NO AND TPSMD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TPSMD.PROD_CD = THP.PROD_CD AND TPSMD.HQ_OFFICE_CD = THPB.HQ_OFFICE_CD(+) AND TPSMD.PROD_CD = THPB.PROD_CD(+) GROUP BY TPSM.HQ_OFFICE_CD , TPSM.SLIP_NO , TO_CHAR(TO_DATE(TPSM.IN_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') , TPSM.IN_STORE_CD , THP.PROD_CLASS_CD , THP.PROD_CD , THP.PROD_NM , THPB.BARCD_CD ) B WHERE A.PROD_CLASS_CD(+) = B.PROD_CLASS_CD ORDER BY B.SLIP_NO, B.PROD_CD

/* USE : StockComPopupMapper.getDailyIoStockStoreFrom17 매장이출 */ , ( SELECT TPSM.SLIP_NO /* 전표번호 */ , TO_CHAR(TO_DATE(TPSM.OUT_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') AS OUT_CONFM_DT , TPSM.HQ_OFFICE_CD , (SELECT HQ_OFFICE_NM FROM TB_HQ_OFFICE Z WHERE Z.HQ_OFFICE_CD = TPSM.HQ_OFFICE_CD) AS HQ_OFFICE_NM , THP.PROD_CLASS_CD , THP.PROD_CD , THP.PROD_NM , THPB.BARCD_CD , SUM(TPSMD.OUT_TOT_QTY) AS OUT_TOT_QTY , SUM(TPSMD.OUT_TOT) AS OUT_TOT FROM TB_PO_STORE_MOVE TPSM , TB_PO_STORE_MOVE_DTL TPSMD , TB_HQ_PRODUCT THP , TB_HQ_PRODUCT_BARCD THPB WHERE TPSM.HQ_OFFICE_CD = #{hqOfficeCd} AND TPSM.OUT_TOT_QTY = #{storeCd} AND THP.STOCK_PROD_YN = 'Y'
AND TPSM.MOVE_DATE = #{saleDate}
AND TPSM.HQ_OFFICE_CD = TPSMD.HQ_OFFICE_CD AND TPSM.SLIP_NO = TPSMD.SLIP_NO AND TPSMD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TPSMD.PROD_CD = THP.PROD_CD AND TPSMD.HQ_OFFICE_CD = THPB.HQ_OFFICE_CD(+) AND TPSMD.PROD_CD = THPB.PROD_CD(+) GROUP BY TPSM.HQ_OFFICE_CD , TPSM.SLIP_NO , TO_CHAR(TO_DATE(TPSM.OUT_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') , TPSM.OUT_STORE_CD , THP.PROD_CLASS_CD , THP.PROD_CD , THP.PROD_NM , THPB.BARCD_CD ) B WHERE A.PROD_CLASS_CD(+) = B.PROD_CLASS_CD ORDER BY B.SLIP_NO, B.PROD_CD

/* USE : StockComPopupMapper.getDailyIoStockStoreFrom21 재고폐기 */ , ( SELECT TSSD.DISUSE_DATE , TSSD.SEQ_NO , TSSD.DISUSE_TITLE , DECODE(TSSD.PROC_FG,'0','입력','확정') AS PROD_FG_NM , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD , SUM(TSSDD.DISUSE_QTY) AS DISUSE_QTY , SUM(TSSDD.DISUSE_AMT) AS DISUSE_AMT FROM TB_ST_STORE_DISUSE TSSD , TB_ST_STORE_DISUSE_DTL TSSDD , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TSSD.STORE_CD = #{storeCd} AND TMP.STOCK_PROD_YN = 'Y'
AND TSSD.DISUSE_DATE = #{saleDate}
