/* USE : StockComPopupMapper.getDailyIoStockStoreFrom22 재고조정 */ , ( SELECT TSSA.ADJ_DATE , TSSA.SEQ_NO , TSSA.ADJ_TITLE , DECODE(TSSA.PROC_FG,'0','입력','확정') AS PROD_FG_NM , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD , SUM(TSSAD.ADJ_QTY) AS ADJ_QTY , SUM(TSSAD.ADJ_AMT) AS ADJ_AMT FROM TB_ST_STORE_ADJUST TSSA , TB_ST_STORE_ADJUST_DTL TSSAD , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TSSA.STORE_CD = #{storeCd} AND TMP.STOCK_PROD_YN = 'Y'
AND TSSA.ADJ_DATE = #{saleDate}
AND TSSA.STORE_CD = TSSAD.STORE_CD AND TSSA.ADJ_DATE = TSSAD.ADJ_DATE AND TSSA.SEQ_NO = TSSAD.SEQ_NO AND TSSAD.STORE_CD = TMP.STORE_CD AND TSSAD.PROD_CD = TMP.PROD_CD AND TSSAD.STORE_CD = TMPB.STORE_CD(+) AND TSSAD.PROD_CD = TMPB.PROD_CD(+) GROUP BY TSSA.ADJ_DATE , TSSA.SEQ_NO , TSSA.ADJ_TITLE , TSSA.PROC_FG , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD ) B WHERE A.PROD_CLASS_CD(+) = B.PROD_CLASS_CD ORDER BY B.PROD_CD

/* USE : StockComPopupMapper.getDailyIoStockStoreFrom19 세트생성 */ , ( SELECT TSSSC.SET_DATE , TSSSC.SEQ_NO , DECODE(TSSSC.PROC_FG,'0','미처리','처리완료') AS PROD_FG_NM , DECODE(TSSSC.SET_MAKE_FG,'1','구성','2','해체') AS SET_MAKE_FG_NM , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD , TSSSCD.COMPST_PROD_CD , SUM(TSSSCD.COMPST_PROD_QTY) AS COMPST_PROD_QTY FROM TB_ST_STORE_SETPROD_COMPOSITION TSSSC , TB_ST_STORE_SETPROD_COMPOSITION_DTL TSSSCD , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TSSSC.STORE_CD = #{storeCd} AND TMP.STOCK_PROD_YN = 'Y'
AND TSSSC.SET_DATE = #{saleDate}
AND TSSSC.STORE_CD = TSSSCD.STORE_CD AND TSSSC.SET_DATE = TSSSCD.SET_DATE AND TSSSC.SEQ_NO = TSSSCD.SEQ_NO AND TSSSC.STORE_CD = TMP.STORE_CD AND TSSSC.PROD_CD = TMP.PROD_CD AND TSSSC.STORE_CD = TMPB.STORE_CD(+) AND TSSSC.PROD_CD = TMPB.PROD_CD(+) GROUP BY TSSSC.SET_DATE , TSSSC.SEQ_NO , TSSSC.PROC_FG , TSSSC.SET_MAKE_FG , TMP.PROD_CLASS_CD , TMP.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD , TSSSCD.COMPST_PROD_CD ) B WHERE A.PROD_CLASS_CD(+) = B.PROD_CLASS_CD ORDER BY B.PROD_CD

mapper.select:
/* USE : StockComPopupMapper.getVendrInQtyDtlList, 본사입고 */ SELECT TPHVI.SLIP_NO /* 전표번호 */ , TO_CHAR(TO_DATE(TPHVI.INSTOCK_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS INSTOCK_DATE /* 입고일 */ , TPHVI.VENDR_CD /* 거래처코드 */ , (SELECT VENDR_NM FROM TB_HQ_VENDOR Z WHERE Z.HQ_OFFICE_CD = #{hqOfficeCd} AND Z.HQ_BRAND_CD = LPAD(TPHVID.HQ_BRAND_CD,7,'0') AND Z.VENDR_CD = TPHVI.VENDR_CD) AS VENDR_NM /* 거래처명 */ , SUM(TPHVID.IN_TOT_QTY) AS IN_TOT_QTY /* 수량 */ , SUM(TPHVID.IN_TOT) AS IN_TOT /* 금액 */ FROM TB_PO_HQ_VENDR_INSTOCK TPHVI , TB_PO_HQ_VENDR_INSTOCK_DTL TPHVID , TB_HQ_PRODUCT THP WHERE TPHVI.HQ_OFFICE_CD = #{hqOfficeCd} AND TPHVI.SLIP_FG = '1' /* 입고:1, 출고:-1 */
AND TPHVID.PROD_CD = #{prodCd} /* 상품코드 */
AND TPHVI.INSTOCK_DATE BETWEEN #{startDate} AND #{endDate}
AND TPHVI.HQ_OFFICE_CD = TPHVID.HQ_OFFICE_CD AND TPHVI.SLIP_NO = TPHVID.SLIP_NO AND THP.STOCK_PROD_YN = 'Y' AND TPHVID.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TPHVID.PROD_CD = THP.PROD_CD GROUP BY TPHVID.HQ_BRAND_CD , TPHVI.SLIP_NO , TPHVI.INSTOCK_DATE , TPHVI.VENDR_CD ORDER BY TPHVI.SLIP_NO, TPHVI.INSTOCK_DATE DESC

/* USE : StockComPopupMapper.getVendrOutQtyDtlList, 업체반출 */ SELECT TPHVI.SLIP_NO /* 전표번호 */ , TO_CHAR(TO_DATE(TPHVI.INSTOCK_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS RTN_DATE /* 반출일 */ , TPHVI.VENDR_CD /* 거래처코드 */ , (SELECT VENDR_NM FROM TB_HQ_VENDOR Z WHERE Z.HQ_OFFICE_CD = #{hqOfficeCd} AND Z.HQ_BRAND_CD = LPAD(TPHVID.HQ_BRAND_CD,7,'0') AND Z.VENDR_CD = TPHVI.VENDR_CD) AS VENDR_NM /* 거래처명 */ , SUM(TPHVID.IN_TOT_QTY * TPHVI.SLIP_FG) AS IN_TOT_QTY /* 수량 */ , SUM(TPHVID.IN_TOT * TPHVI.SLIP_FG) AS IN_TOT /* 금액 */ FROM TB_PO_HQ_VENDR_INSTOCK TPHVI , TB_PO_HQ_VENDR_INSTOCK_DTL TPHVID , TB_HQ_PRODUCT THP WHERE TPHVI.HQ_OFFICE_CD = #{hqOfficeCd} AND TPHVI.SLIP_FG = '-1' /* 입고:1, 출고:-1 */
AND TPHVID.PROD_CD = #{prodCd} /* 상품코드 */
AND TPHVI.INSTOCK_DATE BETWEEN #{startDate} AND #{endDate}
AND TPHVI.HQ_OFFICE_CD = TPHVID.HQ_OFFICE_CD AND TPHVI.SLIP_NO = TPHVID.SLIP_NO AND THP.STOCK_PROD_YN = 'Y' AND TPHVID.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TPHVID.PROD_CD = THP.PROD_CD GROUP BY TPHVID.HQ_BRAND_CD , TPHVI.SLIP_NO , TPHVI.INSTOCK_DATE , TPHVI.VENDR_CD ORDER BY TPHVI.SLIP_NO, TPHVI.INSTOCK_DATE DESC

/* USE : StockComPopupMapper.getMoveInQtyDtlList, 매장이입 */ SELECT TPSM.SLIP_NO , TO_CHAR(TO_DATE(TPSM.IN_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') AS IN_CONFM_DT
, TPSM.IN_STORE_CD , (SELECT STORE_NM FROM TB_MS_STORE Z WHERE Z.STORE_CD = TPSM.IN_STORE_CD) AS STORE_NM
, TPSM.HQ_OFFICE_CD , (SELECT HQ_OFFICE_NM FROM TB_HQ_OFFICE Z WHERE Z.HQ_OFFICE_CD = TPSM.HQ_OFFICE_CD) AS HQ_OFFICE_NM
, SUM(TPSMD.IN_TOT_QTY) AS IN_TOT_QTY , SUM(TPSMD.IN_TOT) AS IN_TOT FROM TB_PO_STORE_MOVE TPSM , TB_PO_STORE_MOVE_DTL TPSMD , TB_HQ_PRODUCT THP WHERE TPSM.HQ_OFFICE_CD = #{hqOfficeCd}
AND TPSM.IN_STORE_CD = #{storeCd}
AND TPSM.HQ_OFFICE_CD = TPSMD.HQ_OFFICE_CD AND TPSM.SLIP_NO = TPSMD.SLIP_NO
AND TPSMD.PROD_CD = #{prodCd}
AND TPSM.MOVE_DATE BETWEEN #{startDate} AND #{endDate}
AND THP.STOCK_PROD_YN = 'Y' AND TPSMD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TPSMD.PROD_CD = THP.PROD_CD GROUP BY TPSM.SLIP_NO , TO_CHAR(TO_DATE(TPSM.IN_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') , TPSM.IN_STORE_CD
, TPSM.HQ_OFFICE_CD
ORDER BY TPSM.SLIP_NO, TO_CHAR(TO_DATE(TPSM.IN_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') DESC

/* USE : StockComPopupMapper.getMoveOutQtyDtlList , 매장이출*/ SELECT TPSM.SLIP_NO , TO_CHAR(TO_DATE(TPSM.OUT_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') AS OUT_CONFM_DT
, TPSM.OUT_STORE_CD AS STORE_CD , (SELECT STORE_NM FROM TB_MS_STORE Z WHERE Z.STORE_CD = TPSM.OUT_STORE_CD) AS STORE_NM
, TPSM.HQ_OFFICE_CD , (SELECT HQ_OFFICE_NM FROM TB_HQ_OFFICE Z WHERE Z.HQ_OFFICE_CD = TPSM.HQ_OFFICE_CD) AS HQ_OFFICE_NM
, SUM(TPSMD.OUT_TOT_QTY) AS OUT_TOT_QTY , SUM(TPSMD.OUT_TOT) AS OUT_TOT FROM TB_PO_STORE_MOVE TPSM , TB_PO_STORE_MOVE_DTL TPSMD , TB_HQ_PRODUCT THP WHERE TPSM.HQ_OFFICE_CD = #{hqOfficeCd}
AND TPSM.OUT_STORE_CD = #{storeCd}
AND TPSM.HQ_OFFICE_CD = TPSMD.HQ_OFFICE_CD AND TPSM.SLIP_NO = TPSMD.SLIP_NO
AND TPSMD.PROD_CD = #{prodCd}
AND TPSM.MOVE_DATE BETWEEN #{startDate} AND #{endDate}
AND THP.STOCK_PROD_YN = 'Y' AND TPSMD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TPSMD.PROD_CD = THP.PROD_CD GROUP BY TPSM.SLIP_NO , TO_CHAR(TO_DATE(TPSM.OUT_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') , TPSM.OUT_STORE_CD
, TPSM.HQ_OFFICE_CD
ORDER BY TPSM.SLIP_NO, TO_CHAR(TO_DATE(TPSM.OUT_CONFM_DT,'YYYYMMDDHH24MISS'),'YYYY-MM-DD') DESC

/* USE : StockComPopupMapper.getDisuseQtyDtlList, 재고폐기 */
SELECT TSHD.DISUSE_DATE , TSHD.SEQ_NO , TSHD.DISUSE_TITLE , DECODE(TSHD.PROC_FG,'0','입력','확정') AS PROD_FG_NM , SUM(TSHDD.DISUSE_QTY) AS DISUSE_QTY , SUM(TSHDD.DISUSE_AMT) AS DISUSE_AMT FROM TB_ST_HQ_DISUSE TSHD , TB_ST_HQ_DISUSE_DTL TSHDD , TB_HQ_PRODUCT THP WHERE TSHD.HQ_OFFICE_CD = #{hqOfficeCd} AND TSHD.HQ_OFFICE_CD = TSHDD.HQ_OFFICE_CD AND TSHD.DISUSE_DATE = TSHDD.DISUSE_DATE AND TSHD.SEQ_NO = TSHDD.SEQ_NO
AND TSHDD.PROD_CD = #{prodCd} /* 상품코드 */
AND TSHD.DISUSE_DATE BETWEEN #{startDate} AND #{endDate}
AND THP.STOCK_PROD_YN = 'Y' AND TSHDD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TSHDD.PROD_CD = THP.PROD_CD GROUP BY TSHD.DISUSE_DATE , TSHD.SEQ_NO , TSHD.DISUSE_TITLE , TSHD.PROC_FG ORDER BY TSHD.DISUSE_DATE DESC
SELECT TSSD.DISUSE_DATE , TSSD.SEQ_NO , TSSD.DISUSE_TITLE AS TITLE , DECODE(TSSD.PROC_FG,'0','입력','확정') AS PROD_FG_NM , SUM(TSSDD.DISUSE_QTY) AS DISUSE_QTY , SUM(TSSDD.DISUSE_AMT) AS DISUSE_AMT FROM TB_ST_STORE_DISUSE TSSD , TB_ST_STORE_DISUSE_DTL TSSDD , TB_MS_PRODUCT TMP WHERE TSSD.STORE_CD = #{storeCd} AND TSSD.STORE_CD = TSSDD.STORE_CD AND TSSD.DISUSE_DATE = TSSDD.DISUSE_DATE AND TSSD.SEQ_NO = TSSDD.SEQ_NO
AND TSSDD.PROD_CD = #{prodCd} /* 상품코드 */
AND TSSD.DISUSE_DATE BETWEEN #{startDate} AND #{endDate}
AND TMP.STOCK_PROD_YN = 'Y' AND TSSDD.STORE_CD = TMP.STORE_CD AND TSSDD.PROD_CD = TMP.PROD_CD GROUP BY TSSD.DISUSE_DATE , TSSD.SEQ_NO , TSSD.DISUSE_TITLE , TSSD.PROC_FG ORDER BY TSSD.DISUSE_DATE DESC

/* USE : StockComPopupMapper.getAdjQtyDtlList, 재고조정 */
SELECT TSHAI.ACINS_DATE AS ADJ_DATE , TSHAI.SEQ_NO , TSHAI.ACINS_TITLE AS TITLE , DECODE(TSHAI.PROC_FG,'0','입력','확정') AS PROD_FG_NM , SUM(TSHAID.ADJ_QTY) AS ADJ_QTY , SUM(TSHAID.ADJ_AMT) AS ADJ_AMT FROM TB_ST_HQ_ACTUAL_INSPECTION TSHAI , TB_ST_HQ_ACTUAL_INSPECTION_DTL TSHAID , TB_HQ_PRODUCT THP WHERE TSHAI.HQ_OFFICE_CD = #{hqOfficeCd} AND TSHAI.HQ_OFFICE_CD = TSHAID.HQ_OFFICE_CD AND TSHAI.ACINS_DATE = TSHAID.ACINS_DATE AND TSHAI.SEQ_NO = TSHAID.SEQ_NO AND TSHAI.PROC_FG = '1'
AND TSHAID.PROD_CD = #{prodCd} /* 상품코드 */
AND TSHAI.ACINS_DATE BETWEEN #{startDate} AND #{endDate}
AND THP.STOCK_PROD_YN = 'Y' AND TSHAID.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TSHAID.PROD_CD = THP.PROD_CD GROUP BY TSHAI.ACINS_DATE , TSHAI.SEQ_NO , TSHAI.ACINS_TITLE , TSHAI.PROC_FG UNION ALL SELECT TSHA.ADJ_DATE , TSHA.SEQ_NO , TSHA.ADJ_TITLE , DECODE(TSHA.PROC_FG,'0','입력','확정') AS PROD_FG_NM , SUM(TSHAD.ADJ_QTY) AS ADJ_QTY , SUM(TSHAD.ADJ_AMT) AS ADJ_AMT FROM TB_ST_HQ_ADJUST TSHA , TB_ST_HQ_ADJUST_DTL TSHAD , TB_HQ_PRODUCT THP WHERE TSHA.HQ_OFFICE_CD = #{hqOfficeCd} AND TSHA.HQ_OFFICE_CD = TSHAD.HQ_OFFICE_CD AND TSHA.ADJ_DATE = TSHAD.ADJ_DATE AND TSHA.SEQ_NO = TSHAD.SEQ_NO AND TSHA.PROC_FG = '1'
AND TSHAD.PROD_CD = #{prodCd} /* 상품코드 */
AND TSHA.ADJ_DATE BETWEEN #{startDate} AND #{endDate}
AND THP.STOCK_PROD_YN = 'Y' AND TSHAD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TSHAD.PROD_CD = THP.PROD_CD GROUP BY TSHA.ADJ_DATE , TSHA.SEQ_NO , TSHA.ADJ_TITLE , TSHA.PROC_FG ORDER BY ADJ_DATE DESC
SELECT TSSAI.ACINS_DATE AS ADJ_DATE , TSSAI.SEQ_NO , TSSAI.ACINS_TITLE AS TITLE , DECODE(TSSAI.PROC_FG,'0','입력','확정') AS PROD_FG_NM , SUM(TSSAID.ADJ_QTY) AS ADJ_QTY , SUM(TSSAID.ADJ_AMT) AS ADJ_AMT FROM TB_ST_STORE_ACTUAL_INSPECTION TSSAI , TB_ST_STORE_ACTUAL_INSPECTION_DTL TSSAID , TB_MS_PRODUCT TMP WHERE TSSAI.STORE_CD = #{storeCd} AND TSSAI.STORE_CD = TSSAID.STORE_CD AND TSSAI.ACINS_DATE = TSSAID.ACINS_DATE AND TSSAI.SEQ_NO = TSSAID.SEQ_NO AND TSSAI.PROC_FG = '1'
AND TSSAID.PROD_CD = #{prodCd} /* 상품코드 */
AND TSSAI.ACINS_DATE BETWEEN #{startDate} AND #{endDate}
AND TMP.STOCK_PROD_YN = 'Y' AND TSSAID.STORE_CD = TMP.STORE_CD AND TSSAID.PROD_CD = TMP.PROD_CD GROUP BY TSSAI.ACINS_DATE , TSSAI.SEQ_NO , TSSAI.ACINS_TITLE , TSSAI.PROC_FG UNION ALL SELECT TSSA.ADJ_DATE , TSSA.SEQ_NO , TSSA.ADJ_TITLE AS TITLE , DECODE(TSSA.PROC_FG,'0','입력','확정') AS PROD_FG_NM , SUM(TSSAD.ADJ_QTY) AS ADJ_QTY , SUM(TSSAD.ADJ_AMT) AS ADJ_AMT FROM TB_ST_STORE_ADJUST TSSA , TB_ST_STORE_ADJUST_DTL TSSAD , TB_MS_PRODUCT TMP WHERE TSSA.STORE_CD = #{storeCd} AND TSSA.STORE_CD = TSSAD.STORE_CD AND TSSA.ADJ_DATE = TSSAD.ADJ_DATE AND TSSA.SEQ_NO = TSSAD.SEQ_NO AND TSSA.PROC_FG = '1'
AND TSSAD.PROD_CD = #{prodCd} /* 상품코드 */
AND TSSA.ADJ_DATE BETWEEN #{startDate} AND #{endDate}
AND TMP.STOCK_PROD_YN = 'Y' AND TSSAD.STORE_CD = TMP.STORE_CD AND TSSAD.PROD_CD = TMP.PROD_CD GROUP BY TSSA.ADJ_DATE , TSSA.SEQ_NO , TSSA.ADJ_TITLE , TSSA.PROC_FG ORDER BY ADJ_DATE DESC

/* USE : StockComPopupMapper.getStoreInQtyDtlList, 매장입고 */ SELECT TPHSO.SLIP_NO , TPHSO.IN_DATE AS INSTOCK_DATE , TPHSO.HQ_OFFICE_CD , (SELECT HQ_OFFICE_NM FROM TB_HQ_OFFICE Z WHERE Z.HQ_OFFICE_CD = TPHSO.HQ_OFFICE_CD) AS HQ_OFFICE_NM , SUM(TPHSOD.IN_TOT_QTY) AS IN_TOT_QTY , SUM(TPHSOD.IN_TOT) AS IN_TOT FROM TB_PO_HQ_STORE_OUTSTOCK TPHSO , TB_PO_HQ_STORE_OUTSTOCK_DTL TPHSOD , TB_HQ_PRODUCT THP WHERE TPHSO.STORE_CD = #{storeCd} AND TPHSO.SLIP_FG = '1' /* 주문:1, 반품:-1 */ AND TPHSO.HQ_OFFICE_CD = TPHSOD.HQ_OFFICE_CD AND TPHSO.SLIP_NO = TPHSOD.SLIP_NO
AND TPHSOD.PROD_CD = #{prodCd} /* 상품코드 */
AND TPHSO.OUT_DATE BETWEEN #{startDate} AND #{endDate}
