/* USE : StockComPopupMapper.getStoreOutQtyDtlList, 매장반품 */ SELECT TPHSO.SLIP_NO , TPHSO.OUT_DATE AS CARRY_OUT_DATE , TPHSO.HQ_OFFICE_CD , (SELECT HQ_OFFICE_NM FROM TB_HQ_OFFICE Z WHERE Z.HQ_OFFICE_CD = TPHSO.HQ_OFFICE_CD) AS HQ_OFFICE_NM , SUM(TPHSOD.OUT_TOT_QTY * TPHSO.SLIP_FG) AS IN_TOT_QTY , SUM(TPHSOD.OUT_TOT * TPHSO.SLIP_FG) AS IN_TOT FROM TB_PO_HQ_STORE_OUTSTOCK TPHSO , TB_PO_HQ_STORE_OUTSTOCK_DTL TPHSOD , TB_HQ_PRODUCT THP WHERE TPHSO.STORE_CD = #{storeCd} AND TPHSO.SLIP_FG = '-1' /* 주문:1, 반품:-1 */ AND TPHSO.SLIP_KIND = '0' AND TPHSO.PROC_FG IN ('20', '30') AND TPHSO.HQ_OFFICE_CD = TPHSOD.HQ_OFFICE_CD AND TPHSO.SLIP_NO = TPHSOD.SLIP_NO
AND TPHSOD.PROD_CD = #{prodCd} /* 상품코드 */
AND TPHSO.OUT_DATE BETWEEN #{startDate} AND #{endDate}
AND THP.STOCK_PROD_YN = 'Y' AND TPHSOD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TPHSOD.PROD_CD = THP.PROD_CD GROUP BY TPHSO.SLIP_NO , TPHSO.IN_DATE , TPHSO.HQ_OFFICE_CD ORDER BY TPHSO.SLIP_NO, TPHSO.IN_DATE DESC

/* USE : StockComPopupMapper.getPurchsInQtyDtlList, 사입입고 */ SELECT TPSVI.SLIP_NO , TPSVI.INSTOCK_DATE AS INSTOCK_DATE , TPSVI.VENDR_CD , (SELECT VENDR_NM FROM TB_MS_VENDOR Z WHERE Z.STORE_CD = TPSVI.STORE_CD AND Z.VENDR_CD = TPSVI.VENDR_CD) AS VENDR_NM /* 거래처명 */ , SUM(TPSVID.IN_TOT_QTY) AS IN_TOT_QTY , SUM(TPSVID.IN_TOT) AS IN_TOT FROM TB_PO_STORE_VENDR_INSTOCK TPSVI , TB_PO_STORE_VENDR_INSTOCK_DTL TPSVID , TB_MS_PRODUCT TMP WHERE TPSVI.STORE_CD = #{storeCd} AND TPSVI.SLIP_FG = '1' AND TPSVI.STORE_CD = TPSVID.STORE_CD AND TPSVI.SLIP_NO = TPSVID.SLIP_NO
AND TPSVID.PROD_CD = #{prodCd} /* 상품코드 */
AND TPSVI.INSTOCK_DATE BETWEEN #{startDate} AND #{endDate}
AND TMP.STOCK_PROD_YN = 'Y' AND TPSVID.STORE_CD = TMP.STORE_CD AND TPSVID.PROD_CD = TMP.PROD_CD GROUP BY TPSVI.STORE_CD , TPSVI.SLIP_NO , TPSVI.INSTOCK_DATE , TPSVI.VENDR_CD ORDER BY TPSVI.SLIP_NO, TPSVI.INSTOCK_DATE DESC

/* USE : StockComPopupMapper.getPurchsOutQtyDtlList, 사입반품 */ SELECT TPSVI.SLIP_NO , TPSVI.INSTOCK_DATE AS CARRY_OUT_DATE , TPSVI.VENDR_CD , (SELECT VENDR_NM FROM TB_MS_VENDOR Z WHERE Z.STORE_CD = TPSVI.STORE_CD AND Z.VENDR_CD = TPSVI.VENDR_CD) AS VENDR_NM /* 거래처명 */ , SUM(TPSVID.OUT_TOT_QTY * TPSVI.SLIP_FG) AS IN_TOT_QTY , SUM(TPSVID.OUT_TOT * TPSVI.SLIP_FG) AS IN_TOT FROM TB_PO_STORE_VENDR_INSTOCK TPSVI , TB_PO_STORE_VENDR_INSTOCK_DTL TPSVID , TB_MS_PRODUCT TMP WHERE TPSVI.STORE_CD = #{storeCd} AND TPSVI.SLIP_FG = '-1' AND TPSVI.STORE_CD = TPSVID.STORE_CD AND TPSVI.SLIP_NO = TPSVID.SLIP_NO
AND TPSVID.PROD_CD = #{prodCd} /* 상품코드 */
AND TPSVI.INSTOCK_DATE BETWEEN #{startDate} AND #{endDate}
AND TMP.STOCK_PROD_YN = 'Y' AND TPSVID.STORE_CD = TMP.STORE_CD AND TPSVID.PROD_CD = TMP.PROD_CD GROUP BY TPSVI.STORE_CD , TPSVI.SLIP_NO , TPSVI.INSTOCK_DATE , TPSVI.VENDR_CD ORDER BY TPSVI.SLIP_NO, TPSVI.INSTOCK_DATE DESC

/* USE : StockComPopupMapper.getStoreSaleQtyDtlList, 매장판매 */ SELECT TSDP.STORE_CD , (SELECT STORE_NM FROM TB_MS_STORE Z WHERE Z.STORE_CD = TSDP.STORE_CD) AS STORE_NM , TO_CHAR(TO_DATE(TSDP.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , SUM(TSDP.TOT_SALE_QTY) AS IN_TOT_QTY , SUM(TSDP.REAL_SALE_AMT) AS IN_TOT FROM TB_SL_DAILY_PROD TSDP , TB_MS_PRODUCT TMP WHERE TSDP.STORE_CD = #{storeCd}
AND TSDP.PROD_CD = #{prodCd} /* 상품코드 */
AND TSDP.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND TMP.STOCK_PROD_YN = 'Y' AND TSDP.STORE_CD = TMP.STORE_CD AND TSDP.PROD_CD = TMP.PROD_CD GROUP BY TSDP.STORE_CD , TSDP.SALE_DATE ORDER BY TSDP.STORE_CD , TSDP.SALE_DATE DESC

mapper.sql:
(
SELECT A.HQ_OFFICE_CD , A.PROD_CLASS_CD , NULL LV1_CD , NULL LV2_CD , NULL LV3_CD , DECODE (A.LV ,1 ,SUBSTR(A.AC,INSTR(A.AC,'^',1,1)+1,LENGTH(A.AC)) , SUBSTR(A.AC,INSTR(A.AC,'^',1,1)+1,INSTR(A.AC,'^',2,1)-2) ) AS LV1_NM , DECODE (A.LV ,1 ,NULL ,2 ,SUBSTR(A.AC,INSTR(A.AC,'^',2,1)+1,LENGTH(A.AC)) ,3 ,SUBSTR(A.AC,INSTR(A.AC,'^',1,2)+1,(INSTR(A.AC,'^',1,3)-1)-INSTR(A.AC,'^',1,2)) ) AS LV2_NM , DECODE (A.LV ,1 ,NULL ,2 ,NULL ,3 ,SUBSTR(A.AC,INSTR(A.AC,'^',1,3)+1) ) AS LV3_NM FROM ( SELECT LEVEL LV, HQ_OFFICE_CD, PROD_CLASS_CD, SYS_CONNECT_BY_PATH(PROD_CLASS_NM, '^') AS AC FROM ( SELECT HQ_OFFICE_CD, PROD_CLASS_CD, PROD_CLASS_NM, P_PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} ) START WITH P_PROD_CLASS_CD ='00000' AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} ) A
)

(
SELECT A.STORE_CD , A.PROD_CLASS_CD , NULL LV1_CD , NULL LV2_CD , NULL LV3_CD , DECODE (A.LV ,1 ,SUBSTR(A.AC,INSTR(A.AC,'^',1,1)+1,LENGTH(A.AC)) , SUBSTR(A.AC,INSTR(A.AC,'^',1,1)+1,INSTR(A.AC,'^',2,1)-2) ) AS LV1_NM , DECODE (A.LV ,1 ,NULL ,2 ,SUBSTR(A.AC,INSTR(A.AC,'^',2,1)+1,LENGTH(A.AC)) ,3 ,SUBSTR(A.AC,INSTR(A.AC,'^',1,2)+1,(INSTR(A.AC,'^',1,3)-1)-INSTR(A.AC,'^',1,2)) ) AS LV2_NM , DECODE (A.LV ,1 ,NULL ,2 ,NULL ,3 ,SUBSTR(A.AC,INSTR(A.AC,'^',1,3)+1) ) AS LV3_NM FROM ( SELECT LEVEL LV, STORE_CD, PROD_CLASS_CD, SYS_CONNECT_BY_PATH(PROD_CLASS_NM, '^') AS AC FROM ( SELECT STORE_CD, PROD_CLASS_CD, PROD_CLASS_NM, P_PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} ) START WITH P_PROD_CLASS_CD ='00000' AND STORE_CD = #{storeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = #{storeCd} ) A
)

/* USE : StockComPopupMapper.getCmmAcinsViewDtlList */
SELECT TSHAID.PROD_CD , THP.PROD_NM , THPB.BARCD_CD , THP.SPLY_UPRC , THP.COST_UPRC , TSHAID.PO_UNIT_QTY , TSHAID.CMPT_CURR_QTY , TSHAID.ACINS_QTY , TSHAID.ADJ_QTY , TSHAID.REMARK FROM TB_ST_HQ_ACTUAL_INSPECTION TSHAI , TB_ST_HQ_ACTUAL_INSPECTION_DTL TSHAID , TB_HQ_PRODUCT THP , TB_HQ_PRODUCT_BARCD THPB WHERE TSHAI.HQ_OFFICE_CD = #{hqOfficeCd} AND TSHAI.ACINS_DATE = #{totDate} AND TSHAI.SEQ_NO = #{seqNo} AND TSHAI.HQ_OFFICE_CD = TSHAID.HQ_OFFICE_CD AND TSHAI.ACINS_DATE = TSHAID.ACINS_DATE AND TSHAI.SEQ_NO = TSHAID.SEQ_NO AND TSHAID.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TSHAID.PROD_CD = THP.PROD_CD AND TSHAID.HQ_OFFICE_CD = THPB.HQ_OFFICE_CD(+) AND TSHAID.PROD_CD = THPB.PROD_CD(+)
SELECT TSSAID.PROD_CD , TMP.PROD_NM , TMPB.BARCD_CD , TMP.SPLY_UPRC , TMP.COST_UPRC , TSSAID.PO_UNIT_QTY , TSSAID.CMPT_CURR_QTY , TSSAID.ACINS_QTY , TSSAID.ADJ_QTY , TSSAID.REMARK FROM TB_ST_STORE_ACTUAL_INSPECTION TSSAI , TB_ST_STORE_ACTUAL_INSPECTION_DTL TSSAID , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TSSAI.STORE_CD = #{storeCd} AND TSSAI.ACINS_DATE = #{totDate} AND TSSAI.SEQ_NO = #{seqNo} AND TSSAI.STORE_CD = TSSAID.STORE_CD AND TSSAI.ACINS_DATE = TSSAID.ACINS_DATE AND TSSAI.SEQ_NO = TSSAID.SEQ_NO AND TSSAID.STORE_CD = TMP.STORE_CD AND TSSAID.PROD_CD = TMP.PROD_CD AND TSSAID.STORE_CD = TMPB.STORE_CD(+) AND TSSAID.PROD_CD = TMPB.PROD_CD(+)

/* USE : StockComPopupMapper.getCmmAdjViewDtlList */
SELECT TSHAD.PROD_CD , THP.PROD_NM , THPB.BARCD_CD , THP.SPLY_UPRC , THP.COST_UPRC , TSHAD.PO_UNIT_QTY , TSHAD.CURR_QTY , TSHAD.ADJ_QTY , TSHAD.REMARK FROM TB_ST_HQ_ADJUST TSHA , TB_ST_HQ_ADJUST_DTL TSHAD , TB_HQ_PRODUCT THP , TB_HQ_PRODUCT_BARCD THPB WHERE TSHA.HQ_OFFICE_CD = #{hqOfficeCd} AND TSHA.ADJ_DATE = #{totDate} AND TSHA.SEQ_NO = #{seqNo} AND TSHA.HQ_OFFICE_CD = TSHAD.HQ_OFFICE_CD AND TSHA.ADJ_DATE = TSHAD.ADJ_DATE AND TSHA.SEQ_NO = TSHAD.SEQ_NO AND TSHAD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TSHAD.PROD_CD = THP.PROD_CD AND TSHAD.HQ_OFFICE_CD = THPB.HQ_OFFICE_CD(+) AND TSHAD.PROD_CD = THPB.PROD_CD(+)
