mapper.select:
/* USE : IostockCmmMapper.selectStorageList */ SELECT STORAGE_CD, STORAGE_NM
FROM TB_HQ_STORAGE WHERE 1 = 1 AND HQ_OFFICE_CD = #{hqOfficeCd} AND USE_YN = 'Y' AND STORAGE_CD > '000' AND STORAGE_CD < '999' ORDER BY STORAGE_CD
FROM TB_MS_STORAGE WHERE 1 = 1 AND STORE_CD = #{storeCd} AND USE_YN = 'Y' AND STORAGE_CD >= '000' AND STORAGE_CD < '999' ORDER BY STORE_CD
