mapper.select:
/* USE : HqDisuseMapper.getHqDisuseList */

SELECT  tshd.DISUSE_DATE, tshd.HQ_OFFICE_CD, tshd.SEQ_NO
,       tshd.DISUSE_TITLE, tshd.PROC_FG, tshd.DTL_CNT
,       tshd.CONFM_DATE
,       SUBSTR(tshd.REG_DT,0,8) AS REG_DATE
FROM    TB_ST_HQ_DISUSE tshd
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     DISUSE_DATE  BETWEEN #{startDate} AND #{endDate}


    AND tshd.PROC_FG = #{procFg}


ORDER BY tshd.DISUSE_DATE DESC, tshd.SEQ_NO DESC

/* USE : HqDisuseMapper.getProcFgCheck */

SELECT  tshd.PROC_FG, tshd.DISUSE_TITLE
FROM    TB_ST_HQ_DISUSE tshd
WHERE   tshd.HQ_OFFICE_CD  = #{hqOfficeCd}
AND     tshd.DISUSE_DATE   = #{disuseDate}
AND     tshd.SEQ_NO        = #{seqNo}

/* USE : HqDisuseMapper.getHqDisuseRegistList */
SELECT thpb.BARCD_CD, thp.PROD_CD, thp.PROD_NM, thp.PO_UNIT_QTY , NVL((SELECT MAX(SALE_UPRC) KEEP(DENSE_RANK FIRST ORDER BY REG_DT DESC) FROM TB_HQ_PRODUCT_SALE_PRICE WHERE HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND PROD_CD = thp.PROD_CD AND #{disuseDate} BETWEEN START_DATE AND END_DATE ), 0) AS SALE_UPRC , NVL(tshsc.AVG_COST_UPRC, thp.COST_UPRC) AS COST_UPRC , NVL(NVL(tshdd.CURR_QTY, tshsc.CURR_QTY), 0) AS CURR_QTY , tshdd.DISUSE_QTY , tshdd.DISUSE_AMT , tshdd.REMARK , DECODE(tshdd.PROD_CD, NULL, 'I', 'U') AS DISUSE_PROD_STATUS
FROM TB_HQ_PRODUCT thp , TB_ST_HQ_DISUSE_DTL tshdd , ( SELECT tshsc.PROD_CD, tshsc.CURR_QTY, tshsm.AVG_COST_UPRC FROM TB_ST_HQ_STOCK_CUR tshsc , TB_ST_HQ_STOCK_MONTHLY tshsm WHERE tshsc.HQ_OFFICE_CD = #{hqOfficeCd} AND tshsm.IOSTOCK_YM (+) = SUBSTR(#{disuseDate}, 0, 6) AND tshsm.HQ_OFFICE_CD(+) = tshsc.HQ_OFFICE_CD AND tshsm.PROD_CD (+) = tshsc.PROD_CD ) tshsc , TB_HQ_PRODUCT_BARCD thpb WHERE thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.STOCK_PROD_YN = 'Y' AND thp.USE_YN = 'Y' AND tshdd.PROD_CD (+) = thp.PROD_CD AND tshdd.HQ_OFFICE_CD (+) = #{hqOfficeCd} AND tshdd.DISUSE_DATE (+) = #{disuseDate} AND tshdd.SEQ_NO (+) = #{seqNo} AND tshsc.PROD_CD (+) = thp.PROD_CD AND thpb.HQ_OFFICE_CD (+) = thp.HQ_OFFICE_CD AND thpb.PROD_CD (+) = thp.PROD_CD
AND thp.PROD_CD = #{prodCd}
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thpb.BARCD_CD = #{barcdCd}
AND (thp.PROD_CD = #{prodBarcdCd} OR thpb.BARCD_CD = #{prodBarcdCd})
AND tshdd.DISUSE_QTY IS NOT NULL
AND tshdd.DISUSE_QTY IS NULL
ORDER BY thp.PROD_CD

/* USE : HqDisuseMapper.getNewSeqNo */

SELECT  NVL(MAX(tshd.SEQ_NO), 0)+1 AS SEQ_NO
FROM    TB_ST_HQ_DISUSE tshd
WHERE   tshd.HQ_OFFICE_CD  =  #{hqOfficeCd}
AND     tshd.DISUSE_DATE   =  #{disuseDate}

/* USE : HqDisuseMapper.getHqDisuseDtlList */
SELECT thpb.BARCD_CD, thp.PROD_CD, thp.PROD_NM, thp.PO_UNIT_QTY , NVL((SELECT MAX(SALE_UPRC) KEEP(DENSE_RANK FIRST ORDER BY REG_DT DESC) FROM TB_HQ_PRODUCT_SALE_PRICE WHERE HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND PROD_CD = thp.PROD_CD AND #{disuseDate} BETWEEN START_DATE AND END_DATE ), 0) AS SALE_UPRC , NVL(tshsm.AVG_COST_UPRC, thp.COST_UPRC) AS COST_UPRC , tshdd.CURR_QTY , tshdd.DISUSE_QTY , tshdd.DISUSE_AMT , tshdd.REMARK , 'U' AS DISUSE_PROD_STATUS
FROM TB_ST_HQ_DISUSE_DTL tshdd , TB_HQ_PRODUCT thp , ( SELECT tshsm.PROD_CD , tshsm.AVG_COST_UPRC , (tshsm.BASE_QTY + NVL(IOSTOCK_QTY, 0)) AS CMPT_DATE_QTY FROM ( SELECT tshsm.IOSTOCK_YM, tshsm.HQ_OFFICE_CD , tshsm.PROD_CD, tshsm.BASE_QTY, tshsm.AVG_COST_UPRC FROM TB_ST_HQ_STOCK_MONTHLY tshsm WHERE tshsm.HQ_OFFICE_CD = #{hqOfficeCd} AND tshsm.IOSTOCK_YM = SUBSTR(#{disuseDate}, 0, 6) ) tshsm , ( SELECT tshsd.IOSTOCK_DATE, tshsd.HQ_OFFICE_CD , tshsd.PROD_CD , SUM(tshsd.IOSTOCK_QTY) AS IOSTOCK_QTY FROM TB_ST_HQ_STOCK_DAILY tshsd WHERE tshsd.HQ_OFFICE_CD = #{hqOfficeCd} AND tshsd.IOSTOCK_DATE = #{disuseDate} GROUP BY tshsd.IOSTOCK_DATE, tshsd.HQ_OFFICE_CD , tshsd.PROD_CD ) tshsd WHERE tshsd.PROD_CD(+)= tshsm.PROD_CD ) tshsm , TB_HQ_PRODUCT_BARCD thpb WHERE tshdd.HQ_OFFICE_CD = #{hqOfficeCd} AND tshdd.DISUSE_DATE = #{disuseDate} AND tshdd.SEQ_NO = #{seqNo} AND tshdd.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND tshdd.PROD_CD = thp.PROD_CD AND tshsm.PROD_CD (+) = tshdd.PROD_CD AND thpb.HQ_OFFICE_CD (+) = tshdd.HQ_OFFICE_CD AND thpb.PROD_CD (+) = tshdd.PROD_CD ORDER BY tshdd.PROD_CD

mapper.delete:
/* USE : HqDisuseMapper.deleteAllHqDisuseDtl */
DELETE  TB_ST_HQ_DISUSE_DTL
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     DISUSE_DATE   = #{disuseDate}
AND     SEQ_NO        = #{seqNo}

/* USE : HqDisuseMapper.deleteHqDisuseHd */
DELETE  TB_ST_HQ_DISUSE
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     DISUSE_DATE   = #{disuseDate}
AND     SEQ_NO        = #{seqNo}

mapper.update:
/* USE : HqDisuseMapper.updateHqDisuseDtl */
UPDATE  TB_ST_HQ_DISUSE_DTL
SET     DISUSE_QTY = #{disuseQty}
,       DISUSE_AMT = #{disuseAmt}
,       REMARK     = #{remark}
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     DISUSE_DATE   = #{disuseDate}
AND     SEQ_NO        = #{seqNo}
AND     PROD_CD       = #{prodCd}

mapper.delete:
/* USE : HqDisuseMapper.deleteHqDisuseDtl */
DELETE  TB_ST_HQ_DISUSE_DTL
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     DISUSE_DATE   = #{disuseDate}
AND     SEQ_NO        = #{seqNo}
AND     PROD_CD       = #{prodCd}

mapper.insert:
/* USE : HqDisuseMapper.insertHqDisuseHd */
INSERT INTO TB_ST_HQ_DISUSE
(
    HQ_OFFICE_CD,
    STORAGE_CD,
    DISUSE_DATE,
    SEQ_NO,
    DISUSE_TITLE,
    PROC_FG,
    DTL_CNT,
    TOT_DISUSE_AMT,
    REG_DT,
    REG_ID,
    MOD_DT,
    MOD_ID
)
SELECT  tshdd.HQ_OFFICE_CD
,       #{storageCd}
,       tshdd.DISUSE_DATE
,       tshdd.SEQ_NO
,       #{disuseTitle}
,       #{procFg}
,       COUNT(tshdd.PROD_CD)
,       SUM(tshdd.DISUSE_AMT)
,       #{regDt}
,       #{regId}
,       #{modDt}
,       #{modId}
FROM    TB_ST_HQ_DISUSE_DTL tshdd
WHERE   tshdd.HQ_OFFICE_CD  =   #{hqOfficeCd}
AND     tshdd.DISUSE_DATE   =   #{disuseDate}
AND     tshdd.SEQ_NO        =   #{seqNo}
GROUP
BY      tshdd.HQ_OFFICE_CD
,       #{storageCd}
,       tshdd.DISUSE_DATE
,       tshdd.SEQ_NO
,       #{disuseTitle}
,       #{procFg}
,       #{regDt}
,       #{regId}
,       #{modDt}
,       #{modId}

mapper.update:
/* USE : HqDisuseMapper.updateHqDisuseHd */
UPDATE  TB_ST_HQ_DISUSE
SET     (DTL_CNT, TOT_DISUSE_AMT)
      = (   SELECT  COUNT(tshdd.PROD_CD)
            ,       SUM(tshdd.DISUSE_AMT)
            FROM    TB_ST_HQ_DISUSE_DTL tshdd
            WHERE   tshdd.HQ_OFFICE_CD  =   #{hqOfficeCd}
            AND     tshdd.DISUSE_DATE   =   #{disuseDate}
            AND     tshdd.SEQ_NO        =   #{seqNo}
        )
,       DISUSE_TITLE = #{disuseTitle}
,       PROC_FG      = #{procFg}
,       CONFM_DATE   = DECODE(#{procFg}, '0', NULL, SUBSTR(#{modDt}, 0, 8))
,       CONFM_ID     = DECODE(#{procFg}, '0', NULL, #{modId})
,       MOD_DT       = #{modDt}
,       MOD_ID       = #{modId}
WHERE   HQ_OFFICE_CD  = #{hqOfficeCd}
AND     DISUSE_DATE   = #{disuseDate}
AND     SEQ_NO        = #{seqNo}
