mapper.select:
/* USE : StockManageViewMapper.getStockManageViewList */
SELECT A.* , (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '202' AND NMCODE_CD = A.HQ_GBN) AS HQ_GBN_NM FROM (SELECT M.HQ_GBN , M.TOT_DATE , M.SEQ_NO , M.PROC_FG_NM , M.TITLE , M.DTL_CNT , M.REASON_CD , M.REASON_NM
FROM ( SELECT '1' AS HQ_GBN , TO_CHAR(TO_DATE(TSHAI.ACINS_DATE,'YYYYMMDD'), 'YYYY-MM-DD') AS TOT_DATE , TSHAI.SEQ_NO , DECODE(TSHAI.PROC_FG,'0','등록','확정') AS PROC_FG_NM , TSHAI.ACINS_TITLE AS TITLE , TSHAI.DTL_CNT , TSHAI.REASON_CD , (SELECT NMCODE_NM FROM TB_HQ_NMCODE thm WHERE thm.HQ_OFFICE_CD = TSHAI.HQ_OFFICE_CD AND NMCODE_GRP_CD = '134' AND thm.NMCODE_CD = TSHAI.REASON_CD) AS REASON_NM FROM TB_ST_HQ_ACTUAL_INSPECTION TSHAI WHERE TSHAI.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSHAI.ACINS_DATE BETWEEN #{startDate} AND #{endDate} /* 조회일자 */
AND TSHAI.PROC_FG = #{procFg} /* (0:등록, 1:확정) */
UNION ALL SELECT '2' AS HQ_GBN , TO_CHAR(TO_DATE(TSHA.ADJ_DATE,'YYYYMMDD'), 'YYYY-MM-DD') AS TOT_DATE , TSHA.SEQ_NO , DECODE(TSHA.PROC_FG,'0','등록','확정') AS PROC_FG_NM , TSHA.ADJ_TITLE AS TITLE , TSHA.DTL_CNT , TSHA.REASON_CD , (SELECT NMCODE_NM FROM TB_HQ_NMCODE thm WHERE thm.HQ_OFFICE_CD = TSHA.HQ_OFFICE_CD AND NMCODE_GRP_CD = '135' AND thm.NMCODE_CD = TSHA.REASON_CD) AS REASON_NM FROM TB_ST_HQ_ADJUST TSHA WHERE TSHA.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSHA.ADJ_DATE BETWEEN #{startDate} AND #{endDate} /* 조회일자 */
AND TSHA.PROC_FG = #{procFg} /* (0:등록, 1:확정) */
UNION ALL SELECT '3' AS HQ_GBN , TO_CHAR(TO_DATE(TSHD.DISUSE_DATE,'YYYYMMDD'), 'YYYY-MM-DD') AS TOT_DATE , TSHD.SEQ_NO , DECODE(TSHD.PROC_FG,'0','등록','확정') AS PROC_FG_NM , TSHD.DISUSE_TITLE AS TITLE , TSHD.DTL_CNT , TSHD.REASON_CD , (SELECT NMCODE_NM FROM TB_HQ_NMCODE thm WHERE thm.HQ_OFFICE_CD = TSHD.HQ_OFFICE_CD AND NMCODE_GRP_CD = '136' AND thm.NMCODE_CD = TSHD.REASON_CD) AS REASON_NM FROM TB_ST_HQ_DISUSE TSHD WHERE TSHD.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSHD.DISUSE_DATE BETWEEN #{startDate} AND #{endDate} /* 조회일자 */
AND TSHD.PROC_FG = #{procFg} /* (0:등록, 1:확정) */
) M
WHERE M.HQ_GBN = #{hqGbn} /* (1:실사, 2:조정, 3:폐기) */
WHERE M.REASON_CD = #{reason}
) A

/* USE : StockManageViewMapper.getStockManageViewStoreList */
SELECT A.* , (SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '202' AND NMCODE_CD = A.HQ_GBN) AS HQ_GBN_NM FROM ( SELECT M.HQ_GBN , M.TOT_DATE , M.SEQ_NO , M.PROC_FG_NM , M.TITLE , M.DTL_CNT , M.REASON_CD , M.REASON_NM
FROM ( SELECT '1' AS HQ_GBN , TSSAI.STORE_CD , TO_CHAR(TO_DATE(TSSAI.ACINS_DATE,'YYYYMMDD'), 'YYYY-MM-DD') AS TOT_DATE , TSSAI.SEQ_NO , DECODE(TSSAI.PROC_FG,'0','등록','확정') AS PROC_FG_NM , ACINS_TITLE AS TITLE , TSSAI.DTL_CNT , TSSAI.REASON_CD , (SELECT NMCODE_NM FROM TB_MS_STORE_NMCODE thm WHERE thm.STORE_CD = TSSAI.STORE_CD AND NMCODE_GRP_CD = '134' AND thm.NMCODE_CD = TSSAI.REASON_CD) AS REASON_NM FROM TB_ST_STORE_ACTUAL_INSPECTION TSSAI WHERE EXISTS (SELECT * FROM TB_MS_STORE Z WHERE Z.HQ_OFFICE_CD = #{hqOfficeCd} AND Z.STORE_CD = TSSAI.STORE_CD) AND TSSAI.STORE_CD = #{storeCd}
AND TSSAI.ACINS_DATE BETWEEN #{startDate} AND #{endDate} /* 조회일자 */
AND TSSAI.PROC_FG = #{procFg} /* (0:등록, 1:확정) */
UNION ALL SELECT '2' AS HQ_GBN , TSSA.STORE_CD , TO_CHAR(TO_DATE(TSSA.ADJ_DATE,'YYYYMMDD'), 'YYYY-MM-DD') AS TOT_DATE , TSSA.SEQ_NO , DECODE(TSSA.PROC_FG,'0','등록','확정') AS PROC_FG_NM , ADJ_TITLE AS TITLE , TSSA.DTL_CNT , TSSA.REASON_CD , (SELECT NMCODE_NM FROM TB_MS_STORE_NMCODE thm WHERE thm.STORE_CD = TSSA.STORE_CD AND NMCODE_GRP_CD = '135' AND thm.NMCODE_CD = TSSA.REASON_CD) AS REASON_NM FROM TB_ST_STORE_ADJUST TSSA WHERE EXISTS (SELECT * FROM TB_MS_STORE Z WHERE Z.HQ_OFFICE_CD = #{hqOfficeCd} AND Z.STORE_CD = TSSA.STORE_CD) AND TSSA.STORE_CD = #{storeCd}
AND TSSA.ADJ_DATE BETWEEN #{startDate} AND #{endDate} /* 조회일자 */
AND TSSA.PROC_FG = #{procFg} /* (0:등록, 1:확정) */
UNION ALL SELECT '3' AS HQ_GBN , TSSD.STORE_CD , TO_CHAR(TO_DATE(TSSD.DISUSE_DATE,'YYYYMMDD'), 'YYYY-MM-DD') AS TOT_DATE , TSSD.SEQ_NO , DECODE(TSSD.PROC_FG,'0','등록','확정') AS PROC_FG_NM , DISUSE_TITLE AS TITLE , TSSD.DTL_CNT , TSSD.REASON_CD , (SELECT NMCODE_NM FROM TB_MS_STORE_NMCODE thm WHERE thm.STORE_CD = TSSD.STORE_CD AND NMCODE_GRP_CD = '136' AND thm.NMCODE_CD = TSSD.REASON_CD) AS REASON_NM FROM TB_ST_STORE_DISUSE TSSD WHERE EXISTS (SELECT * FROM TB_MS_STORE Z WHERE Z.HQ_OFFICE_CD = #{hqOfficeCd} AND Z.STORE_CD = TSSD.STORE_CD) AND TSSD.STORE_CD = #{storeCd}
AND TSSD.DISUSE_DATE BETWEEN #{startDate} AND #{endDate} /* 조회일자 */
AND TSSD.PROC_FG = #{procFg} /* (0:등록, 1:확정) */
) M
WHERE M.HQ_GBN = #{hqGbn} /* (1:실사, 2:조정, 3:폐기) */
WHERE M.REASON_CD = #{reason}
ORDER BY M.STORE_CD, M.HQ_GBN ) A

