choose.when:
SELECT TO_CHAR(TO_DATE(TSHSD.IOSTOCK_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS IO_OCCR_DT , SF_ZERO_TO_NULL(SUM(VENDR_IN_QTY)) AS IO_OCCR_QTY_01 /* 본사입고 수량*/ , SF_ZERO_TO_NULL(SUM(VENDR_IN_TOT)) AS IO_OCCR_TOT_01 /* 본사입고 금액*/ , SF_ZERO_TO_NULL(SUM(VENDR_OUT_QTY)) AS IO_OCCR_QTY_16 /* 업체반출 수량*/ , SF_ZERO_TO_NULL(SUM(VENDR_OUT_TOT)) AS IO_OCCR_TOT_16 /* 업체반출 금액*/ , SF_ZERO_TO_NULL(SUM(HQ_OUT_QTY)) AS IO_OCCR_QTY_13 /* 본사출고 수량*/ , SF_ZERO_TO_NULL(SUM(HQ_OUT_TOT)) AS IO_OCCR_TOT_13 /* 본사출고 금액*/ , SF_ZERO_TO_NULL(SUM(HQ_IN_QTY)) AS IO_OCCR_QTY_02 /* 본사반입 수량*/ , SF_ZERO_TO_NULL(SUM(HQ_IN_TOT)) AS IO_OCCR_TOT_02 /* 본사반입 금액*/ , SF_ZERO_TO_NULL(SUM(STORE_MOVE_IN_QTY)) AS IO_OCCR_QTY_04 /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(STORE_MOVE_IN_TOT)) AS IO_OCCR_TOT_04 /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(STORE_MOVE_OUT_QTY)) AS IO_OCCR_QTY_14 /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(STORE_MOVE_OUT_TOT)) AS IO_OCCR_TOT_14 /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(DISUSE_QTY)) AS IO_OCCR_QTY_17 /* 재고폐기 수량*/ -- , SF_ZERO_TO_NULL(SUM()) AS IO_OCCR_TOT_17 /* 재고폐기 금액*/ , SF_ZERO_TO_NULL(SUM(ADJ_QTY)) AS IO_OCCR_QTY_21 /* 재고조정 수량*/ -- , SF_ZERO_TO_NULL(SUM()) AS IO_OCCR_TOT_21 /* 재고조정 금액*/ , SF_ZERO_TO_NULL(SUM(SET_IN_QTY)) AS IO_OCCR_QTY_22 /* 세트생성 수량*/ -- , SF_ZERO_TO_NULL(SUM()) AS IO_OCCR_TOT_22 /* 세트생성 금액*/ , SF_ZERO_TO_NULL(SUM(SALE_VENDR_ORDER_QTY)) AS IO_OCCR_QTY_19 /* 거래처출고 수량*/ , SF_ZERO_TO_NULL(SUM(SALE_VENDR_ORDER_TOT)) AS IO_OCCR_TOT_19 /* 거래처출고 금액*/ , SF_ZERO_TO_NULL(SUM(SALE_VENDR_RTN_QTY)) AS IO_OCCR_QTY_33 /* 거래처반품 수량*/ , SF_ZERO_TO_NULL(SUM(SALE_VENDR_RTN_TOT)) AS IO_OCCR_TOT_33 /* 거래처반품 금액*/ FROM TB_ST_HQ_STOCK_DAILY TSHSD , TB_HQ_PRODUCT THP WHERE TSHSD.HQ_OFFICE_CD = #{hqOfficeCd} AND THP.STOCK_PROD_YN = 'Y' AND TSHSD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TSHSD.PROD_CD = THP.PROD_CD
AND TSHSD.IOSTOCK_DATE BETWEEN #{startDate} AND #{endDate}
GROUP BY TSHSD.IOSTOCK_DATE ORDER BY TSHSD.IOSTOCK_DATE DESC

SELECT TO_CHAR(TO_DATE(TSSSD.IOSTOCK_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS IO_OCCR_DT , SF_ZERO_TO_NULL(SUM(STORE_IN_QTY)) AS IO_OCCR_QTY_03 /* 매장입고 수량*/ , SF_ZERO_TO_NULL(SUM(STORE_IN_TOT)) AS IO_OCCR_TOT_03 /* 매장입고 금액*/ , SF_ZERO_TO_NULL(SUM(STORE_OUT_QTY)) AS IO_OCCR_QTY_12 /* 매장반품 수량*/ , SF_ZERO_TO_NULL(SUM(STORE_OUT_TOT)) AS IO_OCCR_TOT_12 /* 매장반품 금액*/ , SF_ZERO_TO_NULL(SUM(PURCHS_IN_QTY)) AS IO_OCCR_QTY_06 /* 사입입고 수량*/ , SF_ZERO_TO_NULL(SUM(PURCHS_IN_TOT)) AS IO_OCCR_TOT_06 /* 사입입고 금액*/ , SF_ZERO_TO_NULL(SUM(PURCHS_OUT_QTY)) AS IO_OCCR_QTY_18 /* 사입반품 수량*/ , SF_ZERO_TO_NULL(SUM(PURCHS_OUT_TOT)) AS IO_OCCR_TOT_18 /* 사입반품 금액*/ , SF_ZERO_TO_NULL(SUM(STORE_SALE_QTY)) AS IO_OCCR_QTY_11 /* 매장판매 수량*/ , SF_ZERO_TO_NULL(SUM(STORE_SALE_TOT)) AS IO_OCCR_TOT_11 /* 매장판매 금액*/ , SF_ZERO_TO_NULL(SUM(MOVE_IN_QTY)) AS IO_OCCR_QTY_04 /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(MOVE_IN_TOT)) AS IO_OCCR_TOT_04 /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(MOVE_OUT_QTY)) AS IO_OCCR_QTY_14 /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(MOVE_OUT_TOT)) AS IO_OCCR_TOT_14 /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(DISUSE_QTY)) AS IO_OCCR_QTY_01 /* 재고폐기 수량*/ -- , SF_ZERO_TO_NULL(SUM()) AS IO_OCCR_TOT_01 /* 재고폐기 금액*/ , SF_ZERO_TO_NULL(SUM(ADJ_QTY)) AS IO_OCCR_QTY_02 /* 재고조정 수량*/ -- , SF_ZERO_TO_NULL(SUM()) AS IO_OCCR_TOT_02 /* 재고조정 금액*/ , SF_ZERO_TO_NULL(SUM(SET_IN_QTY)) AS IO_OCCR_QTY_05 /* 세트생성 수량*/ -- , SF_ZERO_TO_NULL(SUM()) AS IO_OCCR_TOT_05 /* 세트생성 금액*/ FROM TB_ST_STORE_STOCK_DAILY TSSSD , TB_MS_PRODUCT TMP WHERE TSSSD.STORE_CD = #{storeCd} AND TMP.STOCK_PROD_YN = 'Y' AND TSSSD.STORE_CD = TMP.STORE_CD AND TSSSD.PROD_CD = TMP.PROD_CD
AND TSSSD.IOSTOCK_DATE BETWEEN #{startDate} AND #{endDate}
GROUP BY TSSSD.IOSTOCK_DATE ORDER BY TSSSD.IOSTOCK_DATE DESC
