, SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_IN_QTY / THP.PO_UNIT_QTY)) AS IO_OCCR_QTY_01 /* 본사입고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_IN_TOT)) AS IO_OCCR_TOT_01 /* 본사입고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_OUT_QTY / THP.PO_UNIT_QTY)) AS IO_OCCR_QTY_16 /* 업체반출 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_OUT_TOT)) AS IO_OCCR_TOT_16 /* 업체반출 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_OUT_QTY / THP.PO_UNIT_QTY)) AS IO_OCCR_QTY_13 /* 본사출고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_OUT_TOT)) AS IO_OCCR_TOT_13 /* 본사출고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_IN_QTY / THP.PO_UNIT_QTY)) AS IO_OCCR_QTY_02 /* 본사반입 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_IN_TOT)) AS IO_OCCR_TOT_02 /* 본사반입 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_IN_QTY / THP.PO_UNIT_QTY)) AS IO_OCCR_QTY_04 /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_IN_TOT)) AS IO_OCCR_TOT_04 /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_OUT_QTY / THP.PO_UNIT_QTY)) AS IO_OCCR_QTY_14 /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_OUT_TOT)) AS IO_OCCR_TOT_14 /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.DISUSE_QTY / THP.PO_UNIT_QTY)) AS IO_OCCR_QTY_17 /* 재고폐기 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSHSD.DISUSE_AMT)) AS IO_OCCR_TOT_17 /* 재고폐기 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.ADJ_QTY / THP.PO_UNIT_QTY)) AS IO_OCCR_QTY_21 /* 재고조정 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSHSD.ADJ_AMT)) AS IO_OCCR_TOT_21 /* 재고조정 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SET_IN_QTY / THP.PO_UNIT_QTY)) AS IO_OCCR_QTY_22 /* 세트생성 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSHSD.SET_IN_AMT)) AS IO_OCCR_TOT_22 /* 세트생성 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_ORDER_QTY / THP.PO_UNIT_QTY)) AS IO_OCCR_QTY_19 /* 거래처출고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_ORDER_TOT)) AS IO_OCCR_TOT_19 /* 거래처출고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_RTN_QTY / THP.PO_UNIT_QTY)) AS IO_OCCR_QTY_33 /* 거래처반품 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_RTN_TOT)) AS IO_OCCR_TOT_33 /* 거래처반품 금액*/
, SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_IN_QTY)) AS IO_OCCR_QTY_01 /* 본사입고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_IN_TOT)) AS IO_OCCR_TOT_01 /* 본사입고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_OUT_QTY)) AS IO_OCCR_QTY_16 /* 업체반출 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_OUT_TOT)) AS IO_OCCR_TOT_16 /* 업체반출 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_OUT_QTY)) AS IO_OCCR_QTY_13 /* 본사출고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_OUT_TOT)) AS IO_OCCR_TOT_13 /* 본사출고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_IN_QTY)) AS IO_OCCR_QTY_02 /* 본사반입 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_IN_TOT)) AS IO_OCCR_TOT_02 /* 본사반입 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_IN_QTY)) AS IO_OCCR_QTY_04 /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_IN_TOT)) AS IO_OCCR_TOT_04 /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_OUT_QTY)) AS IO_OCCR_QTY_14 /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_OUT_TOT)) AS IO_OCCR_TOT_14 /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.DISUSE_QTY)) AS IO_OCCR_QTY_17 /* 재고폐기 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSHSD.DISUSE_AMT)) AS IO_OCCR_TOT_17 /* 재고폐기 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.ADJ_QTY)) AS IO_OCCR_QTY_21 /* 재고조정 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSHSD.ADJ_AMT)) AS IO_OCCR_TOT_21 /* 재고조정 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SET_IN_QTY)) AS IO_OCCR_QTY_22 /* 세트생성 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSHSD.SET_IN_AMT)) AS IO_OCCR_TOT_22 /* 세트생성 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_ORDER_QTY)) AS IO_OCCR_QTY_19 /* 거래처출고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_ORDER_TOT)) AS IO_OCCR_TOT_19 /* 거래처출고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_RTN_QTY)) AS IO_OCCR_QTY_33 /* 거래처반품 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_RTN_TOT)) AS IO_OCCR_TOT_33 /* 거래처반품 금액*/
