, SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_IN_QTY)) AS IO_OCCR_QTY_01 /* 본사입고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_IN_TOT)) AS IO_OCCR_TOT_01 /* 본사입고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_OUT_QTY)) AS IO_OCCR_QTY_16 /* 업체반출 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_OUT_TOT)) AS IO_OCCR_TOT_16 /* 업체반출 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_OUT_QTY)) AS IO_OCCR_QTY_13 /* 본사출고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_OUT_TOT)) AS IO_OCCR_TOT_13 /* 본사출고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_IN_QTY)) AS IO_OCCR_QTY_02 /* 본사반입 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_IN_TOT)) AS IO_OCCR_TOT_02 /* 본사반입 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_IN_QTY)) AS IO_OCCR_QTY_04 /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_IN_TOT)) AS IO_OCCR_TOT_04 /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_OUT_QTY)) AS IO_OCCR_QTY_14 /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_OUT_TOT)) AS IO_OCCR_TOT_14 /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.DISUSE_QTY)) AS IO_OCCR_QTY_17 /* 재고폐기 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSHSD.DISUSE_AMT)) AS IO_OCCR_TOT_17 /* 재고폐기 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.ADJ_QTY)) AS IO_OCCR_QTY_21 /* 재고조정 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSHSD.ADJ_AMT)) AS IO_OCCR_TOT_21 /* 재고조정 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SET_IN_QTY)) AS IO_OCCR_QTY_22 /* 세트생성 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSHSD.SET_IN_AMT)) AS IO_OCCR_TOT_22 /* 세트생성 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_ORDER_QTY)) AS IO_OCCR_QTY_19 /* 거래처출고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_ORDER_TOT)) AS IO_OCCR_TOT_19 /* 거래처출고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_RTN_QTY)) AS IO_OCCR_QTY_33 /* 거래처반품 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_RTN_TOT)) AS IO_OCCR_TOT_33 /* 거래처반품 금액*/
FROM TB_ST_HQ_STOCK_DAILY TSHSD , TB_HQ_PRODUCT THP , TB_HQ_PRODUCT_BARCD THPB WHERE TSHSD.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSHSD.IOSTOCK_DATE = #{startDate}
AND THP.PROD_CD = #{prodCd}
AND THP.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
AND THP.PROD_NM LIKE '%'||#{prodNm}||'%'
AND THPB.BARCD_CD = #{barcdCd}
AND THP.PROD_CD IN ( SELECT PROD_CD FROM TB_HQ_VENDOR_PROD WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND VENDR_CD IN
#{item}
)
AND TSHSD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TSHSD.PROD_CD = THP.PROD_CD AND TSHSD.HQ_OFFICE_CD = THPB.HQ_OFFICE_CD(+) AND TSHSD.PROD_CD = THPB.PROD_CD(+) GROUP BY THP.PROD_CLASS_CD , TSHSD.PROD_CD , THP.PROD_NM , THP.PO_UNIT_QTY , THP.PO_UNIT_FG , THPB.BARCD_CD ) A ORDER BY A.PROD_CD

SELECT A.* , (SELECT NMCODE_NM FROM TB_MS_STORE_NMCODE WHERE NMCODE_GRP_CD = '093' AND STORE_CD = #{storeCd} AND NMCODE_CD = A.PO_UNIT_FG) AS PO_UNIT_FG_NM /* 매장 가상로그인시 */ FROM ( SELECT FN_GET_PROD_CLASS_CD_NM_STR(#{storeCd}, TMP.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM , TSSSD.PROD_CD , TMP.PROD_NM , TMP.PO_UNIT_QTY , TMP.PO_UNIT_FG , TMPB.BARCD_CD
, SF_ZERO_TO_NULL(SUM(TSSSD.STORE_IN_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_03 /* 매장입고 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_IN_TOT)) AS IO_OCCR_TOT_03 /* 매장입고 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_OUT_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_12 /* 매장반품 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_OUT_TOT)) AS IO_OCCR_TOT_12 /* 매장반품 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_IN_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_06 /* 사입입고 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_IN_TOT)) AS IO_OCCR_TOT_06 /* 사입입고 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_OUT_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_18 /* 사입반품 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_OUT_TOT)) AS IO_OCCR_TOT_18 /* 사입반품 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_SALE_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_11 /* 매장판매 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_SALE_TOT)) AS IO_OCCR_TOT_11 /* 매장판매 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_IN_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_04 /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_IN_TOT)) AS IO_OCCR_TOT_04 /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_OUT_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_14 /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_OUT_TOT)) AS IO_OCCR_TOT_14 /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.DISUSE_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_17 /* 재고폐기 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSSSD.DISUSE_AMT)) AS IO_OCCR_TOT_17 /* 재고폐기 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.ADJ_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_21 /* 재고조정 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSSSD.ADJ_AMT)) AS IO_OCCR_TOT_21 /* 재고조정 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.SET_IN_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_22 /* 세트생성 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSSSD.SET_IN_AMT)) AS IO_OCCR_TOT_22 /* 세트생성 금액*/
, SF_ZERO_TO_NULL(SUM(TSSSD.STORE_IN_QTY)) AS IO_OCCR_QTY_03 /* 매장입고 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_IN_TOT)) AS IO_OCCR_TOT_03 /* 매장입고 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_OUT_QTY)) AS IO_OCCR_QTY_12 /* 매장반품 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_OUT_TOT)) AS IO_OCCR_TOT_12 /* 매장반품 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_IN_QTY)) AS IO_OCCR_QTY_06 /* 사입입고 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_IN_TOT)) AS IO_OCCR_TOT_06 /* 사입입고 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_OUT_QTY)) AS IO_OCCR_QTY_18 /* 사입반품 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_OUT_TOT)) AS IO_OCCR_TOT_18 /* 사입반품 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_SALE_QTY)) AS IO_OCCR_QTY_11 /* 매장판매 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_SALE_TOT)) AS IO_OCCR_TOT_11 /* 매장판매 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_IN_QTY)) AS IO_OCCR_QTY_04 /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_IN_TOT)) AS IO_OCCR_TOT_04 /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_OUT_QTY)) AS IO_OCCR_QTY_14 /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_OUT_TOT)) AS IO_OCCR_TOT_14 /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.DISUSE_QTY)) AS IO_OCCR_QTY_17 /* 재고폐기 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSSSD.DISUSE_AMT)) AS IO_OCCR_TOT_17 /* 재고폐기 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.ADJ_QTY)) AS IO_OCCR_QTY_21 /* 재고조정 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSSSD.ADJ_AMT)) AS IO_OCCR_TOT_21 /* 재고조정 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.SET_IN_QTY)) AS IO_OCCR_QTY_22 /* 세트생성 수량*/ -- , SF_ZERO_TO_NULL(SUM(TSSSD.SET_IN_AMT)) AS IO_OCCR_TOT_22 /* 세트생성 금액*/
FROM TB_ST_STORE_STOCK_DAILY TSSSD , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TSSSD.STORE_CD = #{storeCd}
AND TSSSD.IOSTOCK_DATE = #{startDate}
AND TMP.PROD_CD = #{prodCd}
AND TMP.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = #{storeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = #{storeCd} )
AND TMP.PROD_NM LIKE '%'||#{prodNm}||'%'
AND TMPB.BARCD_CD = #{barcdCd}
AND TMP.PROD_CD IN ( SELECT PROD_CD FROM TB_MS_VENDOR_PROD WHERE STORE_CD = #{storeCd} AND VENDR_CD IN
#{item}
)
AND TSSSD.STORE_CD = TMP.STORE_CD AND TSSSD.PROD_CD = TMP.PROD_CD AND TSSSD.STORE_CD = TMPB.STORE_CD(+) AND TSSSD.PROD_CD = TMPB.PROD_CD(+) GROUP BY TMP.PROD_CLASS_CD , TSSSD.PROD_CD , TMP.PROD_NM , TMP.PO_UNIT_QTY , TMP.PO_UNIT_FG , TMPB.BARCD_CD ) A ORDER BY A.PROD_CD
