SELECT A.* , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE NMCODE_GRP_CD = '093' AND HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_CD = A.PO_UNIT_FG) AS PO_UNIT_FG_NM /* 본사 가상로그인시 */
FROM ( SELECT FN_GET_PROD_CLASS_CD_NM_STR(#{hqOfficeCd}, THP.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM , TSHSD.PROD_CD , THP.PROD_NM , THP.PO_UNIT_QTY , THP.PO_UNIT_FG , THPB.BARCD_CD
, SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_IN_QTY / THP.PO_UNIT_QTY)) AS VENDR_IN_QTY /* 본사입고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_IN_TOT)) AS VENDR_IN_TOT /* 본사입고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_OUT_QTY / THP.PO_UNIT_QTY)) AS VENDR_OUT_QTY /* 업체반출 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_OUT_TOT)) AS VENDR_OUT_TOT /* 업체반출 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_OUT_QTY / THP.PO_UNIT_QTY)) AS HQ_OUT_QTY /* 본사출고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_OUT_TOT)) AS HQ_OUT_TOT /* 본사출고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_IN_QTY / THP.PO_UNIT_QTY)) AS HQ_IN_QTY /* 본사반입 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_IN_TOT)) AS HQ_IN_TOT /* 본사반입 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_IN_QTY / THP.PO_UNIT_QTY)) AS STORE_MOVE_IN_QTY /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_IN_TOT)) AS STORE_MOVE_IN_TOT /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_OUT_QTY / THP.PO_UNIT_QTY)) AS STORE_MOVE_OUT_QTY /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_OUT_TOT)) AS STORE_MOVE_OUT_TOT /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.DISUSE_QTY / THP.PO_UNIT_QTY)) AS DISUSE_QTY /* 재고폐기 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.ADJ_QTY / THP.PO_UNIT_QTY)) AS ADJ_QTY /* 재고조정 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SET_IN_QTY / THP.PO_UNIT_QTY)) AS SET_IN_QTY /* 세트생성 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_ORDER_QTY / THP.PO_UNIT_QTY)) AS SALE_VENDR_ORDER_QTY /* 거래처출고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_ORDER_TOT)) AS SALE_VENDR_ORDER_TOT /* 거래처출고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_RTN_QTY / THP.PO_UNIT_QTY)) AS SALE_VENDR_RTN_QTY /* 거래처반품 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_RTN_TOT)) AS SALE_VENDR_RTN_TOT /* 거래처반품 금액*/
, SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_IN_QTY)) AS VENDR_IN_QTY /* 본사입고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_IN_TOT)) AS VENDR_IN_TOT /* 본사입고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_OUT_QTY)) AS VENDR_OUT_QTY /* 업체반출 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.VENDR_OUT_TOT)) AS VENDR_OUT_TOT /* 업체반출 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_OUT_QTY)) AS HQ_OUT_QTY /* 본사출고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_OUT_TOT)) AS HQ_OUT_TOT /* 본사출고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_IN_QTY)) AS HQ_IN_QTY /* 본사반입 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.HQ_IN_TOT)) AS HQ_IN_TOT /* 본사반입 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_IN_QTY)) AS STORE_MOVE_IN_QTY /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_IN_TOT)) AS STORE_MOVE_IN_TOT /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_OUT_QTY)) AS STORE_MOVE_OUT_QTY /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.STORE_MOVE_OUT_TOT)) AS STORE_MOVE_OUT_TOT /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.DISUSE_QTY)) AS DISUSE_QTY /* 재고폐기 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.ADJ_QTY)) AS ADJ_QTY /* 재고조정 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SET_IN_QTY)) AS SET_IN_QTY /* 세트생성 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_ORDER_QTY)) AS SALE_VENDR_ORDER_QTY /* 거래처출고 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_ORDER_TOT)) AS SALE_VENDR_ORDER_TOT /* 거래처출고 금액*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_RTN_QTY)) AS SALE_VENDR_RTN_QTY /* 거래처반품 수량*/ , SF_ZERO_TO_NULL(SUM(TSHSD.SALE_VENDR_RTN_TOT)) AS SALE_VENDR_RTN_TOT /* 거래처반품 금액*/
, #{startDate} AS START_DATE FROM TB_ST_HQ_STOCK_DAILY TSHSD , TB_HQ_PRODUCT THP , TB_HQ_PRODUCT_BARCD THPB WHERE TSHSD.HQ_OFFICE_CD = #{hqOfficeCd} AND THP.STOCK_PROD_YN = 'Y' AND THP.USE_YN = 'Y'
AND TSHSD.IOSTOCK_DATE = #{startDate}
AND THP.PROD_CD = #{prodCd}
AND THP.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
AND THP.PROD_NM LIKE '%'||#{prodNm}||'%'
AND THPB.BARCD_CD = #{barcdCd}
AND THP.PROD_CD IN ( SELECT PROD_CD FROM TB_HQ_VENDOR_PROD WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND VENDR_CD IN
#{item}
)
AND TSHSD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TSHSD.PROD_CD = THP.PROD_CD AND TSHSD.HQ_OFFICE_CD = THPB.HQ_OFFICE_CD(+) AND TSHSD.PROD_CD = THPB.PROD_CD(+) GROUP BY THP.PROD_CLASS_CD , TSHSD.PROD_CD , THP.PROD_NM , THP.PO_UNIT_QTY , THP.PO_UNIT_FG , THPB.BARCD_CD ) A ORDER BY A.PROD_CD

