AND TSHSD.HQ_OFFICE_CD = THP.HQ_OFFICE_CD AND TSHSD.PROD_CD = THP.PROD_CD AND TSHSD.HQ_OFFICE_CD = THPB.HQ_OFFICE_CD(+) AND TSHSD.PROD_CD = THPB.PROD_CD(+) GROUP BY THP.PROD_CLASS_CD , TSHSD.PROD_CD , THP.PROD_NM , THP.PO_UNIT_QTY , THP.PO_UNIT_FG , THPB.BARCD_CD ) A ORDER BY A.PROD_CD

SELECT A.* , (SELECT NMCODE_NM FROM TB_MS_STORE_NMCODE WHERE NMCODE_GRP_CD = '093' AND STORE_CD = #{storeCd} AND NMCODE_CD = A.PO_UNIT_FG) AS PO_UNIT_FG_NM /* 매장 가상로그인시 */ FROM ( SELECT FN_GET_PROD_CLASS_CD_NM_STR(#{storeCd}, TMP.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM , TSSSD.PROD_CD , TMP.PROD_NM , TMP.PO_UNIT_QTY , TMP.PO_UNIT_FG , TMPB.BARCD_CD
, SF_ZERO_TO_NULL(SUM(TSSSD.STORE_IN_QTY / TMP.PO_UNIT_QTY)) AS STORE_IN_QTY /* 매장입고 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_IN_TOT)) AS STORE_IN_TOT /* 매장입고 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_OUT_QTY / TMP.PO_UNIT_QTY)) AS STORE_OUT_QTY /* 매장반품 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_OUT_TOT)) AS STORE_OUT_TOT /* 매장반품 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_IN_QTY / TMP.PO_UNIT_QTY)) AS PURCHS_IN_QTY /* 사입입고 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_IN_TOT)) AS PURCHS_IN_TOT /* 사입입고 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_OUT_QTY / TMP.PO_UNIT_QTY)) AS PURCHS_OUT_QTY /* 사입반품 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_OUT_TOT)) AS PURCHS_OUT_TOT /* 사입반품 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_SALE_QTY / TMP.PO_UNIT_QTY)) AS STORE_SALE_QTY /* 매장판매 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_SALE_TOT)) AS STORE_SALE_TOT /* 매장판매 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_IN_QTY / TMP.PO_UNIT_QTY)) AS MOVE_IN_QTY /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_IN_TOT)) AS MOVE_IN_TOT /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_OUT_QTY / TMP.PO_UNIT_QTY)) AS MOVE_OUT_QTY /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_OUT_TOT)) AS MOVE_OUT_TOT /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.DISUSE_QTY / TMP.PO_UNIT_QTY)) AS DISUSE_QTY /* 재고폐기 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.ADJ_QTY / TMP.PO_UNIT_QTY)) AS ADJ_QTY /* 재고조정 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.SET_IN_QTY / TMP.PO_UNIT_QTY)) AS SET_IN_QTY /* 세트생성 수량*/
, SF_ZERO_TO_NULL(SUM(TSSSD.STORE_IN_QTY)) AS STORE_IN_QTY /* 매장입고 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_IN_TOT)) AS STORE_IN_TOT /* 매장입고 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_OUT_QTY)) AS STORE_OUT_QTY /* 매장반품 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_OUT_TOT)) AS STORE_OUT_TOT /* 매장반품 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_IN_QTY)) AS PURCHS_IN_QTY /* 사입입고 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_IN_TOT)) AS PURCHS_IN_TOT /* 사입입고 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_OUT_QTY)) AS PURCHS_OUT_QTY /* 사입반품 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.PURCHS_OUT_TOT)) AS PURCHS_OUT_TOT /* 사입반품 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_SALE_QTY)) AS STORE_SALE_QTY /* 매장판매 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.STORE_SALE_TOT)) AS STORE_SALE_TOT /* 매장판매 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_IN_QTY)) AS MOVE_IN_QTY /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_IN_TOT)) AS MOVE_IN_TOT /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_OUT_QTY)) AS MOVE_OUT_QTY /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.MOVE_OUT_TOT)) AS MOVE_OUT_TOT /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(TSSSD.DISUSE_QTY)) AS DISUSE_QTY /* 재고폐기 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.ADJ_QTY)) AS ADJ_QTY /* 재고조정 수량*/ , SF_ZERO_TO_NULL(SUM(TSSSD.SET_IN_QTY)) AS SET_IN_QTY /* 세트생성 수량*/
FROM TB_ST_STORE_STOCK_DAILY TSSSD , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE TSSSD.STORE_CD = #{storeCd} AND TMP.STOCK_PROD_YN = 'Y' AND TMP.USE_YN = 'Y'
AND TSSSD.IOSTOCK_DATE = #{startDate}
AND TMP.PROD_CD = #{prodCd}
AND TMP.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = #{storeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = #{storeCd} )
AND TMP.PROD_NM LIKE '%'||#{prodNm}||'%'
AND TMPB.BARCD_CD = #{barcdCd}
AND TMP.PROD_CD IN ( SELECT PROD_CD FROM TB_MS_VENDOR_PROD WHERE STORE_CD = #{storeCd} AND VENDR_CD IN
#{item}
)
AND TSSSD.STORE_CD = TMP.STORE_CD AND TSSSD.PROD_CD = TMP.PROD_CD AND TSSSD.STORE_CD = TMPB.STORE_CD(+) AND TSSSD.PROD_CD = TMPB.PROD_CD(+) GROUP BY TMP.PROD_CLASS_CD , TSSSD.PROD_CD , TMP.PROD_NM , TMP.PO_UNIT_QTY , TMP.PO_UNIT_FG , TMPB.BARCD_CD ) A ORDER BY A.PROD_CD
