, SF_ZERO_TO_NULL(SUM(TPHSM.BASE_QTY / THP.PO_UNIT_QTY)) AS BASE_QTY /* 기초재고수량 */ , SF_ZERO_TO_NULL(SUM(TPHSM.BASE_AMT)) AS BASE_AMT /* 기초재고금액 */ , SF_ZERO_TO_NULL(SUM(TPHSM.VENDR_IN_QTY / THP.PO_UNIT_QTY)) AS VENDR_IN_QTY /* 본사입고 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.VENDR_IN_TOT)) AS VENDR_IN_TOT /* 본사입고 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.VENDR_OUT_QTY / THP.PO_UNIT_QTY)) AS VENDR_OUT_QTY /* 업체반출 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.VENDR_OUT_TOT)) AS VENDR_OUT_TOT /* 업체반출 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.HQ_OUT_QTY / THP.PO_UNIT_QTY)) AS HQ_OUT_QTY /* 본사출고 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.HQ_OUT_TOT)) AS HQ_OUT_TOT /* 본사출고 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.HQ_IN_QTY / THP.PO_UNIT_QTY)) AS HQ_IN_QTY /* 본사반입 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.HQ_IN_TOT)) AS HQ_IN_TOT /* 본사반입 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.STORE_MOVE_IN_QTY / THP.PO_UNIT_QTY)) AS STORE_MOVE_IN_QTY /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.STORE_MOVE_IN_TOT)) AS STORE_MOVE_IN_TOT /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.STORE_MOVE_OUT_QTY / THP.PO_UNIT_QTY)) AS STORE_MOVE_OUT_QTY /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.STORE_MOVE_OUT_TOT)) AS STORE_MOVE_OUT_TOT /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.DISUSE_QTY / THP.PO_UNIT_QTY)) AS DISUSE_QTY /* 재고폐기 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.ADJ_QTY / THP.PO_UNIT_QTY)) AS ADJ_QTY /* 재고조정 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.SET_IN_QTY / THP.PO_UNIT_QTY)) AS SET_IN_QTY /* 세트생성 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.SALE_VENDR_ORDER_QTY / THP.PO_UNIT_QTY)) AS SALE_VENDR_ORDER_QTY /* 거래처출고 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.SALE_VENDR_ORDER_TOT)) AS SALE_VENDR_ORDER_TOT /* 거래처출고 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.SALE_VENDR_RTN_QTY / THP.PO_UNIT_QTY)) AS SALE_VENDR_RTN_QTY /* 거래처반품 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.SALE_VENDR_RTN_TOT)) AS SALE_VENDR_RTN_TOT /* 거래처반품 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.CLOSE_QTY / THP.PO_UNIT_QTY)) AS CLOSE_QTY /* 기말재고수량 */ , SF_ZERO_TO_NULL(SUM(TPHSM.CLOSE_AMT)) AS CLOSE_AMT /* 기말재고금액 */
, SF_ZERO_TO_NULL(SUM(TPHSM.BASE_QTY)) AS BASE_QTY /* 기초재고수량 */ , SF_ZERO_TO_NULL(SUM(TPHSM.BASE_AMT)) AS BASE_AMT /* 기초재고금액 */ , SF_ZERO_TO_NULL(SUM(TPHSM.VENDR_IN_QTY)) AS VENDR_IN_QTY /* 본사입고 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.VENDR_IN_TOT)) AS VENDR_IN_TOT /* 본사입고 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.VENDR_OUT_QTY)) AS VENDR_OUT_QTY /* 업체반출 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.VENDR_OUT_TOT)) AS VENDR_OUT_TOT /* 업체반출 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.HQ_OUT_QTY)) AS HQ_OUT_QTY /* 본사출고 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.HQ_OUT_TOT)) AS HQ_OUT_TOT /* 본사출고 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.HQ_IN_QTY)) AS HQ_IN_QTY /* 본사반입 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.HQ_IN_TOT)) AS HQ_IN_TOT /* 본사반입 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.STORE_MOVE_IN_QTY)) AS STORE_MOVE_IN_QTY /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.STORE_MOVE_IN_TOT)) AS STORE_MOVE_IN_TOT /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.STORE_MOVE_OUT_QTY)) AS STORE_MOVE_OUT_QTY /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.STORE_MOVE_OUT_TOT)) AS STORE_MOVE_OUT_TOT /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.DISUSE_QTY)) AS DISUSE_QTY /* 재고폐기 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.ADJ_QTY)) AS ADJ_QTY /* 재고조정 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.SET_IN_QTY)) AS SET_IN_QTY /* 세트생성 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.SALE_VENDR_ORDER_QTY)) AS SALE_VENDR_ORDER_QTY /* 거래처출고 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.SALE_VENDR_ORDER_TOT)) AS SALE_VENDR_ORDER_TOT /* 거래처출고 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.SALE_VENDR_RTN_QTY)) AS SALE_VENDR_RTN_QTY /* 거래처반품 수량*/ , SF_ZERO_TO_NULL(SUM(TPHSM.SALE_VENDR_RTN_TOT)) AS SALE_VENDR_RTN_TOT /* 거래처반품 금액*/ , SF_ZERO_TO_NULL(SUM(TPHSM.CLOSE_QTY)) AS CLOSE_QTY /* 기말재고수량 */ , SF_ZERO_TO_NULL(SUM(TPHSM.CLOSE_AMT)) AS CLOSE_AMT /* 기말재고금액 */
, #{startDate} AS START_DATE FROM TB_ST_HQ_STOCK_MONTHLY TPHSM , TB_HQ_PRODUCT THP , TB_HQ_PRODUCT_BARCD THPB WHERE TPHSM.HQ_OFFICE_CD = #{hqOfficeCd} AND THP.STOCK_PROD_YN = 'Y' AND THP.USE_YN = 'Y'
AND TPHSM.IOSTOCK_YM = #{startDate}
AND THP.PROD_CD = #{prodCd}
AND THP.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
AND THP.PROD_NM LIKE '%'||#{prodNm}||'%'
AND THPB.BARCD_CD = #{barcdCd}
