/* USE : StorePeriodMapper.getStorePeriodList */
SELECT FN_GET_PROD_CLASS_CD_NM_STR(TMP.STORE_CD, TMP.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM , TSSSD.PROD_CD , TMP.PROD_NM , TSSSD.STORE_CD , (SELECT STORE_NM FROM TB_MS_STORE Z WHERE Z.STORE_CD = TSSSD.STORE_CD) AS STORE_NM , TMP.PO_UNIT_QTY , TMP.PO_UNIT_FG , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE NMCODE_GRP_CD = '093' AND HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_CD = TMP.PO_UNIT_FG) AS PO_UNIT_FG_NM , TMPB.BARCD_CD
, SF_ZERO_TO_NULL(SUM(STORE_IN_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_03 /* 매장입고 수량*/ , SF_ZERO_TO_NULL(SUM(STORE_OUT_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_12 /* 매장반품 수량*/ , SF_ZERO_TO_NULL(SUM(PURCHS_IN_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_06 /* 사입입고 수량*/ , SF_ZERO_TO_NULL(SUM(PURCHS_OUT_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_18 /* 사입반품 수량*/ , SF_ZERO_TO_NULL(SUM(STORE_SALE_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_11 /* 매장판매 수량*/ , SF_ZERO_TO_NULL(SUM(MOVE_IN_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_04 /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(MOVE_OUT_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_14 /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(DISUSE_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_17 /* 재고폐기 수량*/ , SF_ZERO_TO_NULL(SUM(ADJ_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_21 /* 재고조정 수량*/ , SF_ZERO_TO_NULL(SUM(SET_IN_QTY / TMP.PO_UNIT_QTY)) AS IO_OCCR_QTY_22 /* 세트생성 수량*/
, SF_ZERO_TO_NULL(SUM(STORE_IN_QTY )) AS IO_OCCR_QTY_03 /* 매장입고 수량*/ , SF_ZERO_TO_NULL(SUM(STORE_OUT_QTY )) AS IO_OCCR_QTY_12 /* 매장반품 수량*/ , SF_ZERO_TO_NULL(SUM(PURCHS_IN_QTY )) AS IO_OCCR_QTY_06 /* 사입입고 수량*/ , SF_ZERO_TO_NULL(SUM(PURCHS_OUT_QTY )) AS IO_OCCR_QTY_18 /* 사입반품 수량*/ , SF_ZERO_TO_NULL(SUM(STORE_SALE_QTY )) AS IO_OCCR_QTY_11 /* 매장판매 수량*/ , SF_ZERO_TO_NULL(SUM(MOVE_IN_QTY )) AS IO_OCCR_QTY_04 /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(MOVE_OUT_QTY )) AS IO_OCCR_QTY_14 /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(DISUSE_QTY )) AS IO_OCCR_QTY_17 /* 재고폐기 수량*/ , SF_ZERO_TO_NULL(SUM(ADJ_QTY )) AS IO_OCCR_QTY_21 /* 재고조정 수량*/ , SF_ZERO_TO_NULL(SUM(SET_IN_QTY )) AS IO_OCCR_QTY_22 /* 세트생성 수량*/
, SF_ZERO_TO_NULL(SUM(STORE_IN_TOT )) AS IO_OCCR_TOT_03 /* 매장입고 금액*/ , SF_ZERO_TO_NULL(SUM(STORE_OUT_TOT )) AS IO_OCCR_TOT_12 /* 매장반품 금액*/ , SF_ZERO_TO_NULL(SUM(PURCHS_IN_TOT )) AS IO_OCCR_TOT_06 /* 사입입고 금액*/ , SF_ZERO_TO_NULL(SUM(PURCHS_OUT_TOT )) AS IO_OCCR_TOT_18 /* 사입반품 금액*/ , SF_ZERO_TO_NULL(SUM(STORE_SALE_TOT )) AS IO_OCCR_TOT_11 /* 매장판매 금액*/ , SF_ZERO_TO_NULL(SUM(MOVE_IN_TOT )) AS IO_OCCR_TOT_04 /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(MOVE_OUT_TOT )) AS IO_OCCR_TOT_14 /* 매장이출 금액*/ -- , SF_ZERO_TO_NULL(SUM()) AS IO_OCCR_TOT_17 /* 재고폐기 금액*/ -- , SF_ZERO_TO_NULL(SUM()) AS IO_OCCR_TOT_21 /* 재고조정 금액*/ -- , SF_ZERO_TO_NULL(SUM()) AS IO_OCCR_TOT_22 /* 세트생성 금액*/
FROM TB_ST_STORE_STOCK_DAILY TSSSD , TB_MS_PRODUCT TMP , TB_MS_PRODUCT_BARCD TMPB WHERE EXISTS (SELECT * FROM TB_MS_STORE Z WHERE Z.STORE_CD = tmp.STORE_CD AND Z.HQ_OFFICE_CD = #{hqOfficeCd}) AND TSSSD.STORE_CD = TMP.STORE_CD AND TSSSD.PROD_CD = TMP.PROD_CD AND TSSSD.STORE_CD = TMPB.STORE_CD(+) AND TSSSD.PROD_CD = TMPB.PROD_CD(+)
AND TSSSD.IOSTOCK_DATE BETWEEN #{startDate} AND #{endDate}
AND TMP.PROD_CD = #{prodCd}
AND TMP.STORE_CD IN
#{item}
AND TMP.PROD_NM LIKE '%'||#{prodNm}||'%'
AND TMPB.BARCD_CD = #{barcdCd}
AND TMP.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
AND tmp.PROD_CD IN ( SELECT PROD_CD FROM TB_MS_VENDOR_PROD WHERE STORE_CD = TSSSD.STORE_CD AND VENDR_CD IN
#{item}
)
GROUP BY TMP.STORE_CD , TMP.PROD_CLASS_CD , TSSSD.PROD_CD , TMP.PROD_NM , TSSSD.STORE_CD , TMP.PO_UNIT_QTY , TMP.PO_UNIT_FG , TMPB.BARCD_CD ORDER BY TSSSD.PROD_CD, TSSSD.STORE_CD

/* USE : StorePeriodMapper.getPeriodiostockProdDtlList */
SELECT TSHSD.IOSTOCK_DATE AS IO_OCCR_DT , SF_ZERO_TO_NULL(SUM(VENDR_IN_QTY )) AS IO_OCCR_QTY_01 /* 본사입고 수량*/ , SF_ZERO_TO_NULL(SUM(VENDR_IN_TOT )) AS IO_OCCR_TOT_01 /* 본사입고 금액*/ , SF_ZERO_TO_NULL(SUM(VENDR_OUT_QTY )) AS IO_OCCR_QTY_16 /* 업체반출 수량*/ , SF_ZERO_TO_NULL(SUM(VENDR_OUT_TOT )) AS IO_OCCR_TOT_16 /* 업체반출 금액*/ , SF_ZERO_TO_NULL(SUM(HQ_OUT_QTY )) AS IO_OCCR_QTY_13 /* 본사출고 수량*/ , SF_ZERO_TO_NULL(SUM(HQ_OUT_TOT )) AS IO_OCCR_TOT_13 /* 본사출고 금액*/ , SF_ZERO_TO_NULL(SUM(HQ_IN_QTY )) AS IO_OCCR_QTY_02 /* 본사반입 수량*/ , SF_ZERO_TO_NULL(SUM(HQ_IN_TOT )) AS IO_OCCR_TOT_02 /* 본사반입 금액*/ , SF_ZERO_TO_NULL(SUM(STORE_MOVE_IN_QTY )) AS IO_OCCR_QTY_04 /* 매장이입 수량*/ , SF_ZERO_TO_NULL(SUM(STORE_MOVE_IN_TOT )) AS IO_OCCR_TOT_04 /* 매장이입 금액*/ , SF_ZERO_TO_NULL(SUM(STORE_MOVE_OUT_QTY )) AS IO_OCCR_QTY_14 /* 매장이출 수량*/ , SF_ZERO_TO_NULL(SUM(STORE_MOVE_OUT_TOT )) AS IO_OCCR_TOT_14 /* 매장이출 금액*/ , SF_ZERO_TO_NULL(SUM(DISUSE_QTY )) AS IO_OCCR_QTY_17 /* 재고폐기 수량*/ -- , SF_ZERO_TO_NULL(SUM()) AS IO_OCCR_TOT_17 /* 재고폐기 금액*/ , SF_ZERO_TO_NULL(SUM(ADJ_QTY )) AS IO_OCCR_QTY_21 /* 재고조정 수량*/ -- , SF_ZERO_TO_NULL(SUM()) AS IO_OCCR_TOT_21 /* 재고조정 금액*/ , SF_ZERO_TO_NULL(SUM(SET_IN_AMT )) AS IO_OCCR_QTY_22 /* 세트생성 수량*/ -- , SF_ZERO_TO_NULL(SUM()) AS IO_OCCR_TOT_22 /* 세트생성 금액*/ , SF_ZERO_TO_NULL(SUM(SALE_VENDR_ORDER_QTY )) AS IO_OCCR_QTY_19 /* 거래처출고 수량*/ , SF_ZERO_TO_NULL(SUM(SALE_VENDR_ORDER_TOT )) AS IO_OCCR_TOT_19 /* 거래처출고 금액*/ , SF_ZERO_TO_NULL(SUM(SALE_VENDR_RTN_QTY )) AS IO_OCCR_QTY_33 /* 거래처반품 수량*/ , SF_ZERO_TO_NULL(SUM(SALE_VENDR_RTN_TOT )) AS IO_OCCR_TOT_33 /* 거래처반품 금액*/
FROM TB_ST_HQ_STOCK_DAILY TSHSD WHERE TSHSD.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSHSD.IOSTOCK_DATE BETWEEN #{startDate} AND #{endDate} AND TSHSD.PROD_CD = #{prodCd} GROUP BY TSHSD.IOSTOCK_DATE ORDER BY TSHSD.IOSTOCK_DATE DESC

