mapper.select:
/* USE : BranchMomsMapper.getBranchMomsList */
SELECT BRANCH_CD , BRANCH_NM , BRANCH_OWNER_NM , TEL_NO , PHONE_NO , EMAIL , POST_NO , ADDR , ADDR_DTL , USE_YN , ORGPLCE_INFO , TO_CHAR(TO_DATE(REG_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD') AS REG_DT , REG_ID
FROM TB_HQ_BRANCH_MOMS WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND BRANCH_CD LIKE '%'|| #{branchCd} || '%'
AND BRANCH_NM LIKE '%'|| #{branchNm} || '%'
AND BRANCH_OWNER_NM LIKE '%'|| #{branchOwnerNm} || '%'
AND PHONE_NO LIKE '%'|| #{phoneNo} || '%'
AND USE_YN = #{useYn}
ORDER BY BRANCH_CD

/* USE : saveBranchMoms.setBranchCd */
SELECT LPAD(NVL(MAX(BRANCH_CD), '0') + 1, 3, '0')
FROM TB_HQ_BRANCH_MOMS
WHERE HQ_OFFICE_CD = #{hqOfficeCd}

mapper.insert:
/* USE : BranchMomsMapper.saveBranchMoms */
MERGE INTO TB_HQ_BRANCH_MOMS thbm
USING DUAL
ON (
    HQ_OFFICE_CD = #{hqOfficeCd}
    AND BRANCH_CD = #{branchCd}
)
WHEN MATCHED THEN
    UPDATE
    SET
        BRANCH_NM       = #{branchNm}
    ,   BRANCH_OWNER_NM = #{branchOwnerNm}
    ,   TEL_NO          = #{telNo}
    ,   PHONE_NO        = #{phoneNo}
    ,   EMAIL           = #{email}
    ,   POST_NO         = #{postNo}
    ,   ADDR            = #{addr}
    ,   ADDR_DTL        = #{addrDtl}
    ,   USE_YN          = #{useYn}
    ,   ORGPLCE_INFO    = #{orgplceInfo}
WHEN NOT MATCHED THEN
    INSERT
    (
        HQ_OFFICE_CD
    ,   BRANCH_CD
    ,   BRANCH_NM
    ,   BRANCH_OWNER_NM
    ,   TEL_NO
    ,   PHONE_NO
    ,   EMAIL
    ,   POST_NO
    ,   ADDR
    ,   ADDR_DTL
    ,   USE_YN
    ,   ORGPLCE_INFO
    ,   REG_DT
    ,   REG_ID
    ,   MOD_DT
    ,   MOD_ID
    )
    VALUES
    (
        #{hqOfficeCd}
    ,   #{branchCd}
    ,   #{branchNm}
    ,   #{branchOwnerNm}
    ,   #{telNo}
    ,   #{phoneNo}
    ,   #{email}
    ,   #{postNo}
    ,   #{addr}
    ,   #{addrDtl}
    ,   #{useYn}
    ,   #{orgplceInfo}
    ,   #{regDt}
    ,   #{regId}
    ,   #{modDt}
    ,   #{modId}
    )
