mapper.select:
/* AccountManageMapper.getLongTermUnusedList */
SELECT A.HQ_OFFICE_CD , A.HQ_OFFICE_NM , A.HQ_SYS_STAT_FG , A.STORE_CD , A.STORE_NM , A.STORE_SYS_STAT_FG , A.USER_ID , A.USER_NM , A.WEB_USE_YN , A.SERVICE_FG , A.USE_YN , A.WEB_USER_USE_YN , A.LAST_LOGIN_DT , A.ORGN_FG , A.ORGN_FG2 , A.ORGN_CD , A.ORGN_NM , A.EMP_NO , TO_CHAR(TO_DATE(NVL(B.SALE_YM, ''), 'yyyymm'), 'yyyy-mm') SALE_YM
FROM (
SELECT * FROM TB_WB_USER_INFO_DTL_V01 A WHERE 1=1
SELECT * FROM TB_WB_USER_INFO_DTL_V01 A WHERE 1=1 AND NVL(A.LAST_LOGIN_DT, '0') < TO_CHAR(SYSDATE - TO_NUMBER(#{loginPeriod}),'YYYYMMDDHH24MISS') AND A.ORGN_FG2 IN
#{item}
SELECT * FROM TB_WB_USER_INFO_DTL_V01 A WHERE 1=1 AND NVL(A.LAST_LOGIN_DT, '0') < TO_CHAR(SYSDATE - TO_NUMBER(#{loginPeriod}),'YYYYMMDDHH24MISS') AND A.HQ_OFFICE_CD NOT IN ( SELECT B.HQ_OFFICE_CD FROM TB_SL_MONTHLY_TOTAL B WHERE B.SALE_YM > TO_CHAR(SYSDATE - TO_NUMBER(#{loginPeriod}),'YYYYMM') GROUP BY B.HQ_OFFICE_CD ) AND A.ORGN_FG2 IN
#{item}
SELECT * FROM TB_WB_USER_INFO_DTL_V01 A WHERE 1=1 AND NVL(A.LAST_LOGIN_DT, '0') < TO_CHAR(SYSDATE - TO_NUMBER(#{loginPeriod}),'YYYYMMDDHH24MISS') AND A.STORE_CD NOT IN ( SELECT B.STORE_CD FROM TB_SL_MONTHLY_TOTAL B WHERE B.SALE_YM > TO_CHAR(SYSDATE - TO_NUMBER(#{loginPeriod}),'YYYYMM') GROUP BY B.STORE_CD ) AND A.ORGN_FG2 IN
#{item}
SELECT * FROM TB_WB_USER_INFO_DTL_V01 A WHERE 1=1 AND NVL(A.LAST_LOGIN_DT, '0') < TO_CHAR(SYSDATE - TO_NUMBER(#{loginPeriod}),'YYYYMMDDHH24MISS') AND A.HQ_OFFICE_CD NOT IN ( SELECT B.HQ_OFFICE_CD FROM TB_SL_MONTHLY_TOTAL B WHERE B.SALE_YM > TO_CHAR(SYSDATE - TO_NUMBER(#{loginPeriod}),'YYYYMM') GROUP BY B.HQ_OFFICE_CD ) AND A.ORGN_FG2 IN ('H') UNION ALL SELECT * FROM TB_WB_USER_INFO_DTL_V01 A WHERE 1=1 AND NVL(A.LAST_LOGIN_DT, '0') < TO_CHAR(SYSDATE - TO_NUMBER(#{loginPeriod}),'YYYYMMDDHH24MISS') AND A.STORE_CD NOT IN ( SELECT B.STORE_CD FROM TB_SL_MONTHLY_TOTAL B WHERE B.SALE_YM > TO_CHAR(SYSDATE - TO_NUMBER(#{loginPeriod}),'YYYYMM') GROUP BY B.STORE_CD ) AND A.ORGN_FG2 IN ('S', 'I')
) A , ( SELECT STORE_CD , NVL(MAX(SALE_YM),'') AS SALE_YM FROM TB_SL_MONTHLY_TOTAL GROUP BY STORE_CD ) B WHERE B.STORE_CD (+)= A.STORE_CD
AND A.HQ_OFFICE_CD LIKE '%'||#{hqOfficeCd}||'%'
AND A.HQ_OFFICE_NM LIKE '%'||#{hqOfficeNm}||'%'
AND A.STORE_CD LIKE '%'||#{storeCd}||'%'
AND A.STORE_NM LIKE '%'||#{storeNm}||'%'
AND A.USER_ID LIKE '%'||#{userId}||'%'
AND A.USER_NM LIKE '%'||#{userNm}||'%'
AND A.WEB_USE_YN = #{webUseYn}
AND A.ORGN_FG2 IN
#{item}
ORDER BY A.HQ_OFFICE_CD , A.STORE_CD , A.USER_ID

