mapper.update:
/* USE : CloseStoreMapper.saveCloseStore */ MERGE INTO TB_MS_STORE A USING ( SELECT A.STORE_CD, A.SYS_REMARK FROM TB_MS_STORE A, TB_HQ_OFFICE B, TB_CM_AGENCY C WHERE B.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND C.AGENCY_CD = A.AGENCY_CD
AND A.SYS_STAT_FG NOT IN ('2','9') AND A.REG_DT <= TO_CHAR(ADD_MONTHS(SYSDATE, -3),'YYYYMMDD') AND A.STORE_CD NOT IN ( SELECT DISTINCT STORE_CD FROM TB_SL_MONTHLY_TOTAL TSMT WHERE SALE_YM >= TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') )
--AND A.STORE_CD NOT LIKE 'D%' -- 제외(요청 시 진행(김중선))
AND A.AGENCY_CD LIKE '%' || #{agencyCd} || '%'
AND C.AGENCY_NM LIKE '%' || #{agencyNm} || '%'
AND A.HQ_OFFICE_CD LIKE '%' || #{hqOfficeCd} || '%'
AND B.HQ_OFFICE_NM LIKE '%' || #{hqOfficeNm} || '%'
AND A.STORE_CD LIKE '%' || #{storeCd} || '%'
AND A.STORE_NM LIKE '%' || #{storeNm} || '%'
AND A.SYS_STAT_FG = #{sysStatFg}
) B ON ( A.STORE_CD = B.STORE_CD ) WHEN MATCHED THEN UPDATE SET A.SYS_STAT_FG = '2' , A.SYS_CLOSURE_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD') , A.SYS_REMARK = A.SYS_REMARK || '1개월 동안 매출 자료가 발생되지 않아 ' || TO_CHAR(SYSDATE, 'YYYY-MM-DD') || ' 폐점처리됨, USER_ID='|| #{modId} || ', 폐점비고 : ' || #{remark} , A.MOD_DT = #{modDt} , A.MOD_ID = #{modId}
