mapper.select:
/* EnvConfgBatchChangeMapper.getEnvConfgBatchChangeHqList */ SELECT the.HQ_OFFICE_CD, the.HQ_OFFICE_NM, the.ENVST_CD, the.ENVST_VAL, the.EXIST_FG, NVL(tms.TOT_STORE_CNT , 0) AS TOT_STORE_CNT, NVL(tmse.STORE_CNT, 0) AS STORE_CNT FROM ( SELECT tho.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tce.ENVST_CD, the.ENVST_VAL, DECODE(the.ENVST_CD, NULL, 'N', 'Y') AS EXIST_FG FROM TB_HQ_OFFICE tho, TB_HQ_ENVST the, TB_CM_ENVST tce WHERE 1=1 AND tho.HQ_OFFICE_CD != '00000'
AND tho.AGENCY_CD = #{agencyCd}
AND the.HQ_OFFICE_CD (+)= tho.HQ_OFFICE_CD AND the.ENVST_CD (+)= tce.ENVST_CD ORDER BY tho.HQ_OFFICE_CD ) the, ( SELECT tms.HQ_OFFICE_CD, COUNT(tms.STORE_CD) AS TOT_STORE_CNT FROM TB_MS_STORE tms, TB_HQ_OFFICE tho WHERE 1=1 AND tms.HQ_OFFICE_CD != '00000'
AND tho.AGENCY_CD = #{agencyCd}
AND tho.HQ_OFFICE_CD = tms.HQ_OFFICE_CD GROUP BY tms.HQ_OFFICE_CD ) tms, ( SELECT tms.HQ_OFFICE_CD, tmse.ENVST_CD, COUNT(tmse.STORE_CD) AS STORE_CNT FROM TB_MS_STORE_ENVST tmse, TB_MS_STORE tms, TB_HQ_OFFICE tho WHERE 1=1 AND tms.HQ_OFFICE_CD != '00000'
AND tho.AGENCY_CD = #{agencyCd}
AND tms.STORE_CD (+)= tmse.STORE_CD AND tho.HQ_OFFICE_CD = tms.HQ_OFFICE_CD GROUP BY tms.HQ_OFFICE_CD, tmse.ENVST_CD ) tmse WHERE 1=1
AND the.HQ_OFFICE_CD LIKE '%'|| #{hqOfficeCd} ||'%'
AND the.HQ_OFFICE_NM LIKE '%'|| #{hqOfficeNm} ||'%'
AND the.EXIST_FG = #{useYn}
AND the.ENVST_CD = #{envstCd} AND tms.HQ_OFFICE_CD (+)= the.HQ_OFFICE_CD AND tmse.HQ_OFFICE_CD (+)= the.HQ_OFFICE_CD AND tmse.ENVST_CD (+)= the.ENVST_CD ORDER BY the.HQ_OFFICE_CD

/* EnvConfgBatchChangeMapper.getStoreCdList */
SELECT tms.STORE_CD
FROM TB_MS_STORE tms
WHERE 1=1
AND tms.HQ_OFFICE_CD = #{hqOfficeCd}
ORDER BY tms.STORE_CD

/* EnvConfgBatchChangeMapper.getEnvConfgBatchChangeStoreList */ SELECT HQ_OFFICE_CD, HQ_OFFICE_NM, STORE_CD, STORE_NM, ENVST_CD, ENVST_VAL, EXIST_FG FROM ( SELECT tms.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tms.STORE_CD, tms.STORE_NM, tce.ENVST_CD, tmse.ENVST_VAL, DECODE(tmse.ENVST_CD, NULL, 'N', 'Y') AS EXIST_FG FROM TB_MS_STORE tms, TB_HQ_OFFICE tho, TB_MS_STORE_ENVST tmse, TB_CM_ENVST tce WHERE 1=1 AND tms.STORE_CD != '0000000'
AND tms.HQ_OFFICE_CD = '00000'
AND tms.AGENCY_CD = #{agencyCd}
AND tho.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tmse.STORE_CD (+)= tms.STORE_CD AND tmse.ENVST_CD (+)= tce.ENVST_CD ORDER BY tms.HQ_OFFICE_CD, tms.STORE_CD ) WHERE 1=1
AND HQ_OFFICE_CD LIKE '%'|| #{hqOfficeCd} ||'%'
AND HQ_OFFICE_NM LIKE '%'|| #{hqOfficeNm} ||'%'
AND STORE_CD LIKE '%'|| #{storeCd} ||'%'
AND STORE_NM LIKE '%'|| #{storeNm} ||'%'
AND EXIST_FG = #{useYn}
AND ENVST_CD = #{envstCd} ORDER BY HQ_OFFICE_CD, STORE_CD

/* EnvConfgBatchChangeMapper.getEnvConfgBatchChangeStorePosList */ SELECT HQ_OFFICE_CD, HQ_OFFICE_NM, STORE_CD, STORE_NM, POS_NO, POS_NM, ENVST_CD, ENVST_VAL, EXIST_FG FROM ( SELECT tms.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tms.STORE_CD, tms.STORE_NM, tmp.POS_NO, tmp.POS_NM, tce.ENVST_CD, tmpe.ENVST_VAL, DECODE(tmpe.ENVST_CD, NULL, 'N', 'Y') AS EXIST_FG FROM TB_MS_POS tmp, TB_MS_STORE tms, TB_HQ_OFFICE tho, TB_MS_POS_ENVST tmpe, TB_CM_ENVST tce WHERE 1=1 AND tms.STORE_CD != '0000000'
AND tms.HQ_OFFICE_CD = '00000'
AND tms.AGENCY_CD = #{agencyCd}
AND tms.STORE_CD (+)= tmp.STORE_CD AND tho.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tmpe.STORE_CD (+)= tms.STORE_CD AND tmpe.ENVST_CD (+)= tce.ENVST_CD ORDER BY tms.HQ_OFFICE_CD, tms.STORE_CD, tmp.POS_NO ) WHERE 1=1
AND HQ_OFFICE_CD LIKE '%'|| #{hqOfficeCd} ||'%'
AND HQ_OFFICE_NM LIKE '%'|| #{hqOfficeNm} ||'%'
AND STORE_CD LIKE '%'|| #{storeCd} ||'%'
AND STORE_NM LIKE '%'|| #{storeNm} ||'%'
AND EXIST_FG = #{useYn}
AND ENVST_CD = #{envstCd} ORDER BY HQ_OFFICE_CD, STORE_CD, POS_NO
