mapper.select:
/* MigDataMappingMapper.getMigDataMappingList */ SELECT tmdm.OKPOS_STORE_CD, tmdm.OKPOS_STORE_NM, tmdm.SOLBI_STORE_CD, tmdm.SOLBI_STORE_NM, tmdm.MIG_YN, tmdm.MIG_POS_MST_CNT, tmdm.MIG_PROD_CLS_CNT, tmdm.MIG_PROD_CNT, tmdm.MIG_PROD_PRICE_CNT, tmdm.MIG_SALE_CNT FROM TB_MIG_DATA_MAPPING tmdm WHERE 1=1 AND tmdm.MIG_REG_DATE BETWEEN #{startDate} AND #{endDate}
AND tmdm.OKPOS_STORE_CD LIKE '%'|| #{okposStoreCd} ||'%'
AND tmdm.OKPOS_STORE_NM LIKE '%'|| #{okposStoreNm} ||'%'
AND tmdm.REG_ID = #{userId}
ORDER BY tmdm.SOLBI_STORE_CD

/* MigDataMappingMapper.getOkposUserInfoList */
SELECT
oui.USER_ID -- OKPOS ID
,oui.USER_PWD -- OKPOS 비밀번호 ( 평문 )
,oui.CORP_CD -- OKPOS VAN대리점 코드
FROM OKPOS_USER_INFO oui --view
WHERE 1=1

    AND oui.USER_ID = #{userId}


    AND oui.USER_PWD = #{userPwd}

/* MigDataMappingMapper.getMigDataMappingInfoList */ SELECT otms.HQ_OFFICE_CD ,otms.HQ_OFFICE_NM ,otms.STORE_CD ,otms.STORE_NM ,otms.BIZ_NO ,otms.POS_CNT AS POS_NO ,otms.POS_CNT AS INSTALL_POS_CNT ,otms.OWNER_NM -- ,otms.BIZ_TYPE_NM -- ,otms.BIZ_KIND_NM -- ,otms.BIZ_SHOP_NM ,SUBSTR(otms.POST_NO, 0, 5) AS POST_NO -- (신)우편번호5자리, (구)우편번호6자리 ,otms.ADDR ,otms.ADDR_DTL ,otms.ADDRESS ,otms.TEL_NO -- ,otms.HP_NO ,otms.SHOP_STAT_FG AS SYS_STAT_FG -- ,otms.SOLBI_SHOP_STAT_FG -- ,otms.PGM_TYPE_FG_NM ,otms.CLS_FG -- ,otms.BRAND_MGR_YN -- ,otms.CLS_MGR_LEVEL_FG -- ,otms.CORP_CD -- OKPOS VAN대리점 코드 -- ,otms.CORP_NM -- ,otms.VAN_CD -- ,otms.MIG_FG ,otms.VAN_TERM_NO -- 다중 사업자 구분 -- ,otms.PROGRAM_FG -- ,otms.POS_PROGRAM_FG_NM -- ,otms.SAIP_FG -- ,otms.PROD_CR_FG -- ,otms.CORNER_FG -- ,otms.CORNER_USE_FG -- ,otms.PRICE_MGR_FG -- ,otms.PRICE_CONTROL_FG -- ,otms.LAST_LOGIN_DATE ,otms.SYS_OPEN_DATE -- ,otms.SYS_CLOSE_DATE -- ,otms.SHOP_USER_ID -- ,otms.LAST_VER_SEQ -- ,otms.USER_ID -- ,otms.USER_PWD -- ,otms.MULTI_BIZ_TYPE -- ,otms.MULTI_BIZ_TYPE_FG -- 1 :단독 2: 다중사업자 ,otms.AREA_CD ,otms.LAST_SALE_DATE -- 최종 영업일 FROM OKPOS_TB_MS_STORE otms --view WHERE 1=1
AND otms.USER_ID = #{userId}
AND otms.CORP_CD = #{corpCd}
AND otms.STORE_CD LIKE '%'|| #{okposStoreCd} ||'%'
AND otms.STORE_NM LIKE '%'|| #{okposStoreNm} ||'%'
ORDER BY otms.STORE_CD
