mapper.select:
/* USE : PosHwInfoMapper.getPosHwInfo */
SELECT tms.HQ_OFFICE_CD, tms.HQ_OFFICE_NM, tms.STORE_CD, tms.STORE_NM, tca.AGENCY_CD, tca.AGENCY_NM, tpphoi.POS_NO, TO_CHAR(TO_DATE(tpphoi.SYS_DTIME, 'yyyymmddhh24miss'), 'yyyy-mm-dd hh24:mi:ss') AS SYS_DTIME, tpphoi.WIN_VER_NO, tpphoi.WIN_BLD_NO, tpphoi.WIN_EDT_NO, tpphoi.IE_VER_NO, tpphoi.C_TOT_DISK, tpphoi.C_REMAIN_DISK, tpphoi.D_TOT_DISK, tpphoi.D_REMAIN_DISK, tpphoi.MAC_ADDRESS, tpphoi.CPU_INFO, tpphoi.MEM_INFO
FROM ( SELECT A.HQ_OFFICE_CD , A.HQ_OFFICE_NM , B.STORE_CD , B.STORE_NM , B.CLS_FG , B.SYS_STAT_FG , B.OWNER_NM , B.TEL_NO , B.AGENCY_CD , B.SYS_OPEN_DATE , B.SYS_CLOSURE_DATE FROM TB_HQ_OFFICE A , TB_MS_STORE B WHERE B.HQ_OFFICE_CD (+) = A.HQ_OFFICE_CD ) tms LEFT OUTER JOIN TB_PS_POS_HW_OS_INFO tpphoi ON tms.STORE_CD = tpphoi.STORE_CD LEFT OUTER JOIN TB_CM_AGENCY tca ON tms.AGENCY_CD = tca.AGENCY_CD LEFT OUTER JOIN TB_CM_EMPLOYEE tce ON tca.AGENCY_CD = tce.AGENCY_CD AND tce.EMP_NO = '0000' WHERE 1=1 -- 본사권한 접근시 AND tms.HQ_OFFICE_CD = #{hqOfficeCd}
-- 총판권한 접근시 AND (tca.AGENCY_CD = #{agencyCd} OR tca.P_AGENCY_CD = #{agencyCd})
AND UPPER(tms.HQ_OFFICE_CD) LIKE '%' || UPPER(#{hqOfficeCd}) || '%'
AND tms.HQ_OFFICE_NM LIKE '%' ||#{hqOfficeNm} || '%'
AND UPPER(tms.STORE_CD) LIKE '%' || UPPER(#{storeCd}) || '%'
AND tms.STORE_NM LIKE '%' ||#{storeNm} || '%'
AND tms.CLS_FG = #{clsFg}
AND tms.SYS_STAT_FG = #{sysStatFg}
ORDER BY tms.HQ_OFFICE_CD ASC, tms.STORE_CD ASC, tca.AGENCY_CD ASC
