mapper.select:
/* USE : PwdManageMapper.getOldPassword */
SELECT USER_PWD FROM TB_WB_USER_INFO WHERE USER_ID = #{userId}
SELECT EMP_PWD FROM TB_MS_EMPLOYEE WHERE STORE_CD = #{storeCd} AND EMP_NO = #{empNo}

mapper.update:
/* USE : PwdManageMapper.updatePassword */


        UPDATE
            TB_WB_USER_INFO
        SET
            USER_PWD = #{newPassword},
            LAST_PWD_CHG_DT = #{lastPwdChgDt}
        WHERE
            USER_ID = #{userId}


        UPDATE
            TB_MS_EMPLOYEE
        SET
            EMP_PWD = #{newPassword},
            MOD_DT = #{modDt},
            MOD_ID = #{modId}
        WHERE
            EMP_NO = #{empNo}
