mapper.select:
/* StoreStatusMapper.getStatusAgencyList */
SELECT AGENCY_CD, AGENCY_NM, AGENCY_FG, COUNT(STORE_CD) AS STORE_CNT, SUM(POS_CNT) AS TOT_POS_CNT
, CLS_FG
, SYS_STAT_FG
FROM ( SELECT tca.AGENCY_CD, tca.AGENCY_NM,
(CASE WHEN tca.P_AGENCY_CD = '00000' THEN '총판' WHEN tca.P_AGENCY_CD != '00000' THEN '대리점' ELSE '' END) AS AGENCY_FG,
(CASE WHEN tca.AGENCY_CD = #{agencyCd} THEN '자사' WHEN tca.P_AGENCY_CD = #{agencyCd} THEN '대리점' ELSE '' END) AS AGENCY_FG,
tms.STORE_CD, (SELECT COUNT(POS_NO) FROM TB_MS_POS WHERE STORE_CD = tms.STORE_CD) AS POS_CNT, tms.CLS_FG, tms.SYS_STAT_FG FROM TB_CM_AGENCY tca, TB_MS_STORE tms WHERE 1=1 AND tms.AGENCY_CD (+)= tca.AGENCY_CD AND tca.AGENCY_CD != '00000'
AND (tca.AGENCY_CD = #{agencyCd} OR tca.P_AGENCY_CD = #{agencyCd})
AND tca.AGENCY_CD LIKE '%'|| #{srchAgencyCd} ||'%'
AND tca.AGENCY_NM LIKE '%'|| #{srchAgencyNm} ||'%'
AND tca.AGENCY_CD = #{agencyCd}
AND tca.P_AGENCY_CD = #{agencyCd}
AND tca.USE_YN = #{useYn}
AND tms.CLS_FG = #{clsFg}
AND tms.SYS_STAT_FG = #{sysStatFg}
) GROUP BY AGENCY_CD, AGENCY_NM, AGENCY_FG
, CLS_FG
, SYS_STAT_FG
ORDER BY AGENCY_CD

/* StoreStatusMapper.getStatusAgencyDetailList */
SELECT tms.HQ_OFFICE_CD, (SELECT HQ_OFFICE_NM FROM TB_HQ_OFFICE WHERE HQ_OFFICE_CD = tms.HQ_OFFICE_CD) AS HQ_OFFICE_NM, tms.STORE_CD, tms.STORE_NM, tms.SYS_STAT_FG, tms.CLS_FG, (SELECT COUNT(POS_NO) FROM TB_MS_POS WHERE STORE_CD = tms.STORE_CD) AS POS_CNT , CASE WHEN G.MIN_SALE_DATE IS NOT NULL THEN TO_CHAR(TO_DATE(G.MIN_SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') ELSE '' END AS MIN_SALE_DATE , CASE WHEN G.MAX_SALE_DATE IS NOT NULL THEN TO_CHAR(TO_DATE(G.MAX_SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') ELSE '' END AS MAX_SALE_DATE
FROM TB_MS_STORE tms , ( SELECT A.STORE_CD STORE_CD , A.BILL_CNT BILL_CNT , A.SALE_CNT SALE_CNT , A.RTN_SALE_CNT RTN_SALE_CNT , A.REAL_SALE_CNT REAL_SALE_CNT , A.TOT_GUEST_CNT TOT_GUEST_CNT , A.TOT_DLVR_CNT TOT_DLVR_CNT , A.TOT_RESVE_CNT TOT_RESVE_CNT , A.TOT_REFUND_CNT TOT_REFUND_CNT , A.MIN_SALE_YM MIN_SALE_YM , A.MAX_SALE_YM MAX_SALE_YM , MIN(B.SALE_DATE) MIN_SALE_DATE , MAX(C.SALE_DATE) MAX_SALE_DATE FROM ( SELECT A.STORE_CD , SUM(NVL(A.BILL_CNT , 0)) BILL_CNT , SUM(NVL(A.SALE_CNT , 0)) SALE_CNT , SUM(NVL(A.RTN_SALE_CNT , 0)) RTN_SALE_CNT , SUM(NVL(A.REAL_SALE_CNT , 0)) REAL_SALE_CNT , SUM(NVL(A.TOT_GUEST_CNT , 0)) TOT_GUEST_CNT , SUM(NVL(A.TOT_DLVR_CNT , 0)) TOT_DLVR_CNT , SUM(NVL(A.TOT_RESVE_CNT , 0)) TOT_RESVE_CNT , SUM(NVL(A.TOT_REFUND_CNT, 0)) TOT_REFUND_CNT , MIN(A.SALE_YM) MIN_SALE_YM , MAX(A.SALE_YM) MAX_SALE_YM FROM TB_SL_MONTHLY_TOTAL A GROUP BY A.STORE_CD ) A , TB_SL_DAILY_TOTAL B , TB_SL_DAILY_TOTAL C WHERE B.STORE_CD (+) = A.STORE_CD AND B.SALE_DATE (+) LIKE A.MIN_SALE_YM||'%' AND C.STORE_CD (+) = A.STORE_CD AND C.SALE_DATE (+) LIKE A.MAX_SALE_YM||'%' GROUP BY A.STORE_CD , A.BILL_CNT , A.SALE_CNT , A.RTN_SALE_CNT , A.REAL_SALE_CNT , A.TOT_GUEST_CNT , A.TOT_DLVR_CNT , A.TOT_RESVE_CNT , A.TOT_REFUND_CNT , A.MIN_SALE_YM , A.MAX_SALE_YM ) G WHERE 1=1 AND tms.AGENCY_CD = #{agencyCd}
AND tms.CLS_FG = #{clsFg}
AND tms.SYS_STAT_FG = #{sysStatFg}
AND G.STORE_CD (+) = tms.STORE_CD ORDER BY tms.HQ_OFFICE_CD, tms.STORE_CD

/* StoreStatusMapper.getStatusVanList */ SELECT VAN_CD, VAN_NM, COUNT(STORE_CD) AS STORE_CNT
, CLS_FG
, SYS_STAT_FG
FROM ( SELECT tcvc.VAN_CD, tcvc.VAN_NM, tms.STORE_CD, tms.SYS_STAT_FG, tms.CLS_FG FROM TB_CM_VAN_CMPNY tcvc, TB_MS_STORE tms, TB_CM_AGENCY tca WHERE 1=1 AND tcvc.VAN_CD = tms.VAN_CD AND tcvc.VAN_FG = '01'
AND tcvc.VAN_CD LIKE '%'|| #{vanCd} ||'%'
AND tcvc.VAN_NM LIKE '%'|| #{vanNm} ||'%'
AND tms.CLS_FG = #{clsFg}
AND tms.SYS_STAT_FG = #{sysStatFg}
AND tca.AGENCY_CD = #{agencyCd}
AND tca.P_AGENCY_CD = #{agencyCd}
AND tca.AGENCY_CD = tms.AGENCY_CD
AND (tca.AGENCY_CD = #{agencyCd} OR tca.P_AGENCY_CD = #{agencyCd})
) GROUP BY VAN_CD, VAN_NM
, CLS_FG
, SYS_STAT_FG
ORDER BY VAN_CD

/* StoreStatusMapper.getStatusVanDetailList */
SELECT tms.HQ_OFFICE_CD, (SELECT HQ_OFFICE_NM FROM TB_HQ_OFFICE WHERE HQ_OFFICE_CD = tms.HQ_OFFICE_CD) AS HQ_OFFICE_NM, tms.STORE_CD, tms.STORE_NM, tms.SYS_STAT_FG, tms.CLS_FG, (SELECT COUNT(POS_NO) FROM TB_MS_POS WHERE STORE_CD = tms.STORE_CD) AS POS_CNT, tms.AGENCY_CD, tca.AGENCY_NM , CASE WHEN G.MIN_SALE_DATE IS NOT NULL THEN TO_CHAR(TO_DATE(G.MIN_SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') ELSE '' END AS MIN_SALE_DATE , CASE WHEN G.MAX_SALE_DATE IS NOT NULL THEN TO_CHAR(TO_DATE(G.MAX_SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') ELSE '' END AS MAX_SALE_DATE
FROM TB_MS_STORE tms, TB_CM_AGENCY tca , ( SELECT A.STORE_CD STORE_CD , A.BILL_CNT BILL_CNT , A.SALE_CNT SALE_CNT , A.RTN_SALE_CNT RTN_SALE_CNT , A.REAL_SALE_CNT REAL_SALE_CNT , A.TOT_GUEST_CNT TOT_GUEST_CNT , A.TOT_DLVR_CNT TOT_DLVR_CNT , A.TOT_RESVE_CNT TOT_RESVE_CNT , A.TOT_REFUND_CNT TOT_REFUND_CNT , A.MIN_SALE_YM MIN_SALE_YM , A.MAX_SALE_YM MAX_SALE_YM , MIN(B.SALE_DATE) MIN_SALE_DATE , MAX(C.SALE_DATE) MAX_SALE_DATE FROM ( SELECT A.STORE_CD , SUM(NVL(A.BILL_CNT , 0)) BILL_CNT , SUM(NVL(A.SALE_CNT , 0)) SALE_CNT , SUM(NVL(A.RTN_SALE_CNT , 0)) RTN_SALE_CNT , SUM(NVL(A.REAL_SALE_CNT , 0)) REAL_SALE_CNT , SUM(NVL(A.TOT_GUEST_CNT , 0)) TOT_GUEST_CNT , SUM(NVL(A.TOT_DLVR_CNT , 0)) TOT_DLVR_CNT , SUM(NVL(A.TOT_RESVE_CNT , 0)) TOT_RESVE_CNT , SUM(NVL(A.TOT_REFUND_CNT, 0)) TOT_REFUND_CNT , MIN(A.SALE_YM) MIN_SALE_YM , MAX(A.SALE_YM) MAX_SALE_YM FROM TB_SL_MONTHLY_TOTAL A GROUP BY A.STORE_CD ) A , TB_SL_DAILY_TOTAL B , TB_SL_DAILY_TOTAL C WHERE B.STORE_CD (+) = A.STORE_CD AND B.SALE_DATE (+) LIKE A.MIN_SALE_YM||'%' AND C.STORE_CD (+) = A.STORE_CD AND C.SALE_DATE (+) LIKE A.MAX_SALE_YM||'%' GROUP BY A.STORE_CD , A.BILL_CNT , A.SALE_CNT , A.RTN_SALE_CNT , A.REAL_SALE_CNT , A.TOT_GUEST_CNT , A.TOT_DLVR_CNT , A.TOT_RESVE_CNT , A.TOT_REFUND_CNT , A.MIN_SALE_YM , A.MAX_SALE_YM ) G WHERE 1=1 AND tms.VAN_CD = #{vanCd}
AND tms.CLS_FG = #{clsFg}
AND tms.SYS_STAT_FG = #{sysStatFg}
AND (tca.AGENCY_CD = #{agencyCd} OR tca.P_AGENCY_CD = #{agencyCd})
AND tca.AGENCY_CD = tms.AGENCY_CD AND G.STORE_CD (+) = tms.STORE_CD ORDER BY tms.HQ_OFFICE_CD, tms.STORE_CD

/* StoreStatusMapper.getStatusPosInstallExcelList */ SELECT tms.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tci.STORE_CD, tms.STORE_NM, (SUBSTR(tms.BIZ_NO, 1,3) || '-' || SUBSTR(tms.BIZ_NO, 4,2) || '-' || SUBSTR(tms.BIZ_NO, 6)) AS BIZ_NO, (SELECT COUNT(POS_NO) FROM TB_MS_POS WHERE STORE_CD = tci.STORE_CD) AS POS_CNT, tci.INST_INS_DT, tci.INST_FG, (SELECT AGENCY_NM FROM TB_CM_AGENCY WHERE AGENCY_CD = tms.AGENCY_CD) AS AGENCY_NM, (SELECT AGENCY_NM FROM TB_CM_AGENCY WHERE AGENCY_CD = tci.AGENCY_CD) AS INST_AGENCY_NM, tci.INST_INS_ID AS INST_INS_ID_BCK , K.VENDOR_TERMNL_NO AS VAN_NO , E.VAN_NM , (SELECT NVL(MAX(EMP_NM), tci.INST_INS_ID) FROM TB_CM_EMPLOYEE WHERE USER_ID = tci.INST_INS_ID) AS INST_INS_ID , TO_CHAR(TO_DATE(SUBSTR(tci.INST_INS_DT,1,8), 'yyyymmdd'), 'yyyy-mm-dd') AS DAY , tci.POS_NO AS POS_NO , (SELECT AGENCY_NM FROM TB_CM_AGENCY WHERE AGENCY_CD = (SELECT DECODE(P_AGENCY_CD, '00000', AGENCY_CD, P_AGENCY_CD) FROM TB_CM_AGENCY WHERE AGENCY_CD = tms.AGENCY_CD)) AS DIST , (SELECT AGENCY_NM FROM TB_CM_AGENCY WHERE AGENCY_CD = tms.AGENCY_CD) AS AGENCY FROM TB_CM_INSTLL tci, TB_MS_STORE tms, TB_HQ_OFFICE tho , TB_CM_VAN_CMPNY E , ( SELECT A.STORE_CD , REGEXP_REPLACE(LISTAGG(A.VENDOR_TERMNL_NO, ',') WITHIN GROUP(ORDER BY A.VENDOR_TERMNL_NO), '([^,]+)(,\1)*(,|$)', '\1\3') VENDOR_TERMNL_NO FROM TB_MS_POS_TERMNL A , TB_MS_POS B WHERE A.VENDOR_FG = '01' AND B.STORE_CD = A.STORE_CD AND B.POS_NO = A.POS_NO AND B.USE_YN = 'Y' GROUP BY A.STORE_CD ) K, TB_CM_AGENCY tca WHERE 1=1
AND tci.INST_INS_DT BETWEEN #{startDate}||'000000' AND #{endDate}||'235959'
AND tho.HQ_OFFICE_CD LIKE '%'|| #{hqOfficeCd} ||'%'
AND tho.HQ_OFFICE_NM LIKE '%'|| #{hqOfficeNm} ||'%'
AND tms.STORE_CD LIKE '%'|| #{storeCd} ||'%'
AND tms.STORE_NM LIKE '%'|| #{storeNm} ||'%'
AND tci.INST_FG = #{instFg}
AND tca.AGENCY_CD = #{agencyCd}
AND tca.P_AGENCY_CD = #{agencyCd}
AND tci.STORE_CD = tms.STORE_CD AND tms.HQ_OFFICE_CD = tho.HQ_OFFICE_CD AND E.VAN_FG (+) = '01' AND E.VAN_CD (+) = tms.VAN_CD AND K.STORE_CD (+) = tms.STORE_CD AND tca.AGENCY_CD = tms.AGENCY_CD
AND (tca.AGENCY_CD = #{agencyCd} OR tca.P_AGENCY_CD = #{agencyCd})
ORDER BY tms.HQ_OFFICE_CD, tci.STORE_CD, tci.MOD_DT

/* USE : StoreStatusMapper.getStatusApprList */
SELECT tms.AGENCY_CD, tca.AGENCY_NM, tms.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tms.STORE_CD, tms.STORE_NM, SUBSTR(tms.BIZ_NO,0,3)||'-'||SUBSTR(tms.BIZ_NO,4,2)||'-'||SUBSTR(tms.BIZ_NO,6,5) AS BIZ_NO, tms.OWNER_NM, TO_CHAR(TO_DATE(tms.SYS_OPEN_DATE, 'YYYYMMDD') , 'YYYY/MM/DD') AS SYS_OPEN_DATE, TO_CHAR(TO_DATE(tms.SYS_CLOSURE_DATE, 'YYYYMMDD') , 'YYYY/MM/DD') AS SYS_CLOSURE_DATE, tlcra.CARD_APPR_CNT, tlcra.CARD_REQ_AMT, tlcsa.CASH_APPR_CNT, tlcsa.CASH_REQ_AMT
FROM TB_MS_STORE tms, TB_CM_AGENCY tca, TB_HQ_OFFICE tho, ( SELECT HQ_OFFICE_CD, STORE_CD, COUNT(SALE_FG) AS CARD_APPR_CNT, SUM(SALE_AMT) AS CARD_REQ_AMT FROM TB_SL_SALE_PAY_CARD WHERE 1=1 AND SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY HQ_OFFICE_CD, STORE_CD ) tlcra, ( SELECT HQ_OFFICE_CD, STORE_CD, COUNT(SALE_FG) AS CASH_APPR_CNT, SUM(SALE_AMT) AS CASH_REQ_AMT FROM TB_SL_SALE_PAY_CASH WHERE 1=1 AND SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY HQ_OFFICE_CD, STORE_CD ) tlcsa WHERE 1=1 AND tca.AGENCY_CD = tms.AGENCY_CD AND tho.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tlcra.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tlcra.STORE_CD = tms.STORE_CD AND tlcsa.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tlcsa.STORE_CD = tms.STORE_CD
AND tca.AGENCY_NM LIKE '%'|| #{agencyNm} ||'%'
AND (tca.AGENCY_CD = #{agencyCd} OR tca.P_AGENCY_CD = #{agencyCd})
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
AND tms.BIZ_NO LIKE '%'||#{bizNo}||'%'
AND tca.AGENCY_CD = #{agencyCd}
AND tca.P_AGENCY_CD = #{agencyCd}
ORDER BY tms.AGENCY_CD, tms.HQ_OFFICE_CD, tms.STORE_CD

/* USE : StoreStatusMapper.getCardAppr */ SELECT tsspc.HQ_OFFICE_CD, tms.AGENCY_CD, tsspc.STORE_CD, tsspc.HQ_BRAND_CD, tsspc.SALE_DATE, tsspc.POS_NO, tsspc.BILL_NO, DECODE(tsspc.SALE_FG, '1', '1', '-1', '2', null) AS APPR_FG, tsspc.APPR_PROC_FG, tsspc.ACQUIRE_NM, SUBSTR(tsspc.CARD_NO,0,4)||'-'||SUBSTR(tsspc.CARD_NO,5,4)||'-'||SUBSTR(tsspc.CARD_NO,9,4) ||'-'||SUBSTR(tsspc.CARD_NO,13,4)AS CARD_NO, tsspc.TIP_AMT, tsspc.VAT_AMT, tsspc.INST_CNT, tsspc.APPR_DT, tsspc.APPR_NO, tsspc.SALE_AMT AS APPR_AMT, 'CARD' AS payApprType FROM TB_SL_SALE_PAY_CARD tsspc, TB_MS_STORE tms WHERE 1=1
AND tms.AGENCY_CD = #{agencyCd}
AND tsspc.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspc.STORE_CD = #{storeCd} AND tsspc.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tms.HQ_OFFICE_CD = tsspc.HQ_OFFICE_CD AND tms.STORE_CD = tsspc.STORE_CD ORDER BY tsspc.HQ_OFFICE_CD, tsspc.STORE_CD, tsspc.SALE_DATE, tsspc.POS_NO, tsspc.BILL_NO

/* USE : StoreStatusMapper.getCashAppr */ SELECT tsspc.HQ_OFFICE_CD, tms.AGENCY_CD, tsspc.HQ_BRAND_CD, tsspc.STORE_CD, tsspc.SALE_DATE, tsspc.POS_NO, tsspc.BILL_NO, DECODE(tsspc.SALE_FG, '1', '1', '-1', '2', null) AS APPR_FG, tsspc.APPR_PROC_FG, tsspc.TIP_AMT, tsspc.VAT_AMT, tsspc.APPR_DT, tsspc.APPR_NO, tsspc.SALE_AMT AS APPR_AMT, 'CASH' AS payApprType FROM TB_SL_SALE_PAY_CASH tsspc, TB_MS_STORE tms WHERE 1=1
AND tms.AGENCY_CD = #{agencyCd}
AND tsspc.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspc.STORE_CD = #{storeCd} AND tsspc.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tms.HQ_OFFICE_CD = tsspc.HQ_OFFICE_CD AND tms.STORE_CD = tsspc.STORE_CD ORDER BY tsspc.HQ_OFFICE_CD, tsspc.STORE_CD, tsspc.SALE_DATE, tsspc.POS_NO, tsspc.BILL_NO

/* USE : DayMapper.getStoreSalePayInfo */ SELECT tssh.STORE_CD, (SELECT STORE_NM FROM TB_MS_STORE WHERE HQ_OFFICE_CD = tssh.HQ_OFFICE_CD AND STORE_CD = tssh.STORE_CD) AS STORE_NM, TO_CHAR(TO_DATE(tssh.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE, tssh.POS_NO, tssh.BILL_NO, NVL(tssh.TOT_SALE_AMT, 0) AS TOT_SALE_AMT, NVL(tssh.TOT_DC_AMT, 0) AS TOT_DC_AMT, NVL(tssh.REAL_SALE_AMT, 0) AS REAL_SALE_AMT, NVL(tssh.NET_SALE_AMT, 0) AS NET_SALE_AMT, NVL(tssh.NO_TAX_SALE_AMT, 0) AS NO_TAX_SALE_AMT, NVL(tssh.TAX_SALE_AMT, 0) AS TAX_SALE_AMT, NVL(tssh.VAT_AMT, 0) AS VAT_AMT, NVL(tssh.TOT_TIP_AMT, 0) AS TOT_TIP_AMT,
NVL(tsshp.PAY${item}, 0) AS PAY${item}
FROM ( SELECT tssh.HQ_OFFICE_CD, tssh.HQ_BRAND_CD, tssh.STORE_CD, tssh.SALE_DATE, tssh.POS_NO, tssh.BILL_NO, tssh.TOT_SALE_AMT, tssh.TOT_DC_AMT, tssh.REAL_SALE_AMT, tssh.NET_SALE_AMT, tssh.NO_TAX_SALE_AMT, tssh.TAX_SALE_AMT, tssh.VAT_AMT, tssh.TOT_TIP_AMT FROM TB_SL_SALE_HDR tssh WHERE tssh.HQ_OFFICE_CD = #{hqOfficeCd} AND tssh.HQ_BRAND_CD = #{hqBrandCd} AND tssh.STORE_CD = #{storeCd} AND tssh.SALE_DATE = #{saleDate} AND tssh.POS_NO = #{posNo} AND tssh.BILL_NO = #{billNo}
) tssh , ( SELECT HQ_OFFICE_CD , HQ_BRAND_CD , STORE_CD , SALE_DATE , POS_NO , BILL_NO,
PAY${item}
FROM ( SELECT tsshp.HQ_OFFICE_CD, tsshp.HQ_BRAND_CD, tsshp.STORE_CD, tsshp.SALE_DATE, tsshp.POS_NO, tsshp.BILL_NO, tsshp.PAY_CD, tsshp.PAY_AMT FROM TB_SL_SALE_HDR_PAY tsshp WHERE tsshp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsshp.HQ_BRAND_CD = #{hqBrandCd} AND tsshp.STORE_CD = #{storeCd} AND tsshp.SALE_DATE = #{saleDate} AND tsshp.POS_NO = #{posNo} AND tsshp.BILL_NO = #{billNo} ) PIVOT ( SUM(PAY_AMT) FOR PAY_CD IN (${pivotPayCol}) ) ) tsshp WHERE 1=1 AND tsshp.HQ_OFFICE_CD = tssh.HQ_OFFICE_CD AND tsshp.HQ_BRAND_CD = tssh.HQ_BRAND_CD AND tsshp.STORE_CD = tssh.STORE_CD AND tsshp.SALE_DATE = tssh.SALE_DATE AND tsshp.POS_NO = tssh.POS_NO --AND tsshp.BILL_NO = tssh.BILL_NO

   /* StoreStatusMapper.getCashPayInfo */
  SELECT
           tsspc.CORNR_CD,
           DECODE(tsspc.SALE_FG, '1', '1', '-1', '2', null) AS APPR_FG,
           tsspc.APPR_PROC_FG,
           -- 유효기간
           tsspc.APPR_NO,
           TO_CHAR(TO_DATE(tsspc.APPR_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS APPR_DT,
           tsspc.SALE_AMT AS APPR_AMT,
           tsspc.VAN_CD,
           tsspc.ORG_BILL_NO
   FROM   TB_SL_SALE_PAY_CASH tsspc
   WHERE  1=1
     AND  tsspc.HQ_OFFICE_CD = #{hqOfficeCd}
     AND  tsspc.STORE_CD = #{storeCd}
     AND  tsspc.SALE_DATE = #{saleDate}
     AND  tsspc.POS_NO = #{posNo}
	  AND  tsspc.BILL_NO = #{billNo}

   /* StoreStatusMapper.getSaleProductInfo */
   SELECT tssd.PROD_CD,
            (SELECT PROD_NM FROM TB_MS_PRODUCT
             WHERE 1=1
               /*AND HQ_BRAND_CD = tssd.HQ_BRAND_CD */
               AND STORE_CD = tssd.STORE_CD
               AND PROD_CD = tssd.PROD_CD) AS PROD_NM,
            tssd.SALE_QTY,
            tssd.SALE_UPRC,
            tssd.SALE_AMT,
            tssd.DC_AMT,
            tssd.REAL_SALE_AMT,
            (tssd.REAL_SALE_AMT - tssd.VAT_AMT) AS TAX_SALE_AMT,
            tssd.VAT_AMT
     FROM  TB_SL_SALE_DTL tssd
     WHERE 1=1
      AND   tssd.HQ_OFFICE_CD = #{hqOfficeCd}
      AND   tssd.HQ_BRAND_CD = #{hqBrandCd}
      AND   tssd.STORE_CD = #{storeCd}
      AND   tssd.SALE_DATE = #{saleDate}
      AND   tssd.POS_NO = #{posNo}
	   AND   tssd.BILL_NO = #{billNo}
